import{_ as e,o as a,c as o,a as r}from"./app.1e6146c3.js";const S=JSON.parse('{"title":"Amazon Web Services","description":"","frontmatter":{},"headers":[],"relativePath":"CloudNative/CloudComptering/Amazon Web Services.md","lastUpdated":1657272051000}'),n={name:"CloudNative/CloudComptering/Amazon Web Services.md"},t=r('<h1 id="amazon-web-services" tabindex="-1">Amazon Web Services <a class="header-anchor" href="#amazon-web-services" aria-hidden="true">#</a></h1><p><strong>\u8BB0\u5F55\u5B66\u4E60aws\u6240\u7528\u5230\u7684\u4EA7\u54C1\u53CA\u5176\u670D\u52A1\u7B80\u4ECB.</strong></p><ul><li><a href="https://docs.aws.amazon.com/ec2/index.html" target="_blank" rel="noopener noreferrer">AWS EC2</a>\uFF1AAmazon Elastic Compute Cloud (Amazon EC2) \u662F\u4E00\u79CD Web \u670D\u52A1\uFF0C\u53EF\u4EE5\u5728\u4E91\u4E2D\u63D0\u4F9B\u5B89\u5168\u5E76\u4E14\u53EF\u8C03\u6574\u5927\u5C0F\u7684\u8BA1\u7B97\u5BB9\u91CF\u3002</li><li><a href="https://docs.aws.amazon.com/lambda/index.html" target="_blank" rel="noopener noreferrer">AWS Lambda</a>\uFF1A\u501F\u52A9 Lambda\uFF0C\u60A8\u51E0\u4E4E\u53EF\u4EE5\u4E3A\u4EFB\u4F55\u7C7B\u578B\u7684\u5E94\u7528\u7A0B\u5E8F\u6216\u540E\u7AEF\u670D\u52A1\u8FD0\u884C\u4EE3\u7801\uFF0C\u800C\u4E14\u5B8C\u5168\u65E0\u9700\u7BA1\u7406\u3002\u53EA\u9700\u4E0A\u4F20\u60A8\u7684\u4EE3\u7801\uFF0CLambda \u4F1A\u5904\u7406\u8FD0\u884C\u548C\u6269\u5C55\u9AD8\u53EF\u7528\u6027\u4EE3\u7801\u6240\u9700\u7684\u4E00\u5207\u5DE5\u4F5C\u3002\u60A8\u53EF\u4EE5\u5C06\u60A8\u7684\u4EE3\u7801\u8BBE\u7F6E\u4E3A\u81EA\u52A8\u4ECE\u5176\u4ED6 AWS \u670D\u52A1\u89E6\u53D1\uFF0C\u6216\u8005\u76F4\u63A5\u4ECE\u4EFB\u4F55 Web \u6216\u79FB\u52A8\u5E94\u7528\u7A0B\u5E8F\u8C03\u7528\u3002(PaaS)</li><li><a href="https://docs.aws.amazon.com/elastic-beanstalk/index.html" target="_blank" rel="noopener noreferrer">AWS Elastic Beanstalk</a>\uFF1AAWS Elastic Beanstalk \u662F\u4E00\u9879\u6613\u4E8E\u4F7F\u7528\u7684\u670D\u52A1\uFF0C\u7528\u4E8E\u5728\u719F\u6089\u7684\u670D\u52A1\u5668\uFF08\u4F8B\u5982 Apache \u3001Nginx\u3001Passenger \u548C IIS \uFF09\u4E0A\u90E8\u7F72\u548C\u6269\u5C55\u4F7F\u7528 Java\u3001.NET\u3001PHP\u3001Node.js\u3001Python\u3001Ruby\u3001GO \u548C Docker \u5F00\u53D1\u7684 Web \u5E94\u7528\u7A0B\u5E8F\u548C\u670D\u52A1\u3002(PaaS)</li><li><a href="https://docs.aws.amazon.com/s3/index.html" target="_blank" rel="noopener noreferrer">Amazon S3</a>\uFF1AAmazon Simple Storage Service (Amazon S3) \u662F\u4E00\u79CD\u5BF9\u8C61\u5B58\u50A8\u670D\u52A1\uFF0C\u63D0\u4F9B\u884C\u4E1A\u9886\u5148\u7684\u53EF\u6269\u5C55\u6027\u3001\u6570\u636E\u53EF\u7528\u6027\u3001\u5B89\u5168\u6027\u548C\u6027\u80FD\u3002Amazon S3 \u53EF\u8FBE\u5230 99.999999999%\uFF0811 \u4E2A 9\uFF09\u7684\u6301\u4E45\u6027\uFF0C\u5E76\u4E3A\u5168\u7403\u5404\u5730\u7684\u516C\u53F8\u5B58\u50A8\u6570\u767E\u4E07\u4E2A\u5E94\u7528\u7A0B\u5E8F\u7684\u6570\u636E\u3002</li><li><a href="https://docs.aws.amazon.com/cloudfront/index.html" target="_blank" rel="noopener noreferrer">AWS CloudFront</a>\uFF1AAmazon CloudFront \u662F\u4E00\u9879\u5FEB\u901F\u5185\u5BB9\u5206\u53D1\u7F51\u7EDC (CDN) \u670D\u52A1\uFF0C\u53EF\u4EE5\u5B89\u5168\u5730\u4EE5\u4F4E\u5EF6\u8FDF\u548C\u9AD8\u4F20\u8F93\u901F\u5EA6\u5411\u5168\u7403\u5BA2\u6237\u5206\u53D1\u6570\u636E\u3001\u89C6\u9891\u3001\u5E94\u7528\u7A0B\u5E8F\u548C API\uFF0C\u5168\u90E8\u90FD\u5728\u5F00\u53D1\u4EBA\u5458\u53CB\u597D\u7684\u73AF\u5883\u4E2D\u5B8C\u6210\u3002</li><li><a href="https://docs.aws.amazon.com/cloudwatch/index.html" target="_blank" rel="noopener noreferrer">AWS CloudWatch</a>\uFF1AAmazon CloudWatch \u662F\u4E00\u79CD\u9762\u5411\u5F00\u53D1\u8FD0\u8425\u5DE5\u7A0B\u5E08\u3001\u5F00\u53D1\u4EBA\u5458\u3001\u7AD9\u70B9\u53EF\u9760\u6027\u5DE5\u7A0B\u5E08 (SRE) \u548C IT \u7ECF\u7406\u7684\u76D1\u63A7\u548C\u53EF\u89C2\u6D4B\u6027\u670D\u52A1\u3002CloudWatch \u4E3A\u60A8\u63D0\u4F9B\u76F8\u5173\u6570\u636E\u548C\u5207\u5B9E\u89C1\u89E3\uFF0C\u4EE5\u76D1\u63A7\u5E94\u7528\u7A0B\u5E8F\u3001\u54CD\u5E94\u7CFB\u7EDF\u8303\u56F4\u7684\u6027\u80FD\u53D8\u5316\u3001\u4F18\u5316\u8D44\u6E90\u5229\u7528\u7387\uFF0C\u5E76\u5728\u7EDF\u4E00\u89C6\u56FE\u4E2D\u67E5\u770B\u8FD0\u8425\u72B6\u51B5\u3002CloudWatch \u4EE5\u65E5\u5FD7\u3001\u6307\u6807\u548C\u4E8B\u4EF6\u7684\u5F62\u5F0F\u6536\u96C6\u76D1\u63A7\u548C\u8FD0\u8425\u6570\u636E\uFF0C\u8BA9\u60A8\u80FD\u591F\u5728\u7EDF\u4E00\u67E5\u770B\u5728 AWS \u548C\u672C\u5730\u670D\u52A1\u5668\u4E0A\u8FD0\u884C\u7684\u8D44\u6E90\u3001\u5E94\u7528\u7A0B\u5E8F\u548C\u670D\u52A1\u3002\u60A8\u53EF\u4EE5\u4F7F\u7528 CloudWatch \u68C0\u6D4B\u73AF\u5883\u4E2D\u7684\u5F02\u5E38\u884C\u4E3A\u3001\u8BBE\u7F6E\u8B66\u62A5\u3001\u5E76\u6392\u663E\u793A\u65E5\u5FD7\u548C\u6307\u6807\u3001\u6267\u884C\u81EA\u52A8\u5316\u64CD\u4F5C\u3001\u6392\u67E5\u95EE\u9898\uFF0C\u4EE5\u53CA\u53D1\u73B0\u53EF\u786E\u4FDD\u5E94\u7528\u7A0B\u5E8F\u6B63\u5E38\u8FD0\u884C\u7684\u89C1\u89E3\u3002</li><li><a href="https://docs.aws.amazon.com/cloudformation/index.html" target="_blank" rel="noopener noreferrer">AWS CloudFormation</a>\uFF1A\u501F\u52A9 AWS CloudFormation\uFF0C\u60A8\u53EF\u4EE5\u6709\u9884\u89C1\u6027\u5730\u3001\u91CD\u590D\u5730\u521B\u5EFA\u548C\u9884\u7F6E AWS \u57FA\u7840\u8BBE\u65BD\u90E8\u7F72\u3002\u5B83\u53EF\u4EE5\u5E2E\u52A9\u60A8\u5229\u7528 AWS \u4EA7\u54C1 (\u5982 Amazon EC2\u3001Amazon Elastic Block Store\u3001Amazon SNS\u3001Elastic Load Balancing \u548C Auto Scaling) \u5728\u4E91\u4E2D\u6784\u5EFA\u9AD8\u5EA6\u53EF\u9760\u3001\u9AD8\u5EA6\u53EF\u6269\u5C55\u4E14\u7ECF\u6D4E\u9AD8\u6548\u7684\u5E94\u7528\u7A0B\u5E8F\uFF0C\u4E3A\u60A8\u514D\u9664\u521B\u5EFA\u548C\u914D\u7F6E\u5E95\u5C42 AWS \u57FA\u7840\u8BBE\u65BD\u4E4B\u5FE7\u3002\u501F\u52A9 AWS CloudFormation\uFF0C\u60A8\u53EF\u4EE5\u4F7F\u7528\u6A21\u677F\u6587\u4EF6\uFF0C\u5C06\u8D44\u6E90\u96C6\u4F5C\u4E3A\u4E00\u4E2A\u5355\u5143 (\u5806\u6808) \u8FDB\u884C\u521B\u5EFA\u548C\u5220\u9664\u3002</li><li><a href="#">AMI &amp; AutoScaling &amp; LB</a>\uFF1AEC2\u955C\u50CF\uFF0C \u81EA\u52A8\u6269\u5C55\u548C\u8D1F\u8F7D\u5747\u8861.</li><li><a href="https://docs.aws.amazon.com/iam/index.html" target="_blank" rel="noopener noreferrer">Cloud Security - IAM</a>\uFF1AAWS Identity and Access Management (IAM) \u4F7F\u60A8\u80FD\u591F\u5B89\u5168\u5730\u7BA1\u7406\u5BF9 AWS \u670D\u52A1\u548C\u8D44\u6E90\u7684\u8BBF\u95EE\u3002\u60A8\u53EF\u4EE5\u4F7F\u7528 IAM \u521B\u5EFA\u548C\u7BA1\u7406 AWS \u7528\u6237\u548C\u7EC4\uFF0C\u5E76\u4F7F\u7528\u5404\u79CD\u6743\u9650\u6765\u5141\u8BB8\u6216\u62D2\u7EDD\u4ED6\u4EEC\u5BF9 AWS \u8D44\u6E90\u7684\u8BBF\u95EE\u3002</li><li><a href="https://docs.aws.amazon.com/redshift/index.html" target="_blank" rel="noopener noreferrer">AWS Redshift</a>\uFF1A\u4E91\u6570\u636E\u4ED3\u5E93\u3002</li><li><a href="https://docs.aws.amazon.com/zh_cn/ecs/" target="_blank" rel="noopener noreferrer">Amazon ECS</a>\uFF1A\u5FEB\u901F\u5BB9\u5668\u670D\u52A1\uFF0C\u4E0E\u5176\u7ED3\u5408\u4F7F\u7528\u7684\u65F6<a href="https://docs.aws.amazon.com/zh_cn/AmazonECS/latest/developerguide/ecr-repositories.html" target="_blank" rel="noopener noreferrer">Amazon ECR</a>\u955C\u50CF\u670D\u52A1\u3002</li><li><a href="https://docs.aws.amazon.com/zh_cn/documentdb/latest/developerguide/what-is.html" target="_blank" rel="noopener noreferrer">Amazon DocumentDB</a>\uFF1A\u6587\u6863\u578B\u6570\u636E\u5E93\u670D\u52A1\uFF0C\u517C\u5BB9MongoDB\u3002</li><li><a href="./.html">Amazon MemoryDB for Redis</a>\uFF1Aredis\u3002</li><li><a href="https://docs.aws.amazon.com/amazondynamodb/latest/developerguide/Introduction.html" target="_blank" rel="noopener noreferrer">Amazon DynamoDB</a>\uFF1ANoSQL\u6570\u636E\u5E93\u670D\u52A1\u3002</li></ul>',3),l=[t];function s(i,m,c,d,h,A){return a(),o("div",null,l)}var _=e(n,[["render",s]]);export{S as __pageData,_ as default};
