import{_ as s,o as a,c as n,a as e}from"./app.1e6146c3.js";const y=JSON.parse('{"title":"Prometheus \u5B89\u88C5\u4E0E\u90E8\u7F72\u3001Grafana","description":"","frontmatter":{},"headers":[{"level":2,"title":"Prometheus \u7B80\u4ECB","slug":"prometheus-\u7B80\u4ECB"},{"level":3,"title":"\u4E3B\u8981\u529F\u80FD","slug":"\u4E3B\u8981\u529F\u80FD"},{"level":3,"title":"\u6838\u5FC3\u7EC4\u4EF6","slug":"\u6838\u5FC3\u7EC4\u4EF6"},{"level":3,"title":"\u57FA\u7840\u67B6\u6784","slug":"\u57FA\u7840\u67B6\u6784"},{"level":2,"title":"\u4E0B\u8F7D\u5E76\u8FD0\u884C Prom","slug":"\u4E0B\u8F7D\u5E76\u8FD0\u884C-prom"},{"level":2,"title":"\u5B89\u88C5\u914D\u7F6E node_exporter","slug":"\u5B89\u88C5\u914D\u7F6E-node-exporter"},{"level":2,"title":"\u4F7F\u7528 Grafana \u5C55\u793A\u5DE5\u5177","slug":"\u4F7F\u7528-grafana-\u5C55\u793A\u5DE5\u5177"},{"level":2,"title":"\u53C2\u8003\u94FE\u63A5","slug":"\u53C2\u8003\u94FE\u63A5"}],"relativePath":"CloudNative/Prometheus/Prometheus \u5B89\u88C5\u4E0E\u90E8\u7F72\u3001Grafana.md","lastUpdated":1657272051000}'),l={name:"CloudNative/Prometheus/Prometheus \u5B89\u88C5\u4E0E\u90E8\u7F72\u3001Grafana.md"},p=e(`<h1 id="prometheus-\u5B89\u88C5\u4E0E\u90E8\u7F72\u3001grafana" tabindex="-1">Prometheus \u5B89\u88C5\u4E0E\u90E8\u7F72\u3001Grafana <a class="header-anchor" href="#prometheus-\u5B89\u88C5\u4E0E\u90E8\u7F72\u3001grafana" aria-hidden="true">#</a></h1><h2 id="prometheus-\u7B80\u4ECB" tabindex="-1">Prometheus \u7B80\u4ECB <a class="header-anchor" href="#prometheus-\u7B80\u4ECB" aria-hidden="true">#</a></h2><p>Prometheus\u53D7\u542F\u53D1\u4E8EGoogle\u7684Brogmon\u76D1\u63A7\u7CFB\u7EDF\uFF08\u76F8\u4F3C\u7684Kubernetes\u662F\u4ECEGoogle\u7684Brog\u7CFB\u7EDF\u6F14\u53D8\u800C\u6765\uFF09\uFF0C\u4ECE2012\u5E74\u5F00\u59CB\u7531\u524DGoogle\u5DE5\u7A0B\u5E08\u5728Soundcloud\u4EE5\u5F00\u6E90\u8F6F\u4EF6\u7684\u5F62\u5F0F\u8FDB\u884C\u7814\u53D1\uFF0C\u5E76\u4E14\u4E8E2015\u5E74\u65E9\u671F\u5BF9\u5916\u53D1\u5E03\u65E9\u671F\u7248\u672C\u30022016\u5E745\u6708\u7EE7Kubernetes\u4E4B\u540E\u6210\u4E3A\u7B2C\u4E8C\u4E2A\u6B63\u5F0F\u52A0\u5165CNCF\u57FA\u91D1\u4F1A\u7684\u9879\u76EE\uFF0C\u540C\u5E746\u6708\u6B63\u5F0F\u53D1\u5E031.0\u7248\u672C\u30022017\u5E74\u5E95\u53D1\u5E03\u4E86\u57FA\u4E8E\u5168\u65B0\u5B58\u50A8\u5C42\u76842.0\u7248\u672C\uFF0C\u80FD\u66F4\u597D\u5730\u4E0E\u5BB9\u5668\u5E73\u53F0\u3001\u4E91\u5E73\u53F0\u914D\u5408\u3002</p><h3 id="\u4E3B\u8981\u529F\u80FD" tabindex="-1">\u4E3B\u8981\u529F\u80FD <a class="header-anchor" href="#\u4E3B\u8981\u529F\u80FD" aria-hidden="true">#</a></h3><ul><li>\u591A\u7EF4 <a href="https://prometheus.io/docs/concepts/data_model/" target="_blank" rel="noopener noreferrer">\u6570\u636E\u6A21\u578B</a>\uFF08\u65F6\u5E8F\u7531 metric \u540D\u5B57\u548C k/v \u7684 labels \u6784\u6210\uFF09\u3002</li><li>\u7075\u6D3B\u7684\u67E5\u8BE2\u8BED\u53E5\uFF08<a href="https://prometheus.io/docs/querying/basics/" target="_blank" rel="noopener noreferrer">PromQL</a>\uFF09\u3002</li><li>\u65E0\u4F9D\u8D56\u5B58\u50A8\uFF0C\u652F\u6301 local \u548C remote \u4E0D\u540C\u6A21\u578B\u3002</li><li>\u91C7\u7528 http \u534F\u8BAE\uFF0C\u4F7F\u7528 pull \u6A21\u5F0F\uFF0C\u62C9\u53D6\u6570\u636E\uFF0C\u7B80\u5355\u6613\u61C2\u3002</li><li>\u76D1\u63A7\u76EE\u6807\uFF0C\u53EF\u4EE5\u91C7\u7528\u670D\u52A1\u53D1\u73B0\u6216\u9759\u6001\u914D\u7F6E\u7684\u65B9\u5F0F\u3002</li><li>\u652F\u6301\u591A\u79CD\u7EDF\u8BA1\u6570\u636E\u6A21\u578B\uFF0C\u56FE\u5F62\u5316\u53CB\u597D\u3002</li></ul><h3 id="\u6838\u5FC3\u7EC4\u4EF6" tabindex="-1">\u6838\u5FC3\u7EC4\u4EF6 <a class="header-anchor" href="#\u6838\u5FC3\u7EC4\u4EF6" aria-hidden="true">#</a></h3><ul><li><a href="https://github.com/prometheus/prometheus" target="_blank" rel="noopener noreferrer">Prometheus Server</a>\uFF0C \u4E3B\u8981\u7528\u4E8E\u6293\u53D6\u6570\u636E\u548C\u5B58\u50A8\u65F6\u5E8F\u6570\u636E\uFF0C\u53E6\u5916\u8FD8\u63D0\u4F9B\u67E5\u8BE2\u548C Alert Rule \u914D\u7F6E\u7BA1\u7406\u3002</li><li><a href="https://prometheus.io/docs/instrumenting/clientlibs/" target="_blank" rel="noopener noreferrer">client libraries</a>\uFF0C\u7528\u4E8E\u5BF9\u63A5 Prometheus Server, \u53EF\u4EE5\u67E5\u8BE2\u548C\u4E0A\u62A5\u6570\u636E\u3002</li><li><a href="https://github.com/prometheus/pushgateway" target="_blank" rel="noopener noreferrer">push gateway</a> \uFF0C\u7528\u4E8E\u6279\u91CF\uFF0C\u77ED\u671F\u7684\u76D1\u63A7\u6570\u636E\u7684\u6C47\u603B\u8282\u70B9\uFF0C\u4E3B\u8981\u7528\u4E8E\u4E1A\u52A1\u6570\u636E\u6C47\u62A5\u7B49\u3002</li><li>\u5404\u79CD\u6C47\u62A5\u6570\u636E\u7684 <a href="https://prometheus.io/docs/instrumenting/exporters/" target="_blank" rel="noopener noreferrer">exporters</a> \uFF0C\u4F8B\u5982\u6C47\u62A5\u673A\u5668\u6570\u636E\u7684 node_exporter, \u6C47\u62A5 MongoDB \u4FE1\u606F\u7684 <a href="https://github.com/dcu/mongodb_exporter" target="_blank" rel="noopener noreferrer">MongoDB exporter</a> \u7B49\u7B49\u3002</li><li>\u7528\u4E8E\u544A\u8B66\u901A\u77E5\u7BA1\u7406\u7684 <a href="https://github.com/prometheus/alertmanager" target="_blank" rel="noopener noreferrer">alertmanager</a> \u3002</li></ul><h3 id="\u57FA\u7840\u67B6\u6784" tabindex="-1">\u57FA\u7840\u67B6\u6784 <a class="header-anchor" href="#\u57FA\u7840\u67B6\u6784" aria-hidden="true">#</a></h3><p><img src="https://2584451478-files.gitbook.io/~/files/v0/b/gitbook-legacy-files/o/assets%2F-LBdoxo9EmQ0bJP2BuUi%2F-LPS8BVjkRvEjV8HmbBi%2F-LPS8D1gM9qp1zu_wp8y%2Fprometheus_architecture.png?generation=1540234733609534&amp;alt=media" alt="img"></p><blockquote><p>Prometheus Server\u662FPrometheus\u7EC4\u4EF6\u4E2D\u7684\u6838\u5FC3\u90E8\u5206\uFF0C\u8D1F\u8D23\u5B9E\u73B0\u5BF9\u76D1\u63A7\u6570\u636E\u7684\u83B7\u53D6\uFF0C\u5B58\u50A8\u4EE5\u53CA\u67E5\u8BE2\u3002 Prometheus Server\u53EF\u4EE5\u901A\u8FC7\u9759\u6001\u914D\u7F6E\u7BA1\u7406\u76D1\u63A7\u76EE\u6807\uFF0C\u4E5F\u53EF\u4EE5\u914D\u5408\u4F7F\u7528Service Discovery\u7684\u65B9\u5F0F\u52A8\u6001\u7BA1\u7406\u76D1\u63A7\u76EE\u6807\uFF0C\u5E76\u4ECE\u8FD9\u4E9B\u76D1\u63A7\u76EE\u6807\u4E2D\u83B7\u53D6\u6570\u636E\u3002\u5176\u6B21Prometheus Server\u9700\u8981\u5BF9\u91C7\u96C6\u5230\u7684\u76D1\u63A7\u6570\u636E\u8FDB\u884C\u5B58\u50A8\uFF0CPrometheus Server\u672C\u8EAB\u5C31\u662F\u4E00\u4E2A\u65F6\u5E8F\u6570\u636E\u5E93\uFF0C\u5C06\u91C7\u96C6\u5230\u7684\u76D1\u63A7\u6570\u636E\u6309\u7167\u65F6\u95F4\u5E8F\u5217\u7684\u65B9\u5F0F\u5B58\u50A8\u5728\u672C\u5730\u78C1\u76D8\u5F53\u4E2D\u3002\u6700\u540EPrometheus Server\u5BF9\u5916\u63D0\u4F9B\u4E86\u81EA\u5B9A\u4E49\u7684PromQL\u8BED\u8A00\uFF0C\u5B9E\u73B0\u5BF9\u6570\u636E\u7684\u67E5\u8BE2\u4EE5\u53CA\u5206\u6790\u3002</p><p>Prometheus Server\u5185\u7F6E\u7684Express Browser UI\uFF0C\u901A\u8FC7\u8FD9\u4E2AUI\u53EF\u4EE5\u76F4\u63A5\u901A\u8FC7PromQL\u5B9E\u73B0\u6570\u636E\u7684\u67E5\u8BE2\u4EE5\u53CA\u53EF\u89C6\u5316\u3002</p><p>Prometheus Server\u7684\u8054\u90A6\u96C6\u7FA4\u80FD\u529B\u53EF\u4EE5\u4F7F\u5176\u4ECE\u5176\u4ED6\u7684Prometheus Server\u5B9E\u4F8B\u4E2D\u83B7\u53D6\u6570\u636E\uFF0C\u56E0\u6B64\u5728\u5927\u89C4\u6A21\u76D1\u63A7\u7684\u60C5\u51B5\u4E0B\uFF0C\u53EF\u4EE5\u901A\u8FC7\u8054\u90A6\u96C6\u7FA4\u4EE5\u53CA\u529F\u80FD\u5206\u533A\u7684\u65B9\u5F0F\u5BF9Prometheus Server\u8FDB\u884C\u6269\u5C55\u3002</p></blockquote><p>\u5B83\u5927\u81F4\u4F7F\u7528\u903B\u8F91\u662F\u8FD9\u6837\uFF1A</p><ol><li>Prometheus server \u5B9A\u671F\u4ECE\u9759\u6001\u914D\u7F6E\u7684 targets \u6216\u8005\u670D\u52A1\u53D1\u73B0\u7684 targets \u62C9\u53D6\u6570\u636E\u3002</li><li>\u5F53\u65B0\u62C9\u53D6\u7684\u6570\u636E\u5927\u4E8E\u914D\u7F6E\u5185\u5B58\u7F13\u5B58\u533A\u7684\u65F6\u5019\uFF0CPrometheus \u4F1A\u5C06\u6570\u636E\u6301\u4E45\u5316\u5230\u78C1\u76D8\uFF08\u5982\u679C\u4F7F\u7528 remote storage \u5C06\u6301\u4E45\u5316\u5230\u4E91\u7AEF\uFF09\u3002</li><li>Prometheus \u53EF\u4EE5\u914D\u7F6E rules\uFF0C\u7136\u540E\u5B9A\u65F6\u67E5\u8BE2\u6570\u636E\uFF0C\u5F53\u6761\u4EF6\u89E6\u53D1\u7684\u65F6\u5019\uFF0C\u4F1A\u5C06 alert \u63A8\u9001\u5230\u914D\u7F6E\u7684 Alertmanager\u3002</li><li>Alertmanager \u6536\u5230\u8B66\u544A\u7684\u65F6\u5019\uFF0C\u53EF\u4EE5\u6839\u636E\u914D\u7F6E\uFF0C\u805A\u5408\uFF0C\u53BB\u91CD\uFF0C\u964D\u566A\uFF0C\u6700\u540E\u53D1\u9001\u8B66\u544A\u3002</li><li>\u53EF\u4EE5\u4F7F\u7528 API\uFF0C Prometheus Console \u6216\u8005 Grafana \u67E5\u8BE2\u548C\u805A\u5408\u6570\u636E\u3002</li></ol><h2 id="\u4E0B\u8F7D\u5E76\u8FD0\u884C-prom" tabindex="-1">\u4E0B\u8F7D\u5E76\u8FD0\u884C Prom <a class="header-anchor" href="#\u4E0B\u8F7D\u5E76\u8FD0\u884C-prom" aria-hidden="true">#</a></h2><ol><li>\u4E3A\u4E86\u5B89\u5168\u8D77\u89C1\uFF0C\u4F7F\u7528<code>prometheus</code>\u7528\u6237\u6765\u542F\u52A8\u670D\u52A1\uFF0C\u9996\u5148\u521B\u5EFA\u8FD9\u4E2A\u7528\u6237\uFF1A</li></ol><div class="language-bash"><span class="copy"></span><pre class="vp-code-dark"><code><span class="line"><span style="color:#DBD7CA;">groupadd prometheus</span></span>
<span class="line"><span style="color:#DBD7CA;">useradd -g prometheus -M -s /sbin/nologin prometheus</span></span>
<span class="line"></span></code></pre><pre class="vp-code-light"><code><span class="line"><span style="color:#393A34;">groupadd prometheus</span></span>
<span class="line"><span style="color:#393A34;">useradd -g prometheus -M -s /sbin/nologin prometheus</span></span>
<span class="line"></span></code></pre></div><ol start="2"><li>\u4ECE\u5B98\u65B9\u7AD9\u70B9\u4E0B\u8F7D\u5408\u9002\u7684\u5305\uFF0C\u5E76\u89E3\u538B\uFF1A</li></ol><div class="language-bash"><span class="copy"></span><pre class="vp-code-dark"><code><span class="line"><span style="color:#DBD7CA;">wget https://github.com/prometheus/prometheus/releases/download/v2.17.2/prometheus-2.17.2.linux-amd64.tar.gz</span></span>
<span class="line"><span style="color:#DBD7CA;">tar xf prometheus-2.17.2.linux-amd64.tar.gz -C /usr/share/</span></span>
<span class="line"><span style="color:#DBD7CA;">mv /usr/share/prometheus-2.17.2.linux-amd64 /usr/share/prometheus</span></span>
<span class="line"><span style="color:#DBD7CA;">mkdir -pv /usr/share/prometheus/data</span></span>
<span class="line"><span style="color:#DBD7CA;">chown -R prometheus.prometheus /usr/share/prometheus</span></span>
<span class="line"><span style="color:#E0A569;">cd</span><span style="color:#DBD7CA;"> /usr/share/prometheus</span></span>
<span class="line"></span></code></pre><pre class="vp-code-light"><code><span class="line"><span style="color:#393A34;">wget https://github.com/prometheus/prometheus/releases/download/v2.17.2/prometheus-2.17.2.linux-amd64.tar.gz</span></span>
<span class="line"><span style="color:#393A34;">tar xf prometheus-2.17.2.linux-amd64.tar.gz -C /usr/share/</span></span>
<span class="line"><span style="color:#393A34;">mv /usr/share/prometheus-2.17.2.linux-amd64 /usr/share/prometheus</span></span>
<span class="line"><span style="color:#393A34;">mkdir -pv /usr/share/prometheus/data</span></span>
<span class="line"><span style="color:#393A34;">chown -R prometheus.prometheus /usr/share/prometheus</span></span>
<span class="line"><span style="color:#B58451;">cd</span><span style="color:#393A34;"> /usr/share/prometheus</span></span>
<span class="line"></span></code></pre></div><p>\u2139\uFE0F \u5B98\u65B9\u4E0B\u8F7D\u7AD9\u70B9\uFF1A<a href="https://prometheus.io/download" target="_blank" rel="noopener noreferrer">https://prometheus.io/download</a></p><ol start="3"><li>\u521B\u5EFA prometheus \u7CFB\u7EDF\u670D\u52A1\u542F\u52A8\u6587\u4EF6 <code>/usr/lib/systemd/system/prometheus.service</code>\uFF1A</li></ol><div class="language-bash"><span class="copy"></span><pre class="vp-code-dark"><code><span class="line"><span style="color:#858585;">[</span><span style="color:#DBD7CA;">Unit</span><span style="color:#858585;">]</span></span>
<span class="line"><span style="color:#DBD7CA;">Description=Prometheus Server</span></span>
<span class="line"><span style="color:#DBD7CA;">Documentation=https://prometheus.io/docs/introduction/overview/</span></span>
<span class="line"><span style="color:#DBD7CA;">After=network-online.target</span></span>
<span class="line"></span>
<span class="line"><span style="color:#858585;">[</span><span style="color:#DBD7CA;">Service</span><span style="color:#858585;">]</span></span>
<span class="line"><span style="color:#DBD7CA;">User=prometheus</span></span>
<span class="line"><span style="color:#DBD7CA;">Restart=on-failure</span></span>
<span class="line"></span>
<span class="line"><span style="color:#758575;">#Change this line if you download the </span></span>
<span class="line"><span style="color:#758575;">#Prometheus on different path user</span></span>
<span class="line"><span style="color:#DBD7CA;">ExecStart=/usr/share/prometheus/prometheus \\</span></span>
<span class="line"><span style="color:#DBD7CA;">  --config.file=/usr/share/prometheus/prometheus.yml \\</span></span>
<span class="line"><span style="color:#DBD7CA;">  --storage.tsdb.path=/usr/share/prometheus/data</span></span>
<span class="line"></span>
<span class="line"><span style="color:#858585;">[</span><span style="color:#DBD7CA;">Install</span><span style="color:#858585;">]</span></span>
<span class="line"><span style="color:#DBD7CA;">WantedBy=multi-user.target</span></span>
<span class="line"></span></code></pre><pre class="vp-code-light"><code><span class="line"><span style="color:#8E8F8B;">[</span><span style="color:#393A34;">Unit</span><span style="color:#8E8F8B;">]</span></span>
<span class="line"><span style="color:#393A34;">Description=Prometheus Server</span></span>
<span class="line"><span style="color:#393A34;">Documentation=https://prometheus.io/docs/introduction/overview/</span></span>
<span class="line"><span style="color:#393A34;">After=network-online.target</span></span>
<span class="line"></span>
<span class="line"><span style="color:#8E8F8B;">[</span><span style="color:#393A34;">Service</span><span style="color:#8E8F8B;">]</span></span>
<span class="line"><span style="color:#393A34;">User=prometheus</span></span>
<span class="line"><span style="color:#393A34;">Restart=on-failure</span></span>
<span class="line"></span>
<span class="line"><span style="color:#A0ADA0;">#Change this line if you download the </span></span>
<span class="line"><span style="color:#A0ADA0;">#Prometheus on different path user</span></span>
<span class="line"><span style="color:#393A34;">ExecStart=/usr/share/prometheus/prometheus \\</span></span>
<span class="line"><span style="color:#393A34;">  --config.file=/usr/share/prometheus/prometheus.yml \\</span></span>
<span class="line"><span style="color:#393A34;">  --storage.tsdb.path=/usr/share/prometheus/data</span></span>
<span class="line"></span>
<span class="line"><span style="color:#8E8F8B;">[</span><span style="color:#393A34;">Install</span><span style="color:#8E8F8B;">]</span></span>
<span class="line"><span style="color:#393A34;">WantedBy=multi-user.target</span></span>
<span class="line"></span></code></pre></div><p>a . \u914D\u7F6E Prometheus \u6587\u4EF6<code>prometheus.yml</code>\u8FDB\u884C\u81EA\u6211\u76D1\u63A7\uFF1A</p><div class="language-bash"><span class="copy"></span><pre class="vp-code-dark"><code><span class="line"><span style="color:#DBD7CA;">global:</span></span>
<span class="line"><span style="color:#DBD7CA;">  scrape_interval:     15s </span><span style="color:#758575;"># By default, scrape targets every 15 seconds.</span></span>
<span class="line"></span>
<span class="line"><span style="color:#858585;">  </span><span style="color:#758575;"># Attach these labels to any time series or alerts when communicating with</span></span>
<span class="line"><span style="color:#858585;">  </span><span style="color:#758575;"># external systems (federation, remote storage, Alertmanager).</span></span>
<span class="line"><span style="color:#DBD7CA;">  external_labels:</span></span>
<span class="line"><span style="color:#DBD7CA;">    monitor: </span><span style="color:#C98A7D;">&#39;codelab-monitor&#39;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#758575;"># A scrape configuration containing exactly one endpoint to scrape:</span></span>
<span class="line"><span style="color:#758575;"># Here it&#39;s Prometheus itself.</span></span>
<span class="line"><span style="color:#DBD7CA;">scrape_configs:</span></span>
<span class="line"><span style="color:#858585;">  </span><span style="color:#758575;"># The job name is added as a label \`job=&lt;job_name&gt;\` to any timeseries scraped from this config.</span></span>
<span class="line"><span style="color:#DBD7CA;">  - job_name: </span><span style="color:#C98A7D;">&#39;prometheus&#39;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#858585;">    </span><span style="color:#758575;"># Override the global default and scrape targets from this job every 5 seconds.</span></span>
<span class="line"><span style="color:#DBD7CA;">    scrape_interval: 5s</span></span>
<span class="line"></span>
<span class="line"><span style="color:#DBD7CA;">    static_configs:</span></span>
<span class="line"><span style="color:#DBD7CA;">      - targets: </span><span style="color:#858585;">[</span><span style="color:#C98A7D;">&#39;localhost:9090&#39;</span><span style="color:#858585;">]</span></span>
<span class="line"></span></code></pre><pre class="vp-code-light"><code><span class="line"><span style="color:#393A34;">global:</span></span>
<span class="line"><span style="color:#393A34;">  scrape_interval:     15s </span><span style="color:#A0ADA0;"># By default, scrape targets every 15 seconds.</span></span>
<span class="line"></span>
<span class="line"><span style="color:#8E8F8B;">  </span><span style="color:#A0ADA0;"># Attach these labels to any time series or alerts when communicating with</span></span>
<span class="line"><span style="color:#8E8F8B;">  </span><span style="color:#A0ADA0;"># external systems (federation, remote storage, Alertmanager).</span></span>
<span class="line"><span style="color:#393A34;">  external_labels:</span></span>
<span class="line"><span style="color:#393A34;">    monitor: </span><span style="color:#B56959;">&#39;codelab-monitor&#39;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#A0ADA0;"># A scrape configuration containing exactly one endpoint to scrape:</span></span>
<span class="line"><span style="color:#A0ADA0;"># Here it&#39;s Prometheus itself.</span></span>
<span class="line"><span style="color:#393A34;">scrape_configs:</span></span>
<span class="line"><span style="color:#8E8F8B;">  </span><span style="color:#A0ADA0;"># The job name is added as a label \`job=&lt;job_name&gt;\` to any timeseries scraped from this config.</span></span>
<span class="line"><span style="color:#393A34;">  - job_name: </span><span style="color:#B56959;">&#39;prometheus&#39;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#8E8F8B;">    </span><span style="color:#A0ADA0;"># Override the global default and scrape targets from this job every 5 seconds.</span></span>
<span class="line"><span style="color:#393A34;">    scrape_interval: 5s</span></span>
<span class="line"></span>
<span class="line"><span style="color:#393A34;">    static_configs:</span></span>
<span class="line"><span style="color:#393A34;">      - targets: </span><span style="color:#8E8F8B;">[</span><span style="color:#B56959;">&#39;localhost:9090&#39;</span><span style="color:#8E8F8B;">]</span></span>
<span class="line"></span></code></pre></div><p>\u542F\u52A8 Prom \uFF0C<code>./prometheus --config.file=prometheus.yml</code>\u3010\u9ED8\u8BA4\u60C5\u51B5\u4E0B\uFF0CPrometheus \u5C06\u5176\u6570\u636E\u5E93\u5B58\u50A8\u5728.<code>/data</code>\u4E2D\uFF08\u53EF\u4EE5\u4F7F\u7528<code>--storage.tsdb.path</code>\u81EA\u5B9A\u4E49data\u8DEF\u5F84\uFF09\u3011</p><p>\u6B64\u65F6\u6253\u5F00\u6D4F\u89C8\u5668\uFF0C\u8FDB\u5165<code>localhost:9090</code>\u67E5\u770B\u5176\u81EA\u8EAB\u7684\u72B6\u6001\u9875\uFF0C\u6B64\u5916\u4F60\u8FD8\u53EF\u4EE5\u8BBF\u95EE<code>localhost:metrics</code>\u67E5\u770B\u5176\u6307\u6807\u53C2\u6570</p><blockquote><p>\u7B80\u5355\u5165\u95E8\uFF0C\u6765\u81EA\u5B98\u65B9\u6587\u6863.</p></blockquote><p>b. \u4FEE\u6539 prometheus \u914D\u7F6E\u6587\u4EF6 <code>prometheus.yml</code>\uFF1A</p><div class="language-bash"><span class="copy"></span><pre class="vp-code-dark"><code><span class="line"><span style="color:#DBD7CA;">global:</span></span>
<span class="line"><span style="color:#DBD7CA;">  scrape_interval:     15s </span></span>
<span class="line"><span style="color:#DBD7CA;">  evaluation_interval: 15s </span></span>
<span class="line"></span>
<span class="line"><span style="color:#DBD7CA;">alerting:</span></span>
<span class="line"><span style="color:#DBD7CA;">  alertmanagers:</span></span>
<span class="line"><span style="color:#DBD7CA;">  - static_configs:</span></span>
<span class="line"><span style="color:#DBD7CA;">    - targets: </span><span style="color:#858585;">[</span><span style="color:#C98A7D;">&quot;localhost:9093&quot;</span><span style="color:#858585;">]</span></span>
<span class="line"></span>
<span class="line"><span style="color:#DBD7CA;">rule_files:</span></span>
<span class="line"><span style="color:#858585;">  </span><span style="color:#758575;">#- &quot;alert.rules&quot;</span></span>
<span class="line"><span style="color:#DBD7CA;">  </span></span>
<span class="line"><span style="color:#DBD7CA;">scrape_configs:</span></span>
<span class="line"><span style="color:#DBD7CA;">  - job_name: </span><span style="color:#C98A7D;">&#39;prometheus&#39;</span></span>
<span class="line"><span style="color:#DBD7CA;">    scrape_interval:     5s</span></span>
<span class="line"><span style="color:#DBD7CA;">    static_configs:</span></span>
<span class="line"><span style="color:#DBD7CA;">      - targets: </span><span style="color:#858585;">[</span><span style="color:#C98A7D;">&#39;localhost:9090&#39;</span><span style="color:#858585;">]</span></span>
<span class="line"></span>
<span class="line"><span style="color:#DBD7CA;">  - job_name: </span><span style="color:#C98A7D;">&#39;node&#39;</span></span>
<span class="line"><span style="color:#DBD7CA;">    scrape_interval:     10s</span></span>
<span class="line"><span style="color:#DBD7CA;">    static_configs:</span></span>
<span class="line"><span style="color:#DBD7CA;">      - targets: </span><span style="color:#858585;">[</span><span style="color:#C98A7D;">&#39;localhost:9100&#39;</span><span style="color:#858585;">]</span></span>
<span class="line"></span></code></pre><pre class="vp-code-light"><code><span class="line"><span style="color:#393A34;">global:</span></span>
<span class="line"><span style="color:#393A34;">  scrape_interval:     15s </span></span>
<span class="line"><span style="color:#393A34;">  evaluation_interval: 15s </span></span>
<span class="line"></span>
<span class="line"><span style="color:#393A34;">alerting:</span></span>
<span class="line"><span style="color:#393A34;">  alertmanagers:</span></span>
<span class="line"><span style="color:#393A34;">  - static_configs:</span></span>
<span class="line"><span style="color:#393A34;">    - targets: </span><span style="color:#8E8F8B;">[</span><span style="color:#B56959;">&quot;localhost:9093&quot;</span><span style="color:#8E8F8B;">]</span></span>
<span class="line"></span>
<span class="line"><span style="color:#393A34;">rule_files:</span></span>
<span class="line"><span style="color:#8E8F8B;">  </span><span style="color:#A0ADA0;">#- &quot;alert.rules&quot;</span></span>
<span class="line"><span style="color:#393A34;">  </span></span>
<span class="line"><span style="color:#393A34;">scrape_configs:</span></span>
<span class="line"><span style="color:#393A34;">  - job_name: </span><span style="color:#B56959;">&#39;prometheus&#39;</span></span>
<span class="line"><span style="color:#393A34;">    scrape_interval:     5s</span></span>
<span class="line"><span style="color:#393A34;">    static_configs:</span></span>
<span class="line"><span style="color:#393A34;">      - targets: </span><span style="color:#8E8F8B;">[</span><span style="color:#B56959;">&#39;localhost:9090&#39;</span><span style="color:#8E8F8B;">]</span></span>
<span class="line"></span>
<span class="line"><span style="color:#393A34;">  - job_name: </span><span style="color:#B56959;">&#39;node&#39;</span></span>
<span class="line"><span style="color:#393A34;">    scrape_interval:     10s</span></span>
<span class="line"><span style="color:#393A34;">    static_configs:</span></span>
<span class="line"><span style="color:#393A34;">      - targets: </span><span style="color:#8E8F8B;">[</span><span style="color:#B56959;">&#39;localhost:9100&#39;</span><span style="color:#8E8F8B;">]</span></span>
<span class="line"></span></code></pre></div><p><em>\u8BE5\u914D\u7F6E\u6587\u4EF6\u6BD4\u8F83\u5B8C\u6574\uFF0C\u8BE5\u8282\u70B9\u5B89\u88C5\u4E86 <code>node_exporter </code>\u4EE5\u53CA <code>alertmanager </code>\u670D\u52A1\uFF0C\u53EF\u4EE5\u5148\u6309\u7167\u6B64\u914D\u7F6E\u6587\u4EF6\u505A\u914D\u7F6E\uFF0C\u4E0D\u5F71\u54CD\u670D\u52A1\u7684\u542F\u52A8\u8FC7\u7A0B</em></p><p>\u542F\u52A8\u670D\u52A1\uFF1A</p><div class="language-bash"><span class="copy"></span><pre class="vp-code-dark"><code><span class="line"><span style="color:#DBD7CA;">systemctl daemon-reload</span></span>
<span class="line"><span style="color:#DBD7CA;">systemctl start prometheus.service</span></span>
<span class="line"></span></code></pre><pre class="vp-code-light"><code><span class="line"><span style="color:#393A34;">systemctl daemon-reload</span></span>
<span class="line"><span style="color:#393A34;">systemctl start prometheus.service</span></span>
<span class="line"></span></code></pre></div><p>Prometheus \u670D\u52A1\u542F\u52A8\u5B8C\u6210\u540E\uFF0C\u53EF\u4EE5\u901A\u8FC7http://127.0.0.1:9090\u8BBF\u95EE Prometheus \u7684 UI \u754C\u9762</p><h2 id="\u5B89\u88C5\u914D\u7F6E-node-exporter" tabindex="-1">\u5B89\u88C5\u914D\u7F6E node_exporter <a class="header-anchor" href="#\u5B89\u88C5\u914D\u7F6E-node-exporter" aria-hidden="true">#</a></h2><p>\u4E3A\u76D1\u63A7\u670D\u52A1\u5668 CPU , \u5185\u5B58 , \u78C1\u76D8 , I/O \u7B49\u4FE1\u606F\uFF0C\u9700\u8981\u5728\u88AB\u76D1\u63A7\u673A\u5668\u4E0A\u5B89\u88C5 node_exporter \u670D\u52A1\u3002</p><p>\u4ECE\u5B98\u65B9\u7AD9\u70B9\u4E0B\u8F7D<code>node_exporter</code>\u5305\uFF1A</p><div class="language-bash"><span class="copy"></span><pre class="vp-code-dark"><code><span class="line"><span style="color:#DBD7CA;">wget https://github.com/prometheus/node_exporter/releases/download/v1.0.0-rc.0/node_exporter-1.0.0-rc.0.darwin-amd64.tar.gz</span></span>
<span class="line"><span style="color:#DBD7CA;">tar xf node_exporter-1.0.0-rc.0.darwin-amd64.tar.gz -C /usr/share/node_exporter</span></span>
<span class="line"><span style="color:#DBD7CA;">chown -R prometheus.prometheus /usr/share/node_exporter</span></span>
<span class="line"></span></code></pre><pre class="vp-code-light"><code><span class="line"><span style="color:#393A34;">wget https://github.com/prometheus/node_exporter/releases/download/v1.0.0-rc.0/node_exporter-1.0.0-rc.0.darwin-amd64.tar.gz</span></span>
<span class="line"><span style="color:#393A34;">tar xf node_exporter-1.0.0-rc.0.darwin-amd64.tar.gz -C /usr/share/node_exporter</span></span>
<span class="line"><span style="color:#393A34;">chown -R prometheus.prometheus /usr/share/node_exporter</span></span>
<span class="line"></span></code></pre></div><p>\u521B\u5EFA node_exporter \u7CFB\u7EDF\u670D\u52A1\u542F\u52A8\u6587\u4EF6 <code>/usr/lib/systemd/system/node_exporter.service</code>\uFF1A</p><div class="language-bash"><span class="copy"></span><pre class="vp-code-dark"><code><span class="line"><span style="color:#858585;">[</span><span style="color:#DBD7CA;">Unit</span><span style="color:#858585;">]</span></span>
<span class="line"><span style="color:#DBD7CA;">Description=Node Exporter</span></span>
<span class="line"><span style="color:#DBD7CA;">Wants=network-online.target</span></span>
<span class="line"><span style="color:#DBD7CA;">After=network-online.target</span></span>
<span class="line"></span>
<span class="line"><span style="color:#858585;">[</span><span style="color:#DBD7CA;">Service</span><span style="color:#858585;">]</span></span>
<span class="line"><span style="color:#DBD7CA;">User=prometheus</span></span>
<span class="line"><span style="color:#DBD7CA;">ExecStart=/usr/share/node_exporter/node_exporter</span></span>
<span class="line"></span>
<span class="line"><span style="color:#858585;">[</span><span style="color:#DBD7CA;">Install</span><span style="color:#858585;">]</span></span>
<span class="line"><span style="color:#DBD7CA;">WantedBy=default.target</span></span>
<span class="line"></span></code></pre><pre class="vp-code-light"><code><span class="line"><span style="color:#8E8F8B;">[</span><span style="color:#393A34;">Unit</span><span style="color:#8E8F8B;">]</span></span>
<span class="line"><span style="color:#393A34;">Description=Node Exporter</span></span>
<span class="line"><span style="color:#393A34;">Wants=network-online.target</span></span>
<span class="line"><span style="color:#393A34;">After=network-online.target</span></span>
<span class="line"></span>
<span class="line"><span style="color:#8E8F8B;">[</span><span style="color:#393A34;">Service</span><span style="color:#8E8F8B;">]</span></span>
<span class="line"><span style="color:#393A34;">User=prometheus</span></span>
<span class="line"><span style="color:#393A34;">ExecStart=/usr/share/node_exporter/node_exporter</span></span>
<span class="line"></span>
<span class="line"><span style="color:#8E8F8B;">[</span><span style="color:#393A34;">Install</span><span style="color:#8E8F8B;">]</span></span>
<span class="line"><span style="color:#393A34;">WantedBy=default.target</span></span>
<span class="line"></span></code></pre></div><p>\u542F\u52A8 node_exporter \u670D\u52A1\uFF1A</p><div class="language-bash"><span class="copy"></span><pre class="vp-code-dark"><code><span class="line"><span style="color:#DBD7CA;">systemctl daemon-reload</span></span>
<span class="line"><span style="color:#DBD7CA;">systemctl start node_exporter</span></span>
<span class="line"></span></code></pre><pre class="vp-code-light"><code><span class="line"><span style="color:#393A34;">systemctl daemon-reload</span></span>
<span class="line"><span style="color:#393A34;">systemctl start node_exporter</span></span>
<span class="line"></span></code></pre></div><p>\u670D\u52A1\u542F\u52A8\u540E\u53EF\u4EE5\u7528 <a href="http://127.0.0.1:9100/metrics" target="_blank" rel="noopener noreferrer">http://127.0.0.1:9100/metrics</a> \u6D4B\u8BD5 node_exporter \u662F\u5426\u83B7\u53D6\u5230\u8282\u70B9\u7684\u76D1\u63A7\u6307\u6807\u3002</p><h2 id="\u4F7F\u7528-grafana-\u5C55\u793A\u5DE5\u5177" tabindex="-1">\u4F7F\u7528 Grafana \u5C55\u793A\u5DE5\u5177 <a class="header-anchor" href="#\u4F7F\u7528-grafana-\u5C55\u793A\u5DE5\u5177" aria-hidden="true">#</a></h2><p>\u5B89\u88C5<code>Grafana</code>\uFF1A</p><div class="language-bash"><span class="copy"></span><pre class="vp-code-dark"><code><span class="line"><span style="color:#DBD7CA;">wget https://dl.grafana.com/oss/release/grafana-6.7.3-1.x86_64.rpm</span></span>
<span class="line"><span style="color:#DBD7CA;">sudo yum install grafana-6.7.3-1.x86_64.rpm</span></span>
<span class="line"></span></code></pre><pre class="vp-code-light"><code><span class="line"><span style="color:#393A34;">wget https://dl.grafana.com/oss/release/grafana-6.7.3-1.x86_64.rpm</span></span>
<span class="line"><span style="color:#393A34;">sudo yum install grafana-6.7.3-1.x86_64.rpm</span></span>
<span class="line"></span></code></pre></div><p>\u542F\u52A8 Grafana \u670D\u52A1\uFF1A</p><div class="language-bash"><span class="copy"></span><pre class="vp-code-dark"><code><span class="line"><span style="color:#DBD7CA;">systemctl start grafana-server</span></span>
<span class="line"></span></code></pre><pre class="vp-code-light"><code><span class="line"><span style="color:#393A34;">systemctl start grafana-server</span></span>
<span class="line"></span></code></pre></div><p>\u670D\u52A1\u542F\u52A8\u540E grafana \u9ED8\u8BA4\u76D1\u542C\u5728 3000 \u7AEF\u53E3 \uFF0C\u53EF\u4EE5\u901Ahttp://127.0.0.1:3000 \u8BBF\u95EE grafana \u7684 ui \u754C\u9762\uFF0C\u9ED8\u8BA4\u767B\u5F55\u8D26\u53F7\u5BC6\u7801\u4E3A <code>admin/admin</code> \uFF0C\u7B2C\u4E00\u6B21\u767B\u5F55\u9700\u8981\u6211\u4EEC\u91CD\u7F6E\u5BC6\u7801\u3002</p><p>\u6DFB\u52A0<code>Prometheus</code>\u6570\u636E\u6E90\uFF1A</p><p><code>Data Sources</code> --&gt; <code>Add Data Source</code> --&gt; <code>Prometheus</code></p><p>\u5BFC\u5165\u6A21\u677F\uFF1A</p><p><code>\u9009\u62E9\u5DE6\u4FA7\u83DC\u5355\u680F + \u53F7</code> --&gt; <code>import</code> --&gt; <code>\u586B\u5165id\u6216\u8005json\u683C\u5F0F\u6570\u636E</code></p><p><img src="http://agou-images.oss-cn-qingdao.aliyuncs.com/blog-images/promethues%20%2B%20grafana/grafana.png" alt=""></p><p>\u2139\uFE0F\u6A21\u677F\u6765\u81EA\uFF1A<a href="https://grafana.com/grafana/dashboards/8919" target="_blank" rel="noopener noreferrer">https://grafana.com/grafana/dashboards/8919</a></p><p>\u2139\uFE0F \u6A21\u677F\u6CE8\u610F\u4E8B\u9879\uFF1A</p><p>\u5BFC\u5165\u770B\u677F\u540E\uFF0C\u8BF7\u6839\u636E\u5B9E\u9645\u60C5\u51B5\u5728\u770B\u677F\u53F3\u4E0A\u89D2\u70B9\u51FB<code>Dashboard settings</code>--<code>Variables</code>\u8BBE\u7F6E\u597D\u53D8\u91CF\uFF1A</p><p><strong>\u9ED8\u8BA4\u5DF2\u7ECF\u8BBE\u7F6E\u5E76\u5173\u8054\u597D<code>job</code>\uFF0C<code>hostname</code>\uFF0C<code>node</code>\u8FD93\u4E2A\u53D8\u91CF\u3002</strong>\uFF08name\uFF0Cenv\u53D8\u91CF\u662F\u81EA\u5B9A\u4E49\u7684\u6807\u7B7E\uFF0C\u5DF2\u9690\u85CF\u3002\u5927\u5BB6\u53EF\u6839\u636E\u9700\u8981\u81EA\u884C\u589E\u52A0\u3002\uFF09</p><ul><li><strong><code>$node</code>\u53D6\u503Cnode_exporter\u7684<code>instance</code>\uFF0C<code>IP:\u7AEF\u53E3</code>\u683C\u5F0F\u3002\u5927\u90E8\u5206\u67E5\u8BE2\u5173\u8054\u4E86\u8FD9\u4E2A\u53D8\u91CF\uFF0C\u8BF7\u786E\u4FDD\u8BE5\u53D8\u91CF\u6709\u6548</strong></li><li><strong><code>$maxmount</code>\u7528\u6765\u67E5\u8BE2\u5F53\u524D\u4E3B\u673A\u7684\u6700\u5927\u5206\u533A\uFF0C\u9ED8\u8BA4\u53EA\u83B7\u53D6ext4\u548Cxfs\u7C7B\u578B\u7684\u5206\u533A\u3002</strong></li></ul><hr><p>\u26A0\uFE0F**\u6CE8\u610F\uFF1A**\u6240\u6709\u7684\u76D1\u63A7\u670D\u52A1\u90FD\u5F3A\u4F9D\u8D56\u65F6\u95F4\u670D\u52A1\u5668\uFF0C\u8981\u4FDD\u8BC1\u670D\u52A1\u5668\u5BA2\u6237\u7AEF\u548Cweb\u7AEF\u7684\u65F6\u95F4\u4E00\u81F4\uFF0C\u5426\u5219\u6570\u636E\u65E0\u6CD5\u6B63\u5E38\u5C55\u793A\u548C\u66F4\u65B0.</p><h2 id="\u53C2\u8003\u94FE\u63A5" tabindex="-1">\u53C2\u8003\u94FE\u63A5 <a class="header-anchor" href="#\u53C2\u8003\u94FE\u63A5" aria-hidden="true">#</a></h2><ul><li>prometheus\u5B98\u65B9\u6587\u6863\uFF1A<a href="https://prometheus.io/docs/prometheus/latest/getting_started/" target="_blank" rel="noopener noreferrer">https://prometheus.io/docs/prometheus/latest/getting_started/</a></li><li>grafana \u5B89\u88C5\u6587\u6863\uFF1A<a href="https://grafana.com/docs/grafana/latest/installation/rpm/" target="_blank" rel="noopener noreferrer">https://grafana.com/docs/grafana/latest/installation/rpm/</a></li><li>\u5982\u4F55\u5BFC\u5165\u4E00\u4E2A Grafana \u9762\u677F\uFF1A<a href="https://grafana.com/docs/grafana/latest/reference/export_import/#importing-a-dashboard" target="_blank" rel="noopener noreferrer">https://grafana.com/docs/grafana/latest/reference/export_import/#importing-a-dashboard</a></li><li>prometheus \u5165\u95E8\u8FDB\u9636\uFF1A<a href="https://yunlzheng.gitbook.io/prometheus-book/" target="_blank" rel="noopener noreferrer">https://yunlzheng.gitbook.io/prometheus-book/</a></li></ul>`,60),o=[p];function r(t,c,i,d,h,u){return a(),n("div",null,o)}var g=s(l,[["render",r]]);export{y as __pageData,g as default};
