import{_ as s,o as a,c as n,a as l}from"./app.1e6146c3.js";const A=JSON.parse('{"title":"GitLab Basic","description":"","frontmatter":{},"headers":[{"level":2,"title":"GitLab \u7B80\u4ECB","slug":"gitlab-\u7B80\u4ECB"},{"level":2,"title":"Installing GitLab via Package","slug":"installing-gitlab-via-package"},{"level":2,"title":"Installing GitLab with Docker","slug":"installing-gitlab-with-docker"},{"level":2,"title":"\u914D\u7F6E HTTPS \u8BC1\u4E66","slug":"\u914D\u7F6E-https-\u8BC1\u4E66"},{"level":2,"title":"SMTP \u90AE\u4EF6\u8BBE\u7F6E","slug":"smtp-\u90AE\u4EF6\u8BBE\u7F6E"},{"level":2,"title":"Testing the SMTP configuration","slug":"testing-the-smtp-configuration"},{"level":2,"title":"\u7248\u672C\u5347\u7EA7","slug":"\u7248\u672C\u5347\u7EA7"},{"level":2,"title":"\u53C2\u8003\u94FE\u63A5","slug":"\u53C2\u8003\u94FE\u63A5"}],"relativePath":"CloudNative/cicd/GitLab/GitLab Basic.md","lastUpdated":1657272051000}'),p={name:"CloudNative/cicd/GitLab/GitLab Basic.md"},e=l(`<h1 id="gitlab-basic" tabindex="-1">GitLab Basic <a class="header-anchor" href="#gitlab-basic" aria-hidden="true">#</a></h1><h2 id="gitlab-\u7B80\u4ECB" tabindex="-1">GitLab \u7B80\u4ECB <a class="header-anchor" href="#gitlab-\u7B80\u4ECB" aria-hidden="true">#</a></h2><p><strong>GitLab</strong> \u662F\u7531 GitLab Inc.\u5F00\u53D1\uFF0C\u4E00\u6B3E\u57FA\u4E8E <a href="https://zh.wikipedia.org/wiki/Git" target="_blank" rel="noopener noreferrer">Git</a> \u7684\u5B8C\u5168\u96C6\u6210\u7684\u8F6F\u4EF6\u5F00\u53D1\u5E73\u53F0\uFF08fully <a href="https://zh.wikipedia.org/w/index.php?title=%E9%9B%86%E6%88%90%E8%BB%9F%E9%AB%94&amp;action=edit&amp;redlink=1" target="_blank" rel="noopener noreferrer">\u96C6\u6210\u8F6F\u4EF6</a> development platform\uFF09\u3002[<a href="https://zh.wikipedia.org/wiki/GitLab#cite_note-5" target="_blank" rel="noopener noreferrer">5]</a>[<a href="https://zh.wikipedia.org/wiki/GitLab#cite_note-6" target="_blank" rel="noopener noreferrer">6]</a> \u53E6\u5916\uFF0CGitLab \u4E14\u5177\u6709<a href="https://zh.wikipedia.org/wiki/Wiki" target="_blank" rel="noopener noreferrer">wiki</a>\u4EE5\u53CA\u5728\u7EBF\u7F16\u8F91\u3001<a href="https://zh.wikipedia.org/wiki/%E4%BA%8B%E5%8A%A1%E8%B7%9F%E8%B8%AA%E7%AE%A1%E7%90%86%E7%B3%BB%E7%BB%9F" target="_blank" rel="noopener noreferrer">issue\u8DDF\u8E2A</a>\u529F\u80FD\u3001CI/CD \u7B49\u529F\u80FD</p><p>\u8BE6\u60C5\u53C2\u8003\uFF1A<a href="https://zh.wikipedia.org/wiki/GitLab" target="_blank" rel="noopener noreferrer">https://zh.wikipedia.org/wiki/GitLab</a></p><h2 id="installing-gitlab-via-package" tabindex="-1">Installing GitLab via Package <a class="header-anchor" href="#installing-gitlab-via-package" aria-hidden="true">#</a></h2><p>\u5B98\u65B9\u6240\u63D0\u4F9B\u7684\u5B89\u88C5\u6B65\u9AA4\uFF08<code>GitLab EE </code>\u7248\u672C\uFF09\uFF1A<a href="https://about.gitlab.com/install/#centos-7" target="_blank" rel="noopener noreferrer">https://about.gitlab.com/install/#centos-7</a></p><p>\u61D2\u4EBA\u5B89\u88C5\u65B9\u6CD5\uFF08\u901A\u8FC7rpm\u5305\u5B89\u88C5\uFF0C<code>GitLab CE</code>\u7248\u672C\uFF09\uFF1A</p><div class="language-bash"><span class="copy"></span><pre class="vp-code-dark"><code><span class="line"><span style="color:#758575;"># \u5B89\u88C5\u6240\u9700\u4F9D\u8D56</span></span>
<span class="line"><span style="color:#DBD7CA;">yum install -y curl policycoreutils-python openssh-server postfix</span></span>
<span class="line"></span>
<span class="line"><span style="color:#758575;"># \u4E0B\u8F7DCE\u5305</span></span>
<span class="line"><span style="color:#DBD7CA;">wget https://mirrors.tuna.tsinghua.edu.cn/gitlab-ce/yum/el7/gitlab-ce-13.1.4-ce.0.el7.x86_64.rpm</span></span>
<span class="line"><span style="color:#DBD7CA;">yum install -y gitlab-ce-13.1.4-ce</span></span>
<span class="line"></span></code></pre><pre class="vp-code-light"><code><span class="line"><span style="color:#A0ADA0;"># \u5B89\u88C5\u6240\u9700\u4F9D\u8D56</span></span>
<span class="line"><span style="color:#393A34;">yum install -y curl policycoreutils-python openssh-server postfix</span></span>
<span class="line"></span>
<span class="line"><span style="color:#A0ADA0;"># \u4E0B\u8F7DCE\u5305</span></span>
<span class="line"><span style="color:#393A34;">wget https://mirrors.tuna.tsinghua.edu.cn/gitlab-ce/yum/el7/gitlab-ce-13.1.4-ce.0.el7.x86_64.rpm</span></span>
<span class="line"><span style="color:#393A34;">yum install -y gitlab-ce-13.1.4-ce</span></span>
<span class="line"></span></code></pre></div><p>\u7F16\u8F91<code>gitlab</code>\u914D\u7F6E\u6587\u4EF6<code>/etc/gitlab/gitlab.rb</code>\uFF1A</p><div class="language-bash"><span class="copy"></span><pre class="vp-code-dark"><code><span class="line"><span style="color:#758575;"># \u5927\u6982\u572829\u884C,\u5C06url\u6539\u4E3A\u672C\u673A\u5730\u5740,\u5F53\u7136\u5728\u8FD9\u91CC\u4F60\u53EF\u4EE5\u76F4\u63A5\u586B\u5199\u4F60\u7684\u57DF\u540D</span></span>
<span class="line"><span style="color:#DBD7CA;">external_url </span><span style="color:#C98A7D;">&#39;http://172.16.1.131&#39;</span></span>
<span class="line"><span style="color:#758575;"># \u65F6\u533A</span></span>
<span class="line"><span style="color:#DBD7CA;">gitlab_rails</span><span style="color:#858585;">[</span><span style="color:#C98A7D;">&#39;time_zone&#39;</span><span style="color:#858585;">]</span><span style="color:#DBD7CA;"> = </span><span style="color:#C98A7D;">&#39;Asia/Shanghai&#39;</span></span>
<span class="line"><span style="color:#758575;"># ssh\u62C9\u53D6\u7AEF\u53E3</span></span>
<span class="line"><span style="color:#DBD7CA;">gitlab_rails</span><span style="color:#858585;">[</span><span style="color:#C98A7D;">&#39;gitlab_shell_ssh_port&#39;</span><span style="color:#858585;">]</span><span style="color:#DBD7CA;"> = 10222</span></span>
<span class="line"><span style="color:#758575;"># \u5E76\u6DFB\u52A0\u4EE5\u4E0B\u5185\u5BB9</span></span>
<span class="line"><span style="color:#DBD7CA;">letsencrypt</span><span style="color:#858585;">[</span><span style="color:#C98A7D;">&#39;enable&#39;</span><span style="color:#858585;">]</span><span style="color:#DBD7CA;"> = </span><span style="color:#E0A569;">false</span><span style="color:#DBD7CA;"> </span></span>
<span class="line"></span></code></pre><pre class="vp-code-light"><code><span class="line"><span style="color:#A0ADA0;"># \u5927\u6982\u572829\u884C,\u5C06url\u6539\u4E3A\u672C\u673A\u5730\u5740,\u5F53\u7136\u5728\u8FD9\u91CC\u4F60\u53EF\u4EE5\u76F4\u63A5\u586B\u5199\u4F60\u7684\u57DF\u540D</span></span>
<span class="line"><span style="color:#393A34;">external_url </span><span style="color:#B56959;">&#39;http://172.16.1.131&#39;</span></span>
<span class="line"><span style="color:#A0ADA0;"># \u65F6\u533A</span></span>
<span class="line"><span style="color:#393A34;">gitlab_rails</span><span style="color:#8E8F8B;">[</span><span style="color:#B56959;">&#39;time_zone&#39;</span><span style="color:#8E8F8B;">]</span><span style="color:#393A34;"> = </span><span style="color:#B56959;">&#39;Asia/Shanghai&#39;</span></span>
<span class="line"><span style="color:#A0ADA0;"># ssh\u62C9\u53D6\u7AEF\u53E3</span></span>
<span class="line"><span style="color:#393A34;">gitlab_rails</span><span style="color:#8E8F8B;">[</span><span style="color:#B56959;">&#39;gitlab_shell_ssh_port&#39;</span><span style="color:#8E8F8B;">]</span><span style="color:#393A34;"> = 10222</span></span>
<span class="line"><span style="color:#A0ADA0;"># \u5E76\u6DFB\u52A0\u4EE5\u4E0B\u5185\u5BB9</span></span>
<span class="line"><span style="color:#393A34;">letsencrypt</span><span style="color:#8E8F8B;">[</span><span style="color:#B56959;">&#39;enable&#39;</span><span style="color:#8E8F8B;">]</span><span style="color:#393A34;"> = </span><span style="color:#B58451;">false</span><span style="color:#393A34;"> </span></span>
<span class="line"></span></code></pre></div><p>\u542F\u52A8\u76F8\u5173\u670D\u52A1\uFF1A</p><div class="language-bash"><span class="copy"></span><pre class="vp-code-dark"><code><span class="line"><span style="color:#DBD7CA;">systemctl start postfix</span></span>
<span class="line"><span style="color:#DBD7CA;">systemctl start sshd</span></span>
<span class="line"></span></code></pre><pre class="vp-code-light"><code><span class="line"><span style="color:#393A34;">systemctl start postfix</span></span>
<span class="line"><span style="color:#393A34;">systemctl start sshd</span></span>
<span class="line"></span></code></pre></div><p>\u521D\u59CB\u5316<code>GitLab</code>\u670D\u52A1:</p><div class="language-bash"><span class="copy"></span><pre class="vp-code-dark"><code><span class="line"><span style="color:#DBD7CA;">gitlab-ctl reconfigure</span></span>
<span class="line"></span></code></pre><pre class="vp-code-light"><code><span class="line"><span style="color:#393A34;">gitlab-ctl reconfigure</span></span>
<span class="line"></span></code></pre></div><p>\u9ED8\u8BA4GitLab \u662F\u5B89\u88C5\u5230<code>/opt/gitlab </code>\u76EE\u5F55\uFF0C\u914D\u7F6E\u6587\u4EF6\u5728<code>/etc/gitlab/</code>\u4E0B.</p><p>\u5B89\u88C5\u5B8C\u6210\u4E4B\u540E, \u6253\u5F00\u6D4F\u89C8\u5668\u8FDB\u884C\u8BBF\u95EE\u5373\u53EF:</p><p><img src="https://agou-images.oss-cn-qingdao.aliyuncs.com/blog-images/CI%26CD/gitlab.png" alt=""></p><p>\u26A0\uFE0F\u9ED8\u8BA4\u7528\u6237\u540D\u4E3A<code>root</code>, \u5BC6\u7801\u9996\u6B21\u4F7F\u7528<code>GitLab</code>\u65F6\u4F1A\u63D0\u793A\u8BBE\u7F6E.</p><blockquote><p>GitLab EE \u4E0E CE \u7248\u672C\u6BD4\u8F83\uFF1A<a href="https://about.gitlab.com/features/#compare" target="_blank" rel="noopener noreferrer">https://about.gitlab.com/features/#compare</a></p></blockquote><h2 id="installing-gitlab-with-docker" tabindex="-1">Installing GitLab with Docker <a class="header-anchor" href="#installing-gitlab-with-docker" aria-hidden="true">#</a></h2><p>GitLab \u4ED3\u5E93\u955C\u50CF\uFF1A<a href="https://hub.docker.com/r/gitlab/gitlab-ce/" target="_blank" rel="noopener noreferrer">https://hub.docker.com/r/gitlab/gitlab-ce/</a></p><h2 id="\u914D\u7F6E-https-\u8BC1\u4E66" tabindex="-1">\u914D\u7F6E HTTPS \u8BC1\u4E66 <a class="header-anchor" href="#\u914D\u7F6E-https-\u8BC1\u4E66" aria-hidden="true">#</a></h2><p>\u4E3A<code>GitLab</code>\u7684ssl\u8BC1\u4E66\u521B\u5EFA\u76EE\u5F55: <code>mkdir -pv /etc/gitlab/ssl</code></p><p>\u4FEE\u6539\u914D\u7F6E\u6587\u4EF6<code>/etc/gitlab/gitlab.rb</code>:</p><div class="language-bash"><span class="copy"></span><pre class="vp-code-dark"><code><span class="line"><span style="color:#DBD7CA;">external_url </span><span style="color:#C98A7D;">&#39;https://gitlab.agou-ops.top&#39;</span><span style="color:#DBD7CA;">  </span><span style="color:#758575;"># gitlab\u7533\u8BF7\u7684\u57DF\u540D\u8BC1\u4E66</span></span>
<span class="line"><span style="color:#DBD7CA;">nginx</span><span style="color:#858585;">[</span><span style="color:#C98A7D;">&#39;redirect_http_to_https&#39;</span><span style="color:#858585;">]</span><span style="color:#DBD7CA;">=true</span></span>
<span class="line"><span style="color:#DBD7CA;">nginx</span><span style="color:#858585;">[</span><span style="color:#C98A7D;">&#39;ssl_certificate&#39;</span><span style="color:#858585;">]</span><span style="color:#DBD7CA;"> = </span><span style="color:#C98A7D;">&quot;/etc/gitlab/ssl/gitlab.pem&quot;</span></span>
<span class="line"><span style="color:#DBD7CA;">nginx</span><span style="color:#858585;">[</span><span style="color:#C98A7D;">&#39;ssl_certificate_key&#39;</span><span style="color:#858585;">]</span><span style="color:#DBD7CA;"> = </span><span style="color:#C98A7D;">&quot;/etc/gitlab/ssl/gitlab.key&quot;</span></span>
<span class="line"></span></code></pre><pre class="vp-code-light"><code><span class="line"><span style="color:#393A34;">external_url </span><span style="color:#B56959;">&#39;https://gitlab.agou-ops.top&#39;</span><span style="color:#393A34;">  </span><span style="color:#A0ADA0;"># gitlab\u7533\u8BF7\u7684\u57DF\u540D\u8BC1\u4E66</span></span>
<span class="line"><span style="color:#393A34;">nginx</span><span style="color:#8E8F8B;">[</span><span style="color:#B56959;">&#39;redirect_http_to_https&#39;</span><span style="color:#8E8F8B;">]</span><span style="color:#393A34;">=true</span></span>
<span class="line"><span style="color:#393A34;">nginx</span><span style="color:#8E8F8B;">[</span><span style="color:#B56959;">&#39;ssl_certificate&#39;</span><span style="color:#8E8F8B;">]</span><span style="color:#393A34;"> = </span><span style="color:#B56959;">&quot;/etc/gitlab/ssl/gitlab.pem&quot;</span></span>
<span class="line"><span style="color:#393A34;">nginx</span><span style="color:#8E8F8B;">[</span><span style="color:#B56959;">&#39;ssl_certificate_key&#39;</span><span style="color:#8E8F8B;">]</span><span style="color:#393A34;"> = </span><span style="color:#B56959;">&quot;/etc/gitlab/ssl/gitlab.key&quot;</span></span>
<span class="line"></span></code></pre></div><p>\u66F4\u65B0<code>gitlab</code>\u7684\u914D\u7F6E:</p><div class="language-bash"><span class="copy"></span><pre class="vp-code-dark"><code><span class="line"><span style="color:#DBD7CA;">gitlab-ctl reconfigure</span></span>
<span class="line"></span></code></pre><pre class="vp-code-light"><code><span class="line"><span style="color:#393A34;">gitlab-ctl reconfigure</span></span>
<span class="line"></span></code></pre></div><p>\u6700\u540E\u4FEE\u6539<code>nginx</code>\u7684\u914D\u7F6E\u6587\u4EF6<code>/etc/nginx/conf.d/gitlab.conf</code>:</p><div class="language-bash"><span class="copy"></span><pre class="vp-code-dark"><code><span class="line"><span style="color:#DBD7CA;">server </span><span style="color:#858585;">{</span></span>
<span class="line"><span style="color:#DBD7CA;">      listen </span><span style="color:#CB7676;">*</span><span style="color:#DBD7CA;">:443 ssl http2</span><span style="color:#CB7676;">;</span></span>
<span class="line"><span style="color:#DBD7CA;">      server_name gitlab.agou-ops.top</span><span style="color:#CB7676;">;</span></span>
<span class="line"><span style="color:#DBD7CA;">      error_log   /home/logs/nginx/gitlab.mydomain.com.com.error.log error</span><span style="color:#CB7676;">;</span></span>
<span class="line"><span style="color:#DBD7CA;">      access_log  /home/logs/nginx/gitlab.mydomain.com.access.log  main</span><span style="color:#CB7676;">;</span></span>
<span class="line"><span style="color:#DBD7CA;">      server_tokens off</span><span style="color:#CB7676;">;</span><span style="color:#DBD7CA;"> </span></span>
<span class="line"><span style="color:#DBD7CA;">      client_max_body_size 0</span><span style="color:#CB7676;">;</span></span>
<span class="line"><span style="color:#DBD7CA;">      ssl on</span><span style="color:#CB7676;">;</span></span>
<span class="line"><span style="color:#DBD7CA;">      ssl_certificate /etc/gitlab/ssl/gitlab.pem</span><span style="color:#CB7676;">;</span></span>
<span class="line"><span style="color:#DBD7CA;">      ssl_certificate_key /etc/gitlab/ssl/gitlab.key</span><span style="color:#CB7676;">;</span></span>
<span class="line"><span style="color:#DBD7CA;">...</span></span>
<span class="line"><span style="color:#858585;">}</span></span>
<span class="line"></span>
<span class="line"><span style="color:#DBD7CA;">server{</span></span>
<span class="line"><span style="color:#DBD7CA;">    listen</span><span style="color:#CB7676;">*</span><span style="color:#DBD7CA;">:80</span><span style="color:#CB7676;">;</span></span>
<span class="line"><span style="color:#DBD7CA;">    server_name gitlab.agou-ops.top</span><span style="color:#CB7676;">;</span></span>
<span class="line"><span style="color:#DBD7CA;">    rewrite^</span><span style="color:#858585;">(</span><span style="color:#DBD7CA;">.</span><span style="color:#CB7676;">*</span><span style="color:#858585;">)$</span><span style="color:#B8A965;">https</span><span style="color:#DBD7CA;">://</span><span style="color:#858585;">$</span><span style="color:#B8A965;">host</span><span style="color:#858585;">$</span><span style="color:#B8A965;">1</span><span style="color:#DBD7CA;">permanent</span><span style="color:#CB7676;">;</span></span>
<span class="line"><span style="color:#858585;">    </span><span style="color:#758575;"># \u6216\u8005</span></span>
<span class="line"><span style="color:#858585;">    </span><span style="color:#758575;"># return      301 https://$server_name$request_uri;</span></span>
<span class="line"><span style="color:#DBD7CA;">}</span></span>
<span class="line"></span></code></pre><pre class="vp-code-light"><code><span class="line"><span style="color:#393A34;">server </span><span style="color:#8E8F8B;">{</span></span>
<span class="line"><span style="color:#393A34;">      listen </span><span style="color:#AB5959;">*</span><span style="color:#393A34;">:443 ssl http2</span><span style="color:#AB5959;">;</span></span>
<span class="line"><span style="color:#393A34;">      server_name gitlab.agou-ops.top</span><span style="color:#AB5959;">;</span></span>
<span class="line"><span style="color:#393A34;">      error_log   /home/logs/nginx/gitlab.mydomain.com.com.error.log error</span><span style="color:#AB5959;">;</span></span>
<span class="line"><span style="color:#393A34;">      access_log  /home/logs/nginx/gitlab.mydomain.com.access.log  main</span><span style="color:#AB5959;">;</span></span>
<span class="line"><span style="color:#393A34;">      server_tokens off</span><span style="color:#AB5959;">;</span><span style="color:#393A34;"> </span></span>
<span class="line"><span style="color:#393A34;">      client_max_body_size 0</span><span style="color:#AB5959;">;</span></span>
<span class="line"><span style="color:#393A34;">      ssl on</span><span style="color:#AB5959;">;</span></span>
<span class="line"><span style="color:#393A34;">      ssl_certificate /etc/gitlab/ssl/gitlab.pem</span><span style="color:#AB5959;">;</span></span>
<span class="line"><span style="color:#393A34;">      ssl_certificate_key /etc/gitlab/ssl/gitlab.key</span><span style="color:#AB5959;">;</span></span>
<span class="line"><span style="color:#393A34;">...</span></span>
<span class="line"><span style="color:#8E8F8B;">}</span></span>
<span class="line"></span>
<span class="line"><span style="color:#393A34;">server{</span></span>
<span class="line"><span style="color:#393A34;">    listen</span><span style="color:#AB5959;">*</span><span style="color:#393A34;">:80</span><span style="color:#AB5959;">;</span></span>
<span class="line"><span style="color:#393A34;">    server_name gitlab.agou-ops.top</span><span style="color:#AB5959;">;</span></span>
<span class="line"><span style="color:#393A34;">    rewrite^</span><span style="color:#8E8F8B;">(</span><span style="color:#393A34;">.</span><span style="color:#AB5959;">*</span><span style="color:#8E8F8B;">)$</span><span style="color:#8C862B;">https</span><span style="color:#393A34;">://</span><span style="color:#8E8F8B;">$</span><span style="color:#8C862B;">host</span><span style="color:#8E8F8B;">$</span><span style="color:#8C862B;">1</span><span style="color:#393A34;">permanent</span><span style="color:#AB5959;">;</span></span>
<span class="line"><span style="color:#8E8F8B;">    </span><span style="color:#A0ADA0;"># \u6216\u8005</span></span>
<span class="line"><span style="color:#8E8F8B;">    </span><span style="color:#A0ADA0;"># return      301 https://$server_name$request_uri;</span></span>
<span class="line"><span style="color:#393A34;">}</span></span>
<span class="line"></span></code></pre></div><p>\u91CD\u542F<code>GitLab</code>:</p><div class="language-bash"><span class="copy"></span><pre class="vp-code-dark"><code><span class="line"><span style="color:#DBD7CA;">gitlab-cli restart</span></span>
<span class="line"></span></code></pre><pre class="vp-code-light"><code><span class="line"><span style="color:#393A34;">gitlab-cli restart</span></span>
<span class="line"></span></code></pre></div><h2 id="smtp-\u90AE\u4EF6\u8BBE\u7F6E" tabindex="-1">SMTP \u90AE\u4EF6\u8BBE\u7F6E <a class="header-anchor" href="#smtp-\u90AE\u4EF6\u8BBE\u7F6E" aria-hidden="true">#</a></h2><p>QQ exmail</p><p>QQ exmail (\u817E\u8BAF\u4F01\u4E1A\u90AE\u7BB1)</p><div class="language-"><span class="copy"></span><pre class="vp-code-dark"><code><span class="line"><span style="color:#dbd7ca;">gitlab_rails[&#39;smtp_enable&#39;] = true</span></span>
<span class="line"><span style="color:#dbd7ca;">gitlab_rails[&#39;smtp_address&#39;] = &quot;smtp.exmail.qq.com&quot;</span></span>
<span class="line"><span style="color:#dbd7ca;">gitlab_rails[&#39;smtp_port&#39;] = 465</span></span>
<span class="line"><span style="color:#dbd7ca;">gitlab_rails[&#39;smtp_user_name&#39;] = &quot;xxxx@xx.com&quot;</span></span>
<span class="line"><span style="color:#dbd7ca;">gitlab_rails[&#39;smtp_password&#39;] = &quot;password&quot;</span></span>
<span class="line"><span style="color:#dbd7ca;">gitlab_rails[&#39;smtp_authentication&#39;] = &quot;login&quot;</span></span>
<span class="line"><span style="color:#dbd7ca;">gitlab_rails[&#39;smtp_enable_starttls_auto&#39;] = true</span></span>
<span class="line"><span style="color:#dbd7ca;">gitlab_rails[&#39;smtp_tls&#39;] = true</span></span>
<span class="line"><span style="color:#dbd7ca;">gitlab_rails[&#39;gitlab_email_from&#39;] = &#39;xxxx@xx.com&#39;</span></span>
<span class="line"><span style="color:#dbd7ca;">gitlab_rails[&#39;smtp_domain&#39;] = &quot;exmail.qq.com&quot;</span></span>
<span class="line"><span style="color:#dbd7ca;"></span></span></code></pre><pre class="vp-code-light"><code><span class="line"><span style="color:#393a34;">gitlab_rails[&#39;smtp_enable&#39;] = true</span></span>
<span class="line"><span style="color:#393a34;">gitlab_rails[&#39;smtp_address&#39;] = &quot;smtp.exmail.qq.com&quot;</span></span>
<span class="line"><span style="color:#393a34;">gitlab_rails[&#39;smtp_port&#39;] = 465</span></span>
<span class="line"><span style="color:#393a34;">gitlab_rails[&#39;smtp_user_name&#39;] = &quot;xxxx@xx.com&quot;</span></span>
<span class="line"><span style="color:#393a34;">gitlab_rails[&#39;smtp_password&#39;] = &quot;password&quot;</span></span>
<span class="line"><span style="color:#393a34;">gitlab_rails[&#39;smtp_authentication&#39;] = &quot;login&quot;</span></span>
<span class="line"><span style="color:#393a34;">gitlab_rails[&#39;smtp_enable_starttls_auto&#39;] = true</span></span>
<span class="line"><span style="color:#393a34;">gitlab_rails[&#39;smtp_tls&#39;] = true</span></span>
<span class="line"><span style="color:#393a34;">gitlab_rails[&#39;gitlab_email_from&#39;] = &#39;xxxx@xx.com&#39;</span></span>
<span class="line"><span style="color:#393a34;">gitlab_rails[&#39;smtp_domain&#39;] = &quot;exmail.qq.com&quot;</span></span>
<span class="line"><span style="color:#393a34;"></span></span></code></pre></div><blockquote><p>\u66F4\u591A\u90AE\u7BB1\u683C\u5F0F, \u53C2\u8003<a href="https://docs.gitlab.com/omnibus/settings/smtp.html#smtp-settings" target="_blank" rel="noopener noreferrer">GitLab SMTP Settings</a></p></blockquote><p>\u91CD\u542F<code>GitLab</code>\u670D\u52A1\u5668, \u4F7F\u914D\u7F6E\u751F\u6548:</p><div class="language-bash"><span class="copy"></span><pre class="vp-code-dark"><code><span class="line"><span style="color:#DBD7CA;">gitlab-ctl restart</span></span>
<span class="line"></span></code></pre><pre class="vp-code-light"><code><span class="line"><span style="color:#393A34;">gitlab-ctl restart</span></span>
<span class="line"></span></code></pre></div><p>\u6D4B\u8BD5SMTP\u90AE\u4EF6\u662F\u5426\u6B63\u5E38\u8FD0\u884C:</p><h2 id="testing-the-smtp-configuration" tabindex="-1">Testing the SMTP configuration <a class="header-anchor" href="#testing-the-smtp-configuration" aria-hidden="true">#</a></h2><p>You can verify GitLab\u2019s ability to send emails properly using the Rails console. On the GitLab server, execute <code>gitlab-rails console</code> to enter the console. Then, you can enter the following command at the console prompt to cause GitLab to send a test email:</p><div class="language-bash"><span class="copy"></span><pre class="vp-code-dark"><code><span class="line"><span style="color:#858585;">[</span><span style="color:#DBD7CA;">root@aliyun </span><span style="color:#CB7676;">~</span><span style="color:#858585;">]</span><span style="color:#D4976C;">\\#</span><span style="color:#DBD7CA;"> gitlab-rails console</span></span>
<span class="line"><span style="color:#DBD7CA;">--------------------------------------------------------------------------------</span></span>
<span class="line"><span style="color:#DBD7CA;"> GitLab:       13.1.4 </span><span style="color:#858585;">(</span><span style="color:#DBD7CA;">18c5ab32b73</span><span style="color:#858585;">)</span><span style="color:#DBD7CA;"> FOSS</span></span>
<span class="line"><span style="color:#DBD7CA;"> GitLab Shell: 13.3.0</span></span>
<span class="line"><span style="color:#DBD7CA;"> PostgreSQL:   11.7</span></span>
<span class="line"><span style="color:#DBD7CA;">--------------------------------------------------------------------------------</span></span>
<span class="line"><span style="color:#DBD7CA;">Loading production environment </span><span style="color:#858585;">(</span><span style="color:#DBD7CA;">Rails 6.0.3.1</span><span style="color:#858585;">)</span></span>
<span class="line"><span style="color:#DBD7CA;">irb</span><span style="color:#858585;">(</span><span style="color:#DBD7CA;">main</span><span style="color:#858585;">)</span><span style="color:#DBD7CA;">:001:</span><span style="color:#CB7676;">0&gt;</span><span style="color:#DBD7CA;"> Notify.test_email</span><span style="color:#858585;">(</span><span style="color:#C98A7D;">&#39;destination_email@address.com&#39;</span><span style="color:#DBD7CA;">, </span><span style="color:#C98A7D;">&#39;Message Subject&#39;</span><span style="color:#DBD7CA;">, </span><span style="color:#C98A7D;">&#39;Message Body&#39;</span><span style="color:#858585;">)</span><span style="color:#DBD7CA;">.deliver_now</span></span>
<span class="line"></span>
<span class="line"><span style="color:#758575;"># press enter to test SMTP sending</span></span>
<span class="line"></span></code></pre><pre class="vp-code-light"><code><span class="line"><span style="color:#8E8F8B;">[</span><span style="color:#393A34;">root@aliyun </span><span style="color:#AB5959;">~</span><span style="color:#8E8F8B;">]</span><span style="color:#A65E2B;">\\#</span><span style="color:#393A34;"> gitlab-rails console</span></span>
<span class="line"><span style="color:#393A34;">--------------------------------------------------------------------------------</span></span>
<span class="line"><span style="color:#393A34;"> GitLab:       13.1.4 </span><span style="color:#8E8F8B;">(</span><span style="color:#393A34;">18c5ab32b73</span><span style="color:#8E8F8B;">)</span><span style="color:#393A34;"> FOSS</span></span>
<span class="line"><span style="color:#393A34;"> GitLab Shell: 13.3.0</span></span>
<span class="line"><span style="color:#393A34;"> PostgreSQL:   11.7</span></span>
<span class="line"><span style="color:#393A34;">--------------------------------------------------------------------------------</span></span>
<span class="line"><span style="color:#393A34;">Loading production environment </span><span style="color:#8E8F8B;">(</span><span style="color:#393A34;">Rails 6.0.3.1</span><span style="color:#8E8F8B;">)</span></span>
<span class="line"><span style="color:#393A34;">irb</span><span style="color:#8E8F8B;">(</span><span style="color:#393A34;">main</span><span style="color:#8E8F8B;">)</span><span style="color:#393A34;">:001:</span><span style="color:#AB5959;">0&gt;</span><span style="color:#393A34;"> Notify.test_email</span><span style="color:#8E8F8B;">(</span><span style="color:#B56959;">&#39;destination_email@address.com&#39;</span><span style="color:#393A34;">, </span><span style="color:#B56959;">&#39;Message Subject&#39;</span><span style="color:#393A34;">, </span><span style="color:#B56959;">&#39;Message Body&#39;</span><span style="color:#8E8F8B;">)</span><span style="color:#393A34;">.deliver_now</span></span>
<span class="line"></span>
<span class="line"><span style="color:#A0ADA0;"># press enter to test SMTP sending</span></span>
<span class="line"></span></code></pre></div><h2 id="\u7248\u672C\u5347\u7EA7" tabindex="-1">\u7248\u672C\u5347\u7EA7 <a class="header-anchor" href="#\u7248\u672C\u5347\u7EA7" aria-hidden="true">#</a></h2><blockquote><p>\u5EFA\u8BAE\u5347\u7EA7\u524D\u6309\u7167\u94B1\u6570\u5907\u4EFD\u65B9\u5F0F\uFF0C\u5148\u5BF9GitLab\u6570\u636E\u8FDB\u884C\u5907\u4EFD\uFF0C\u540C\u65F6\u5907\u4EFDGitLab\u914D\u7F6E\u6587\u4EF6<code>/etc/gitlab/gitlab.rb</code>,\u7136\u540E\u518D\u6267\u884C\u4E0B\u5217\u6B65\u9AA4\u3002</p></blockquote><ol><li>\u5173\u95ED\u90E8\u5206GitLab\u670D\u52A1</li></ol><div class="language-"><span class="copy"></span><pre class="vp-code-dark"><code><span class="line"><span style="color:#dbd7ca;">gitlab-ctl stop unicorn</span></span>
<span class="line"><span style="color:#dbd7ca;">gitlab-ctl stop sidekiq</span></span>
<span class="line"><span style="color:#dbd7ca;">gitlab-ctl stop nginx</span></span>
<span class="line"><span style="color:#dbd7ca;"></span></span></code></pre><pre class="vp-code-light"><code><span class="line"><span style="color:#393a34;">gitlab-ctl stop unicorn</span></span>
<span class="line"><span style="color:#393a34;">gitlab-ctl stop sidekiq</span></span>
<span class="line"><span style="color:#393a34;">gitlab-ctl stop nginx</span></span>
<span class="line"><span style="color:#393a34;"></span></span></code></pre></div><ol start="2"><li>\u5347\u7EA7GitLab</li></ol><p>GitLab \u5347\u7EA7\u5305\u4E0B\u8F7D\u5730\u5740\uFF1A<code>https://packages.gitlab.com/gitlab/gitlab-ce</code>\u3002\u4E0B\u8F7D\u597D\u9700\u8981\u7684\u7248\u672C\u4E0A\u4F20\u81F3\u670D\u52A1\u5668\uFF0C\u76F4\u63A5\u8FD0\u884C\u4E0B\u5217\u547D\u4EE4\u5B89\u88C5\u5C31\u53EF\u4EE5\u4E86\u3002</p><div class="language-"><span class="copy"></span><pre class="vp-code-dark"><code><span class="line"><span style="color:#dbd7ca;">rpm -Uvh gitlab-ce-10.0.4-ce.0.el7.x86_64.rpm</span></span>
<span class="line"><span style="color:#dbd7ca;"></span></span></code></pre><pre class="vp-code-light"><code><span class="line"><span style="color:#393a34;">rpm -Uvh gitlab-ce-10.0.4-ce.0.el7.x86_64.rpm</span></span>
<span class="line"><span style="color:#393a34;"></span></span></code></pre></div><ol start="3"><li>\u91CD\u65B0\u914D\u7F6EGitLab</li></ol><p>\u5B89\u88C5\u5B8C\u6210\u540E\uFF0C\u6839\u636E\u9700\u8981\u4FEE\u6539\u914D\u7F6E\u6587\u4EF6<code>/etc/gitlab/gitlab.rb</code>\uFF0C\u4E5F\u53EF\u4EE5\u76F4\u63A5\u4F7F\u7528\u539F\u6765\u5907\u4EFD\u597D\u7684<code>gitlab.rb</code>\u6587\u4EF6\uFF0C\u6267\u884C\u91CD\u65B0\u914D\u7F6E\u547D\u4EE4\uFF1A</p><div class="language-"><span class="copy"></span><pre class="vp-code-dark"><code><span class="line"><span style="color:#dbd7ca;">gitlab-ctl reconfigure</span></span>
<span class="line"><span style="color:#dbd7ca;"></span></span></code></pre><pre class="vp-code-light"><code><span class="line"><span style="color:#393a34;">gitlab-ctl reconfigure</span></span>
<span class="line"><span style="color:#393a34;"></span></span></code></pre></div><ol start="4"><li>\u91CD\u542FGitLab</li></ol><div class="language-"><span class="copy"></span><pre class="vp-code-dark"><code><span class="line"><span style="color:#dbd7ca;">gitlab-ctl restart</span></span>
<span class="line"><span style="color:#dbd7ca;"></span></span></code></pre><pre class="vp-code-light"><code><span class="line"><span style="color:#393a34;">gitlab-ctl restart</span></span>
<span class="line"><span style="color:#393a34;"></span></span></code></pre></div><h2 id="\u53C2\u8003\u94FE\u63A5" tabindex="-1">\u53C2\u8003\u94FE\u63A5 <a class="header-anchor" href="#\u53C2\u8003\u94FE\u63A5" aria-hidden="true">#</a></h2><ul><li>GitLab installation: <a href="https://docs.gitlab.com/ee/install/" target="_blank" rel="noopener noreferrer">https://docs.gitlab.com/ee/install/</a></li><li>GitLab SMTP Settings: <a href="https://docs.gitlab.com/omnibus/settings/smtp.html#smtp-settings" target="_blank" rel="noopener noreferrer">https://docs.gitlab.com/omnibus/settings/smtp.html#smtp-settings</a></li></ul>`,56),o=[e];function t(c,r,i,y,d,g){return a(),n("div",null,o)}var h=s(p,[["render",t]]);export{A as __pageData,h as default};
