import{_ as t,o as e,c as l,a as d}from"./app.1e6146c3.js";const c=JSON.parse('{"title":"MySQL \u51FD\u6570","description":"","frontmatter":{},"headers":[{"level":2,"title":"MySQL \u5E38\u7528\u51FD\u6570","slug":"mysql-\u5E38\u7528\u51FD\u6570"},{"level":2,"title":"MySQL \u5B57\u7B26\u4E32\u51FD\u6570","slug":"mysql-\u5B57\u7B26\u4E32\u51FD\u6570"},{"level":2,"title":"MySQL \u6570\u5B57\u51FD\u6570","slug":"mysql-\u6570\u5B57\u51FD\u6570"},{"level":2,"title":"MySQL \u65E5\u671F\u51FD\u6570","slug":"mysql-\u65E5\u671F\u51FD\u6570"},{"level":2,"title":"MySQL \u52A0\u5BC6\u51FD\u6570","slug":"mysql-\u52A0\u5BC6\u51FD\u6570"},{"level":2,"title":"MySQL \u9AD8\u7EA7\u51FD\u6570","slug":"mysql-\u9AD8\u7EA7\u51FD\u6570"}],"relativePath":"LinuxBasics/DataBases/MySQL/MySQL Internal_function.md","lastUpdated":1657272051000}'),s={name:"LinuxBasics/DataBases/MySQL/MySQL Internal_function.md"},n=d('<blockquote><p>\u90E8\u5206\u5185\u5BB9\u6765\u6E90\uFF1A</p><ul><li><p><a href="https://www.runoob.com/mysql/mysql-functions.html" target="_blank" rel="noopener noreferrer">https://www.runoob.com/mysql/mysql-functions.html</a></p></li><li><p><a href="https://www.cnblogs.com/kissdodog/p/4168721.html" target="_blank" rel="noopener noreferrer">https://www.cnblogs.com/kissdodog/p/4168721.html</a></p></li></ul></blockquote><h1 id="mysql-\u51FD\u6570" tabindex="-1">MySQL \u51FD\u6570 <a class="header-anchor" href="#mysql-\u51FD\u6570" aria-hidden="true">#</a></h1><p>MySQL \u6709\u5F88\u591A\u5185\u7F6E\u7684\u51FD\u6570\uFF0C\u4EE5\u4E0B\u5217\u51FA\u4E86\u8FD9\u4E9B\u51FD\u6570\u7684\u8BF4\u660E\u3002</p><hr><h2 id="mysql-\u5E38\u7528\u51FD\u6570" tabindex="-1">MySQL \u5E38\u7528\u51FD\u6570 <a class="header-anchor" href="#mysql-\u5E38\u7528\u51FD\u6570" aria-hidden="true">#</a></h2><table><thead><tr><th>\u51FD\u6570</th><th>\u63CF\u8FF0</th><th>\u5B9E\u4F8B</th></tr></thead><tbody><tr><td>JSON_SET</td><td>\u4FEE\u6539JSON\u67D0\u4E2A\u5B57\u6BB5</td><td>UPDATE <code>tb_saas_contract</code> SET DATA = json_set( DATA, &quot;$.text_18&quot;, &quot;\u5F20\u4E09&quot; )</td></tr><tr><td></td><td></td><td></td></tr><tr><td></td><td></td><td></td></tr></tbody></table><hr><h2 id="mysql-\u5B57\u7B26\u4E32\u51FD\u6570" tabindex="-1">MySQL \u5B57\u7B26\u4E32\u51FD\u6570 <a class="header-anchor" href="#mysql-\u5B57\u7B26\u4E32\u51FD\u6570" aria-hidden="true">#</a></h2><table><thead><tr><th style="text-align:left;">\u51FD\u6570</th><th style="text-align:left;">\u63CF\u8FF0</th><th style="text-align:left;">\u5B9E\u4F8B</th></tr></thead><tbody><tr><td style="text-align:left;">ASCII(s)</td><td style="text-align:left;">\u8FD4\u56DE\u5B57\u7B26\u4E32 s \u7684\u7B2C\u4E00\u4E2A\u5B57\u7B26\u7684 ASCII \u7801\u3002</td><td style="text-align:left;">\u8FD4\u56DE CustomerName \u5B57\u6BB5\u7B2C\u4E00\u4E2A\u5B57\u6BCD\u7684 ASCII \u7801\uFF1A<code>SELECT ASCII(CustomerName) AS NumCodeOfFirstChar FROM Customers;</code></td></tr><tr><td style="text-align:left;">CHAR_LENGTH(s)</td><td style="text-align:left;">\u8FD4\u56DE\u5B57\u7B26\u4E32 s \u7684\u5B57\u7B26\u6570</td><td style="text-align:left;">\u8FD4\u56DE\u5B57\u7B26\u4E32 RUNOOB \u7684\u5B57\u7B26\u6570<code>SELECT CHAR_LENGTH(&quot;RUNOOB&quot;) AS LengthOfString;</code></td></tr><tr><td style="text-align:left;">CHARACTER_LENGTH(s)</td><td style="text-align:left;">\u8FD4\u56DE\u5B57\u7B26\u4E32 s \u7684\u5B57\u7B26\u6570</td><td style="text-align:left;">\u8FD4\u56DE\u5B57\u7B26\u4E32 RUNOOB \u7684\u5B57\u7B26\u6570<code>SELECT CHARACTER_LENGTH(&quot;RUNOOB&quot;) AS LengthOfString;</code></td></tr><tr><td style="text-align:left;">CONCAT(s1,s2...sn)</td><td style="text-align:left;">\u5B57\u7B26\u4E32 s1,s2 \u7B49\u591A\u4E2A\u5B57\u7B26\u4E32\u5408\u5E76\u4E3A\u4E00\u4E2A\u5B57\u7B26\u4E32</td><td style="text-align:left;">\u5408\u5E76\u591A\u4E2A\u5B57\u7B26\u4E32<code>SELECT CONCAT(&quot;SQL &quot;, &quot;Runoob &quot;, &quot;Gooogle &quot;, &quot;Facebook&quot;) AS ConcatenatedString;</code></td></tr><tr><td style="text-align:left;">CONCAT_WS(x, s1,s2...sn)</td><td style="text-align:left;">\u540C CONCAT(s1,s2,...) \u51FD\u6570\uFF0C\u4F46\u662F\u6BCF\u4E2A\u5B57\u7B26\u4E32\u4E4B\u95F4\u8981\u52A0\u4E0A x\uFF0Cx \u53EF\u4EE5\u662F\u5206\u9694\u7B26</td><td style="text-align:left;">\u5408\u5E76\u591A\u4E2A\u5B57\u7B26\u4E32\uFF0C\u5E76\u6DFB\u52A0\u5206\u9694\u7B26\uFF1A<code>SELECT CONCAT_WS(&quot;-&quot;, &quot;SQL&quot;, &quot;Tutorial&quot;, &quot;is&quot;, &quot;fun!&quot;)AS ConcatenatedString;</code></td></tr><tr><td style="text-align:left;">FIELD(s,s1,s2...)</td><td style="text-align:left;">\u8FD4\u56DE\u7B2C\u4E00\u4E2A\u5B57\u7B26\u4E32 s \u5728\u5B57\u7B26\u4E32\u5217\u8868(s1,s2...)\u4E2D\u7684\u4F4D\u7F6E</td><td style="text-align:left;">\u8FD4\u56DE\u5B57\u7B26\u4E32 c \u5728\u5217\u8868\u503C\u4E2D\u7684\u4F4D\u7F6E\uFF1A<code>SELECT FIELD(&quot;c&quot;, &quot;a&quot;, &quot;b&quot;, &quot;c&quot;, &quot;d&quot;, &quot;e&quot;);</code></td></tr><tr><td style="text-align:left;">FIND_IN_SET(s1,s2)</td><td style="text-align:left;">\u8FD4\u56DE\u5728\u5B57\u7B26\u4E32s2\u4E2D\u4E0Es1\u5339\u914D\u7684\u5B57\u7B26\u4E32\u7684\u4F4D\u7F6E</td><td style="text-align:left;">\u8FD4\u56DE\u5B57\u7B26\u4E32 c \u5728\u6307\u5B9A\u5B57\u7B26\u4E32\u4E2D\u7684\u4F4D\u7F6E\uFF1A<code>SELECT FIND_IN_SET(&quot;c&quot;, &quot;a,b,c,d,e&quot;);</code></td></tr><tr><td style="text-align:left;">FORMAT(x,n)</td><td style="text-align:left;">\u51FD\u6570\u53EF\u4EE5\u5C06\u6570\u5B57 x \u8FDB\u884C\u683C\u5F0F\u5316 &quot;#,###.##&quot;, \u5C06 x \u4FDD\u7559\u5230\u5C0F\u6570\u70B9\u540E n \u4F4D\uFF0C\u6700\u540E\u4E00\u4F4D\u56DB\u820D\u4E94\u5165\u3002</td><td style="text-align:left;">\u683C\u5F0F\u5316\u6570\u5B57 &quot;#,###.##&quot; \u5F62\u5F0F\uFF1A<code>SELECT FORMAT(250500.5634, 2); -- \u8F93\u51FA 250,500.56</code></td></tr><tr><td style="text-align:left;">INSERT(s1,x,len,s2)</td><td style="text-align:left;">\u5B57\u7B26\u4E32 s2 \u66FF\u6362 s1 \u7684 x \u4F4D\u7F6E\u5F00\u59CB\u957F\u5EA6\u4E3A len \u7684\u5B57\u7B26\u4E32</td><td style="text-align:left;">\u4ECE\u5B57\u7B26\u4E32\u7B2C\u4E00\u4E2A\u4F4D\u7F6E\u5F00\u59CB\u7684 6 \u4E2A\u5B57\u7B26\u66FF\u6362\u4E3A runoob\uFF1A<code>SELECT INSERT(&quot;google.com&quot;, 1, 6, &quot;runoob&quot;); -- \u8F93\u51FA\uFF1Arunoob.com</code></td></tr><tr><td style="text-align:left;">LOCATE(s1,s)</td><td style="text-align:left;">\u4ECE\u5B57\u7B26\u4E32 s \u4E2D\u83B7\u53D6 s1 \u7684\u5F00\u59CB\u4F4D\u7F6E</td><td style="text-align:left;">\u83B7\u53D6 b \u5728\u5B57\u7B26\u4E32 abc \u4E2D\u7684\u4F4D\u7F6E\uFF1A<code>SELECT LOCATE(&#39;st&#39;,&#39;myteststring&#39;); -- 5</code>\u8FD4\u56DE\u5B57\u7B26\u4E32 abc \u4E2D b \u7684\u4F4D\u7F6E\uFF1A<code>SELECT LOCATE(&#39;b&#39;, &#39;abc&#39;) -- 2</code></td></tr><tr><td style="text-align:left;">LCASE(s)</td><td style="text-align:left;">\u5C06\u5B57\u7B26\u4E32 s \u7684\u6240\u6709\u5B57\u6BCD\u53D8\u6210\u5C0F\u5199\u5B57\u6BCD</td><td style="text-align:left;">\u5B57\u7B26\u4E32 RUNOOB \u8F6C\u6362\u4E3A\u5C0F\u5199\uFF1A<code>SELECT LCASE(&#39;RUNOOB&#39;) -- runoob</code></td></tr><tr><td style="text-align:left;">LEFT(s,n)</td><td style="text-align:left;">\u8FD4\u56DE\u5B57\u7B26\u4E32 s \u7684\u524D n \u4E2A\u5B57\u7B26</td><td style="text-align:left;">\u8FD4\u56DE\u5B57\u7B26\u4E32 runoob \u4E2D\u7684\u524D\u4E24\u4E2A\u5B57\u7B26\uFF1A<code>SELECT LEFT(&#39;runoob&#39;,2) -- ru</code></td></tr><tr><td style="text-align:left;">LOWER(s)</td><td style="text-align:left;">\u5C06\u5B57\u7B26\u4E32 s \u7684\u6240\u6709\u5B57\u6BCD\u53D8\u6210\u5C0F\u5199\u5B57\u6BCD</td><td style="text-align:left;">\u5B57\u7B26\u4E32 RUNOOB \u8F6C\u6362\u4E3A\u5C0F\u5199\uFF1A<code>SELECT LOWER(&#39;RUNOOB&#39;) -- runoob</code></td></tr><tr><td style="text-align:left;">LPAD(s1,len,s2)</td><td style="text-align:left;">\u5728\u5B57\u7B26\u4E32 s1 \u7684\u5F00\u59CB\u5904\u586B\u5145\u5B57\u7B26\u4E32 s2\uFF0C\u4F7F\u5B57\u7B26\u4E32\u957F\u5EA6\u8FBE\u5230 len</td><td style="text-align:left;">\u5C06\u5B57\u7B26\u4E32 xx \u586B\u5145\u5230 abc \u5B57\u7B26\u4E32\u7684\u5F00\u59CB\u5904\uFF1A<code>SELECT LPAD(&#39;abc&#39;,5,&#39;xx&#39;) -- xxabc</code></td></tr><tr><td style="text-align:left;">LTRIM(s)</td><td style="text-align:left;">\u53BB\u6389\u5B57\u7B26\u4E32 s \u5F00\u59CB\u5904\u7684\u7A7A\u683C</td><td style="text-align:left;">\u53BB\u6389\u5B57\u7B26\u4E32 RUNOOB\u5F00\u59CB\u5904\u7684\u7A7A\u683C\uFF1A<code>SELECT LTRIM(&quot; RUNOOB&quot;) AS LeftTrimmedString;-- RUNOOB</code></td></tr><tr><td style="text-align:left;">MID(s,n,len)</td><td style="text-align:left;">\u4ECE\u5B57\u7B26\u4E32 s \u7684 n \u4F4D\u7F6E\u622A\u53D6\u957F\u5EA6\u4E3A len \u7684\u5B50\u5B57\u7B26\u4E32\uFF0C\u540C SUBSTRING(s,n,len)</td><td style="text-align:left;">\u4ECE\u5B57\u7B26\u4E32 RUNOOB \u4E2D\u7684\u7B2C 2 \u4E2A\u4F4D\u7F6E\u622A\u53D6 3\u4E2A \u5B57\u7B26\uFF1A<code>SELECT MID(&quot;RUNOOB&quot;, 2, 3) AS ExtractString; -- UNO</code></td></tr><tr><td style="text-align:left;">POSITION(s1 IN s)</td><td style="text-align:left;">\u4ECE\u5B57\u7B26\u4E32 s \u4E2D\u83B7\u53D6 s1 \u7684\u5F00\u59CB\u4F4D\u7F6E</td><td style="text-align:left;">\u8FD4\u56DE\u5B57\u7B26\u4E32 abc \u4E2D b \u7684\u4F4D\u7F6E\uFF1A<code>SELECT POSITION(&#39;b&#39; in &#39;abc&#39;) -- 2</code></td></tr><tr><td style="text-align:left;">REPEAT(s,n)</td><td style="text-align:left;">\u5C06\u5B57\u7B26\u4E32 s \u91CD\u590D n \u6B21</td><td style="text-align:left;">\u5C06\u5B57\u7B26\u4E32 runoob \u91CD\u590D\u4E09\u6B21\uFF1A<code>SELECT REPEAT(&#39;runoob&#39;,3) -- runoobrunoobrunoob</code></td></tr><tr><td style="text-align:left;">REPLACE(s,s1,s2)</td><td style="text-align:left;">\u5C06\u5B57\u7B26\u4E32 s2 \u66FF\u4EE3\u5B57\u7B26\u4E32 s \u4E2D\u7684\u5B57\u7B26\u4E32 s1</td><td style="text-align:left;">\u5C06\u5B57\u7B26\u4E32 abc \u4E2D\u7684\u5B57\u7B26 a \u66FF\u6362\u4E3A\u5B57\u7B26 x\uFF1A<code>SELECT REPLACE(&#39;abc&#39;,&#39;a&#39;,&#39;x&#39;) --xbc</code></td></tr><tr><td style="text-align:left;">REVERSE(s)</td><td style="text-align:left;">\u5C06\u5B57\u7B26\u4E32s\u7684\u987A\u5E8F\u53CD\u8FC7\u6765</td><td style="text-align:left;">\u5C06\u5B57\u7B26\u4E32 abc \u7684\u987A\u5E8F\u53CD\u8FC7\u6765\uFF1A<code>SELECT REVERSE(&#39;abc&#39;) -- cba</code></td></tr><tr><td style="text-align:left;">RIGHT(s,n)</td><td style="text-align:left;">\u8FD4\u56DE\u5B57\u7B26\u4E32 s \u7684\u540E n \u4E2A\u5B57\u7B26</td><td style="text-align:left;">\u8FD4\u56DE\u5B57\u7B26\u4E32 runoob \u7684\u540E\u4E24\u4E2A\u5B57\u7B26\uFF1A<code>SELECT RIGHT(&#39;runoob&#39;,2) -- ob</code></td></tr><tr><td style="text-align:left;">RPAD(s1,len,s2)</td><td style="text-align:left;">\u5728\u5B57\u7B26\u4E32 s1 \u7684\u7ED3\u5C3E\u5904\u6DFB\u52A0\u5B57\u7B26\u4E32 s2\uFF0C\u4F7F\u5B57\u7B26\u4E32\u7684\u957F\u5EA6\u8FBE\u5230 len</td><td style="text-align:left;">\u5C06\u5B57\u7B26\u4E32 xx \u586B\u5145\u5230 abc \u5B57\u7B26\u4E32\u7684\u7ED3\u5C3E\u5904\uFF1A<code>SELECT RPAD(&#39;abc&#39;,5,&#39;xx&#39;) -- abcxx</code></td></tr><tr><td style="text-align:left;">RTRIM(s)</td><td style="text-align:left;">\u53BB\u6389\u5B57\u7B26\u4E32 s \u7ED3\u5C3E\u5904\u7684\u7A7A\u683C</td><td style="text-align:left;">\u53BB\u6389\u5B57\u7B26\u4E32 RUNOOB \u7684\u672B\u5C3E\u7A7A\u683C\uFF1A<code>SELECT RTRIM(&quot;RUNOOB &quot;) AS RightTrimmedString; -- RUNOOB</code></td></tr><tr><td style="text-align:left;">SPACE(n)</td><td style="text-align:left;">\u8FD4\u56DE n \u4E2A\u7A7A\u683C</td><td style="text-align:left;">\u8FD4\u56DE 10 \u4E2A\u7A7A\u683C\uFF1A<code>SELECT SPACE(10);</code></td></tr><tr><td style="text-align:left;">STRCMP(s1,s2)</td><td style="text-align:left;">\u6BD4\u8F83\u5B57\u7B26\u4E32 s1 \u548C s2\uFF0C\u5982\u679C s1 \u4E0E s2 \u76F8\u7B49\u8FD4\u56DE 0 \uFF0C\u5982\u679C s1&gt;s2 \u8FD4\u56DE 1\uFF0C\u5982\u679C s1&lt;s2 \u8FD4\u56DE -1</td><td style="text-align:left;">\u6BD4\u8F83\u5B57\u7B26\u4E32\uFF1A<code>SELECT STRCMP(&quot;runoob&quot;, &quot;runoob&quot;); -- 0</code></td></tr><tr><td style="text-align:left;">SUBSTR(s, start, length)</td><td style="text-align:left;">\u4ECE\u5B57\u7B26\u4E32 s \u7684 start \u4F4D\u7F6E\u622A\u53D6\u957F\u5EA6\u4E3A length \u7684\u5B50\u5B57\u7B26\u4E32</td><td style="text-align:left;">\u4ECE\u5B57\u7B26\u4E32 RUNOOB \u4E2D\u7684\u7B2C 2 \u4E2A\u4F4D\u7F6E\u622A\u53D6 3\u4E2A \u5B57\u7B26\uFF1A<code>SELECT SUBSTR(&quot;RUNOOB&quot;, 2, 3) AS ExtractString; -- UNO</code></td></tr><tr><td style="text-align:left;">SUBSTRING(s, start, length)</td><td style="text-align:left;">\u4ECE\u5B57\u7B26\u4E32 s \u7684 start \u4F4D\u7F6E\u622A\u53D6\u957F\u5EA6\u4E3A length \u7684\u5B50\u5B57\u7B26\u4E32</td><td style="text-align:left;">\u4ECE\u5B57\u7B26\u4E32 RUNOOB \u4E2D\u7684\u7B2C 2 \u4E2A\u4F4D\u7F6E\u622A\u53D6 3\u4E2A \u5B57\u7B26\uFF1A<code>SELECT SUBSTRING(&quot;RUNOOB&quot;, 2, 3) AS ExtractString; -- UNO</code></td></tr><tr><td style="text-align:left;">SUBSTRING_INDEX(s, delimiter, number)</td><td style="text-align:left;">\u8FD4\u56DE\u4ECE\u5B57\u7B26\u4E32 s \u7684\u7B2C number \u4E2A\u51FA\u73B0\u7684\u5206\u9694\u7B26 delimiter \u4E4B\u540E\u7684\u5B50\u4E32\u3002 \u5982\u679C number \u662F\u6B63\u6570\uFF0C\u8FD4\u56DE\u7B2C number \u4E2A\u5B57\u7B26\u5DE6\u8FB9\u7684\u5B57\u7B26\u4E32\u3002 \u5982\u679C number \u662F\u8D1F\u6570\uFF0C\u8FD4\u56DE\u7B2C(number \u7684\u7EDD\u5BF9\u503C(\u4ECE\u53F3\u8FB9\u6570))\u4E2A\u5B57\u7B26\u53F3\u8FB9\u7684\u5B57\u7B26\u4E32\u3002</td><td style="text-align:left;"><code>SELECT SUBSTRING_INDEX(&#39;a*b&#39;,&#39;*&#39;,1) -- a SELECT SUBSTRING_INDEX(&#39;a*b&#39;,&#39;*&#39;,-1) -- b SELECT SUBSTRING_INDEX(SUBSTRING_INDEX(&#39;a*b*c*d*e&#39;,&#39;*&#39;,3),&#39;*&#39;,-1) -- c</code></td></tr><tr><td style="text-align:left;">TRIM(s)</td><td style="text-align:left;">\u53BB\u6389\u5B57\u7B26\u4E32 s \u5F00\u59CB\u548C\u7ED3\u5C3E\u5904\u7684\u7A7A\u683C</td><td style="text-align:left;">\u53BB\u6389\u5B57\u7B26\u4E32 RUNOOB \u7684\u9996\u5C3E\u7A7A\u683C\uFF1A<code>SELECT TRIM(&#39; RUNOOB &#39;) AS TrimmedString;</code></td></tr><tr><td style="text-align:left;">UCASE(s)</td><td style="text-align:left;">\u5C06\u5B57\u7B26\u4E32\u8F6C\u6362\u4E3A\u5927\u5199</td><td style="text-align:left;">\u5C06\u5B57\u7B26\u4E32 runoob \u8F6C\u6362\u4E3A\u5927\u5199\uFF1A<code>SELECT UCASE(&quot;runoob&quot;); -- RUNOOB</code></td></tr><tr><td style="text-align:left;">UPPER(s)</td><td style="text-align:left;">\u5C06\u5B57\u7B26\u4E32\u8F6C\u6362\u4E3A\u5927\u5199</td><td style="text-align:left;">\u5C06\u5B57\u7B26\u4E32 runoob \u8F6C\u6362\u4E3A\u5927\u5199\uFF1A<code>SELECT UPPER(&quot;runoob&quot;); -- RUNOOB</code></td></tr></tbody></table><hr><h2 id="mysql-\u6570\u5B57\u51FD\u6570" tabindex="-1">MySQL \u6570\u5B57\u51FD\u6570 <a class="header-anchor" href="#mysql-\u6570\u5B57\u51FD\u6570" aria-hidden="true">#</a></h2><table><thead><tr><th style="text-align:left;">\u51FD\u6570\u540D</th><th style="text-align:left;">\u63CF\u8FF0</th><th style="text-align:left;">\u5B9E\u4F8B</th></tr></thead><tbody><tr><td style="text-align:left;">ABS(x)</td><td style="text-align:left;">\u8FD4\u56DE x \u7684\u7EDD\u5BF9\u503C</td><td style="text-align:left;">\u8FD4\u56DE -1 \u7684\u7EDD\u5BF9\u503C\uFF1A<code>SELECT ABS(-1) -- \u8FD4\u56DE1</code></td></tr><tr><td style="text-align:left;">ACOS(x)</td><td style="text-align:left;">\u6C42 x \u7684\u53CD\u4F59\u5F26\u503C(\u53C2\u6570\u662F\u5F27\u5EA6)</td><td style="text-align:left;"><code>SELECT ACOS(0.25);</code></td></tr><tr><td style="text-align:left;">ASIN(x)</td><td style="text-align:left;">\u6C42\u53CD\u6B63\u5F26\u503C(\u53C2\u6570\u662F\u5F27\u5EA6)</td><td style="text-align:left;"><code>SELECT ASIN(0.25);</code></td></tr><tr><td style="text-align:left;">ATAN(x)</td><td style="text-align:left;">\u6C42\u53CD\u6B63\u5207\u503C(\u53C2\u6570\u662F\u5F27\u5EA6)</td><td style="text-align:left;"><code>SELECT ATAN(2.5);</code></td></tr><tr><td style="text-align:left;">ATAN2(n, m)</td><td style="text-align:left;">\u6C42\u53CD\u6B63\u5207\u503C(\u53C2\u6570\u662F\u5F27\u5EA6)</td><td style="text-align:left;"><code>SELECT ATAN2(-0.8, 2);</code></td></tr><tr><td style="text-align:left;">AVG(expression)</td><td style="text-align:left;">\u8FD4\u56DE\u4E00\u4E2A\u8868\u8FBE\u5F0F\u7684\u5E73\u5747\u503C\uFF0Cexpression \u662F\u4E00\u4E2A\u5B57\u6BB5</td><td style="text-align:left;">\u8FD4\u56DE Products \u8868\u4E2DPrice \u5B57\u6BB5\u7684\u5E73\u5747\u503C\uFF1A<code>SELECT AVG(Price) AS AveragePrice FROM Products;</code></td></tr><tr><td style="text-align:left;">CEIL(x)</td><td style="text-align:left;">\u8FD4\u56DE\u5927\u4E8E\u6216\u7B49\u4E8E x \u7684\u6700\u5C0F\u6574\u6570</td><td style="text-align:left;"><code>SELECT CEIL(1.5) -- \u8FD4\u56DE2</code></td></tr><tr><td style="text-align:left;">CEILING(x)</td><td style="text-align:left;">\u8FD4\u56DE\u5927\u4E8E\u6216\u7B49\u4E8E x \u7684\u6700\u5C0F\u6574\u6570</td><td style="text-align:left;"><code>SELECT CEILING(1.5); -- \u8FD4\u56DE2</code></td></tr><tr><td style="text-align:left;">COS(x)</td><td style="text-align:left;">\u6C42\u4F59\u5F26\u503C(\u53C2\u6570\u662F\u5F27\u5EA6)</td><td style="text-align:left;"><code>SELECT COS(2);</code></td></tr><tr><td style="text-align:left;">COT(x)</td><td style="text-align:left;">\u6C42\u4F59\u5207\u503C(\u53C2\u6570\u662F\u5F27\u5EA6)</td><td style="text-align:left;"><code>SELECT COT(6);</code></td></tr><tr><td style="text-align:left;">COUNT(expression)</td><td style="text-align:left;">\u8FD4\u56DE\u67E5\u8BE2\u7684\u8BB0\u5F55\u603B\u6570\uFF0Cexpression \u53C2\u6570\u662F\u4E00\u4E2A\u5B57\u6BB5\u6216\u8005 * \u53F7</td><td style="text-align:left;">\u8FD4\u56DE Products \u8868\u4E2D products \u5B57\u6BB5\u603B\u5171\u6709\u591A\u5C11\u6761\u8BB0\u5F55\uFF1A<code>SELECT COUNT(ProductID) AS NumberOfProducts FROM Products;</code></td></tr><tr><td style="text-align:left;">DEGREES(x)</td><td style="text-align:left;">\u5C06\u5F27\u5EA6\u8F6C\u6362\u4E3A\u89D2\u5EA6</td><td style="text-align:left;"><code>SELECT DEGREES(3.1415926535898) -- 180</code></td></tr><tr><td style="text-align:left;">n DIV m</td><td style="text-align:left;">\u6574\u9664\uFF0Cn \u4E3A\u88AB\u9664\u6570\uFF0Cm \u4E3A\u9664\u6570</td><td style="text-align:left;">\u8BA1\u7B97 10 \u9664\u4E8E 5\uFF1A<code>SELECT 10 DIV 5; -- 2</code></td></tr><tr><td style="text-align:left;">EXP(x)</td><td style="text-align:left;">\u8FD4\u56DE e \u7684 x \u6B21\u65B9</td><td style="text-align:left;">\u8BA1\u7B97 e \u7684\u4E09\u6B21\u65B9\uFF1A<code>SELECT EXP(3) -- 20.085536923188</code></td></tr><tr><td style="text-align:left;">FLOOR(x)</td><td style="text-align:left;">\u8FD4\u56DE\u5C0F\u4E8E\u6216\u7B49\u4E8E x \u7684\u6700\u5927\u6574\u6570</td><td style="text-align:left;">\u5C0F\u4E8E\u6216\u7B49\u4E8E 1.5 \u7684\u6574\u6570\uFF1A<code>SELECT FLOOR(1.5) -- \u8FD4\u56DE1</code></td></tr><tr><td style="text-align:left;">GREATEST(expr1, expr2, expr3, ...)</td><td style="text-align:left;">\u8FD4\u56DE\u5217\u8868\u4E2D\u7684\u6700\u5927\u503C</td><td style="text-align:left;">\u8FD4\u56DE\u4EE5\u4E0B\u6570\u5B57\u5217\u8868\u4E2D\u7684\u6700\u5927\u503C\uFF1A<code>SELECT GREATEST(3, 12, 34, 8, 25); -- 34</code>\u8FD4\u56DE\u4EE5\u4E0B\u5B57\u7B26\u4E32\u5217\u8868\u4E2D\u7684\u6700\u5927\u503C\uFF1A<code>SELECT GREATEST(&quot;Google&quot;, &quot;Runoob&quot;, &quot;Apple&quot;); -- Runoob</code></td></tr><tr><td style="text-align:left;">LEAST(expr1, expr2, expr3, ...)</td><td style="text-align:left;">\u8FD4\u56DE\u5217\u8868\u4E2D\u7684\u6700\u5C0F\u503C</td><td style="text-align:left;">\u8FD4\u56DE\u4EE5\u4E0B\u6570\u5B57\u5217\u8868\u4E2D\u7684\u6700\u5C0F\u503C\uFF1A<code>SELECT LEAST(3, 12, 34, 8, 25); -- 3</code>\u8FD4\u56DE\u4EE5\u4E0B\u5B57\u7B26\u4E32\u5217\u8868\u4E2D\u7684\u6700\u5C0F\u503C\uFF1A<code>SELECT LEAST(&quot;Google&quot;, &quot;Runoob&quot;, &quot;Apple&quot;); -- Apple</code></td></tr><tr><td style="text-align:left;">LN</td><td style="text-align:left;">\u8FD4\u56DE\u6570\u5B57\u7684\u81EA\u7136\u5BF9\u6570\uFF0C\u4EE5 e \u4E3A\u5E95\u3002</td><td style="text-align:left;">\u8FD4\u56DE 2 \u7684\u81EA\u7136\u5BF9\u6570\uFF1A<code>SELECT LN(2); -- 0.6931471805599453</code></td></tr><tr><td style="text-align:left;">LOG(x) \u6216 LOG(base, x)</td><td style="text-align:left;">\u8FD4\u56DE\u81EA\u7136\u5BF9\u6570(\u4EE5 e \u4E3A\u5E95\u7684\u5BF9\u6570)\uFF0C\u5982\u679C\u5E26\u6709 base \u53C2\u6570\uFF0C\u5219 base \u4E3A\u6307\u5B9A\u5E26\u5E95\u6570\u3002</td><td style="text-align:left;"><code>SELECT LOG(20.085536923188) -- 3 SELECT LOG(2, 4); -- 2</code></td></tr><tr><td style="text-align:left;">LOG10(x)</td><td style="text-align:left;">\u8FD4\u56DE\u4EE5 10 \u4E3A\u5E95\u7684\u5BF9\u6570</td><td style="text-align:left;"><code>SELECT LOG10(100) -- 2</code></td></tr><tr><td style="text-align:left;">LOG2(x)</td><td style="text-align:left;">\u8FD4\u56DE\u4EE5 2 \u4E3A\u5E95\u7684\u5BF9\u6570</td><td style="text-align:left;">\u8FD4\u56DE\u4EE5 2 \u4E3A\u5E95 6 \u7684\u5BF9\u6570\uFF1A<code>SELECT LOG2(6); -- 2.584962500721156</code></td></tr><tr><td style="text-align:left;">MAX(expression)</td><td style="text-align:left;">\u8FD4\u56DE\u5B57\u6BB5 expression \u4E2D\u7684\u6700\u5927\u503C</td><td style="text-align:left;">\u8FD4\u56DE\u6570\u636E\u8868 Products \u4E2D\u5B57\u6BB5 Price \u7684\u6700\u5927\u503C\uFF1A<code>SELECT MAX(Price) AS LargestPrice FROM Products;</code></td></tr><tr><td style="text-align:left;">MIN(expression)</td><td style="text-align:left;">\u8FD4\u56DE\u5B57\u6BB5 expression \u4E2D\u7684\u6700\u5C0F\u503C</td><td style="text-align:left;">\u8FD4\u56DE\u6570\u636E\u8868 Products \u4E2D\u5B57\u6BB5 Price \u7684\u6700\u5C0F\u503C\uFF1A<code>SELECT MIN(Price) AS MinPrice FROM Products;</code></td></tr><tr><td style="text-align:left;">MOD(x,y)</td><td style="text-align:left;">\u8FD4\u56DE x \u9664\u4EE5 y \u4EE5\u540E\u7684\u4F59\u6570</td><td style="text-align:left;">5 \u9664\u4E8E 2 \u7684\u4F59\u6570\uFF1A<code>SELECT MOD(5,2) -- 1</code></td></tr><tr><td style="text-align:left;">PI()</td><td style="text-align:left;">\u8FD4\u56DE\u5706\u5468\u7387(3.141593\uFF09</td><td style="text-align:left;"><code>SELECT PI() --3.141593</code></td></tr><tr><td style="text-align:left;">POW(x,y)</td><td style="text-align:left;">\u8FD4\u56DE x \u7684 y \u6B21\u65B9</td><td style="text-align:left;">2 \u7684 3 \u6B21\u65B9\uFF1A<code>SELECT POW(2,3) -- 8</code></td></tr><tr><td style="text-align:left;">POWER(x,y)</td><td style="text-align:left;">\u8FD4\u56DE x \u7684 y \u6B21\u65B9</td><td style="text-align:left;">2 \u7684 3 \u6B21\u65B9\uFF1A<code>SELECT POWER(2,3) -- 8</code></td></tr><tr><td style="text-align:left;">RADIANS(x)</td><td style="text-align:left;">\u5C06\u89D2\u5EA6\u8F6C\u6362\u4E3A\u5F27\u5EA6</td><td style="text-align:left;">180 \u5EA6\u8F6C\u6362\u4E3A\u5F27\u5EA6\uFF1A<code>SELECT RADIANS(180) -- 3.1415926535898</code></td></tr><tr><td style="text-align:left;">RAND()</td><td style="text-align:left;">\u8FD4\u56DE 0 \u5230 1 \u7684\u968F\u673A\u6570</td><td style="text-align:left;"><code>SELECT RAND() --0.93099315644334</code></td></tr><tr><td style="text-align:left;">ROUND(x)</td><td style="text-align:left;">\u8FD4\u56DE\u79BB x \u6700\u8FD1\u7684\u6574\u6570</td><td style="text-align:left;"><code>SELECT ROUND(1.23456) --1</code></td></tr><tr><td style="text-align:left;">SIGN(x)</td><td style="text-align:left;">\u8FD4\u56DE x \u7684\u7B26\u53F7\uFF0Cx \u662F\u8D1F\u6570\u30010\u3001\u6B63\u6570\u5206\u522B\u8FD4\u56DE -1\u30010 \u548C 1</td><td style="text-align:left;"><code>SELECT SIGN(-10) -- (-1)</code></td></tr><tr><td style="text-align:left;">SIN(x)</td><td style="text-align:left;">\u6C42\u6B63\u5F26\u503C(\u53C2\u6570\u662F\u5F27\u5EA6)</td><td style="text-align:left;"><code>SELECT SIN(RADIANS(30)) -- 0.5</code></td></tr><tr><td style="text-align:left;">SQRT(x)</td><td style="text-align:left;">\u8FD4\u56DEx\u7684\u5E73\u65B9\u6839</td><td style="text-align:left;">25 \u7684\u5E73\u65B9\u6839\uFF1A<code>SELECT SQRT(25) -- 5</code></td></tr><tr><td style="text-align:left;">SUM(expression)</td><td style="text-align:left;">\u8FD4\u56DE\u6307\u5B9A\u5B57\u6BB5\u7684\u603B\u548C</td><td style="text-align:left;">\u8BA1\u7B97 OrderDetails \u8868\u4E2D\u5B57\u6BB5 Quantity \u7684\u603B\u548C\uFF1A<code>SELECT SUM(Quantity) AS TotalItemsOrdered FROM OrderDetails;</code></td></tr><tr><td style="text-align:left;">TAN(x)</td><td style="text-align:left;">\u6C42\u6B63\u5207\u503C(\u53C2\u6570\u662F\u5F27\u5EA6)</td><td style="text-align:left;"><code>SELECT TAN(1.75); -- -5.52037992250933</code></td></tr><tr><td style="text-align:left;">TRUNCATE(x,y)</td><td style="text-align:left;">\u8FD4\u56DE\u6570\u503C x \u4FDD\u7559\u5230\u5C0F\u6570\u70B9\u540E y \u4F4D\u7684\u503C\uFF08\u4E0E ROUND \u6700\u5927\u7684\u533A\u522B\u662F\u4E0D\u4F1A\u8FDB\u884C\u56DB\u820D\u4E94\u5165\uFF09</td><td style="text-align:left;"><code>SELECT TRUNCATE(1.23456,3) -- 1.234</code></td></tr></tbody></table><hr><h2 id="mysql-\u65E5\u671F\u51FD\u6570" tabindex="-1">MySQL \u65E5\u671F\u51FD\u6570 <a class="header-anchor" href="#mysql-\u65E5\u671F\u51FD\u6570" aria-hidden="true">#</a></h2><table><thead><tr><th style="text-align:left;">\u51FD\u6570\u540D</th><th style="text-align:left;">\u63CF\u8FF0</th><th style="text-align:left;">\u5B9E\u4F8B</th></tr></thead><tbody><tr><td style="text-align:left;">ADDDATE(d,n)</td><td style="text-align:left;">\u8BA1\u7B97\u8D77\u59CB\u65E5\u671F d \u52A0\u4E0A n \u5929\u7684\u65E5\u671F</td><td style="text-align:left;"><code>SELECT ADDDATE(&quot;2017-06-15&quot;, INTERVAL 10 DAY); -&gt;2017-06-25</code></td></tr><tr><td style="text-align:left;">ADDTIME(t,n)</td><td style="text-align:left;">n \u662F\u4E00\u4E2A\u65F6\u95F4\u8868\u8FBE\u5F0F\uFF0C\u65F6\u95F4 t \u52A0\u4E0A\u65F6\u95F4\u8868\u8FBE\u5F0F n</td><td style="text-align:left;">\u52A0 5 \u79D2\uFF1A<code>SELECT ADDTIME(&#39;2011-11-11 11:11:11&#39;, 5); -&gt;2011-11-11 11:11:16 (\u79D2)</code>\u6DFB\u52A0 2 \u5C0F\u65F6, 10 \u5206\u949F, 5 \u79D2:<code>SELECT ADDTIME(&quot;2020-06-15 09:34:21&quot;, &quot;2:10:5&quot;); -&gt; 2020-06-15 11:44:26</code></td></tr><tr><td style="text-align:left;">CURDATE()</td><td style="text-align:left;">\u8FD4\u56DE\u5F53\u524D\u65E5\u671F</td><td style="text-align:left;"><code>SELECT CURDATE(); -&gt; 2018-09-19</code></td></tr><tr><td style="text-align:left;">CURRENT_DATE()</td><td style="text-align:left;">\u8FD4\u56DE\u5F53\u524D\u65E5\u671F</td><td style="text-align:left;"><code>SELECT CURRENT_DATE(); -&gt; 2018-09-19</code></td></tr><tr><td style="text-align:left;">CURRENT_TIME</td><td style="text-align:left;">\u8FD4\u56DE\u5F53\u524D\u65F6\u95F4</td><td style="text-align:left;"><code>SELECT CURRENT_TIME(); -&gt; 19:59:02</code></td></tr><tr><td style="text-align:left;">CURRENT_TIMESTAMP()</td><td style="text-align:left;">\u8FD4\u56DE\u5F53\u524D\u65E5\u671F\u548C\u65F6\u95F4</td><td style="text-align:left;"><code>SELECT CURRENT_TIMESTAMP() -&gt; 2018-09-19 20:57:43</code></td></tr><tr><td style="text-align:left;">CURTIME()</td><td style="text-align:left;">\u8FD4\u56DE\u5F53\u524D\u65F6\u95F4</td><td style="text-align:left;"><code>SELECT CURTIME(); -&gt; 19:59:02</code></td></tr><tr><td style="text-align:left;">DATE()</td><td style="text-align:left;">\u4ECE\u65E5\u671F\u6216\u65E5\u671F\u65F6\u95F4\u8868\u8FBE\u5F0F\u4E2D\u63D0\u53D6\u65E5\u671F\u503C</td><td style="text-align:left;"><code>SELECT DATE(&quot;2017-06-15&quot;); -&gt; 2017-06-15</code></td></tr><tr><td style="text-align:left;">DATEDIFF(d1,d2)</td><td style="text-align:left;">\u8BA1\u7B97\u65E5\u671F d1-&gt;d2 \u4E4B\u95F4\u76F8\u9694\u7684\u5929\u6570</td><td style="text-align:left;"><code>SELECT DATEDIFF(&#39;2001-01-01&#39;,&#39;2001-02-02&#39;) -&gt; -32</code></td></tr><tr><td style="text-align:left;">DATE_ADD(d\uFF0CINTERVAL expr type)</td><td style="text-align:left;">\u8BA1\u7B97\u8D77\u59CB\u65E5\u671F d \u52A0\u4E0A\u4E00\u4E2A\u65F6\u95F4\u6BB5\u540E\u7684\u65E5\u671F\uFF0Ctype \u503C\u53EF\u4EE5\u662F\uFF1AMICROSECONDSECONDMINUTEHOURDAYWEEKMONTHQUARTERYEARSECOND_MICROSECONDMINUTE_MICROSECONDMINUTE_SECONDHOUR_MICROSECONDHOUR_SECONDHOUR_MINUTEDAY_MICROSECONDDAY_SECONDDAY_MINUTEDAY_HOURYEAR_MONTH</td><td style="text-align:left;"><code>SELECT DATE_ADD(&quot;2017-06-15&quot;, INTERVAL 10 DAY); -&gt; 2017-06-25 SELECT DATE_ADD(&quot;2017-06-15 09:34:21&quot;, INTERVAL 15 MINUTE); -&gt; 2017-06-15 09:49:21 SELECT DATE_ADD(&quot;2017-06-15 09:34:21&quot;, INTERVAL -3 HOUR); -&gt;2017-06-15 06:34:21 SELECT DATE_ADD(&quot;2017-06-15 09:34:21&quot;, INTERVAL -3 HOUR); -&gt;2017-04-15</code></td></tr><tr><td style="text-align:left;">DATE_FORMAT(d,f)</td><td style="text-align:left;">\u6309\u8868\u8FBE\u5F0F f\u7684\u8981\u6C42\u663E\u793A\u65E5\u671F d</td><td style="text-align:left;"><code>SELECT DATE_FORMAT(&#39;2011-11-11 11:11:11&#39;,&#39;%Y-%m-%d %r&#39;) -&gt; 2011-11-11 11:11:11 AM</code></td></tr><tr><td style="text-align:left;">DATE_SUB(date,INTERVAL expr type)</td><td style="text-align:left;">\u51FD\u6570\u4ECE\u65E5\u671F\u51CF\u53BB\u6307\u5B9A\u7684\u65F6\u95F4\u95F4\u9694\u3002</td><td style="text-align:left;">Orders \u8868\u4E2D OrderDate \u5B57\u6BB5\u51CF\u53BB 2 \u5929\uFF1A<code>SELECT OrderId,DATE_SUB(OrderDate,INTERVAL 2 DAY) AS OrderPayDate FROM Orders</code></td></tr><tr><td style="text-align:left;">DAY(d)</td><td style="text-align:left;">\u8FD4\u56DE\u65E5\u671F\u503C d \u7684\u65E5\u671F\u90E8\u5206</td><td style="text-align:left;"><code>SELECT DAY(&quot;2017-06-15&quot;); -&gt; 15</code></td></tr><tr><td style="text-align:left;">DAYNAME(d)</td><td style="text-align:left;">\u8FD4\u56DE\u65E5\u671F d \u662F\u661F\u671F\u51E0\uFF0C\u5982 Monday,Tuesday</td><td style="text-align:left;"><code>SELECT DAYNAME(&#39;2011-11-11 11:11:11&#39;) -&gt;Friday</code></td></tr><tr><td style="text-align:left;">DAYOFMONTH(d)</td><td style="text-align:left;">\u8BA1\u7B97\u65E5\u671F d \u662F\u672C\u6708\u7684\u7B2C\u51E0\u5929</td><td style="text-align:left;"><code>SELECT DAYOFMONTH(&#39;2011-11-11 11:11:11&#39;) -&gt;11</code></td></tr><tr><td style="text-align:left;">DAYOFWEEK(d)</td><td style="text-align:left;">\u65E5\u671F d \u4ECA\u5929\u662F\u661F\u671F\u51E0\uFF0C1 \u661F\u671F\u65E5\uFF0C2 \u661F\u671F\u4E00\uFF0C\u4EE5\u6B64\u7C7B\u63A8</td><td style="text-align:left;"><code>SELECT DAYOFWEEK(&#39;2011-11-11 11:11:11&#39;) -&gt;6</code></td></tr><tr><td style="text-align:left;">DAYOFYEAR(d)</td><td style="text-align:left;">\u8BA1\u7B97\u65E5\u671F d \u662F\u672C\u5E74\u7684\u7B2C\u51E0\u5929</td><td style="text-align:left;"><code>SELECT DAYOFYEAR(&#39;2011-11-11 11:11:11&#39;) -&gt;315</code></td></tr><tr><td style="text-align:left;">EXTRACT(type FROM d)</td><td style="text-align:left;">\u4ECE\u65E5\u671F d \u4E2D\u83B7\u53D6\u6307\u5B9A\u7684\u503C\uFF0Ctype \u6307\u5B9A\u8FD4\u56DE\u7684\u503C\u3002 type\u53EF\u53D6\u503C\u4E3A\uFF1A MICROSECONDSECONDMINUTEHOURDAYWEEKMONTHQUARTERYEARSECOND_MICROSECONDMINUTE_MICROSECONDMINUTE_SECONDHOUR_MICROSECONDHOUR_SECONDHOUR_MINUTEDAY_MICROSECONDDAY_SECONDDAY_MINUTEDAY_HOURYEAR_MONTH</td><td style="text-align:left;"><code>SELECT EXTRACT(MINUTE FROM &#39;2011-11-11 11:11:11&#39;) -&gt; 11</code></td></tr><tr><td style="text-align:left;">FROM_DAYS(n)</td><td style="text-align:left;">\u8BA1\u7B97\u4ECE 0000 \u5E74 1 \u6708 1 \u65E5\u5F00\u59CB n \u5929\u540E\u7684\u65E5\u671F</td><td style="text-align:left;"><code>SELECT FROM_DAYS(1111) -&gt; 0003-01-16</code></td></tr><tr><td style="text-align:left;">HOUR(t)</td><td style="text-align:left;">\u8FD4\u56DE t \u4E2D\u7684\u5C0F\u65F6\u503C</td><td style="text-align:left;"><code>SELECT HOUR(&#39;1:2:3&#39;) -&gt; 1</code></td></tr><tr><td style="text-align:left;">LAST_DAY(d)</td><td style="text-align:left;">\u8FD4\u56DE\u7ED9\u7ED9\u5B9A\u65E5\u671F\u7684\u90A3\u4E00\u6708\u4EFD\u7684\u6700\u540E\u4E00\u5929</td><td style="text-align:left;"><code>SELECT LAST_DAY(&quot;2017-06-20&quot;); -&gt; 2017-06-30</code></td></tr><tr><td style="text-align:left;">LOCALTIME()</td><td style="text-align:left;">\u8FD4\u56DE\u5F53\u524D\u65E5\u671F\u548C\u65F6\u95F4</td><td style="text-align:left;"><code>SELECT LOCALTIME() -&gt; 2018-09-19 20:57:43</code></td></tr><tr><td style="text-align:left;">LOCALTIMESTAMP()</td><td style="text-align:left;">\u8FD4\u56DE\u5F53\u524D\u65E5\u671F\u548C\u65F6\u95F4</td><td style="text-align:left;"><code>SELECT LOCALTIMESTAMP() -&gt; 2018-09-19 20:57:43</code></td></tr><tr><td style="text-align:left;">MAKEDATE(year, day-of-year)</td><td style="text-align:left;">\u57FA\u4E8E\u7ED9\u5B9A\u53C2\u6570\u5E74\u4EFD year \u548C\u6240\u5728\u5E74\u4E2D\u7684\u5929\u6570\u5E8F\u53F7 day-of-year \u8FD4\u56DE\u4E00\u4E2A\u65E5\u671F</td><td style="text-align:left;"><code>SELECT MAKEDATE(2017, 3); -&gt; 2017-01-03</code></td></tr><tr><td style="text-align:left;">MAKETIME(hour, minute, second)</td><td style="text-align:left;">\u7EC4\u5408\u65F6\u95F4\uFF0C\u53C2\u6570\u5206\u522B\u4E3A\u5C0F\u65F6\u3001\u5206\u949F\u3001\u79D2</td><td style="text-align:left;"><code>SELECT MAKETIME(11, 35, 4); -&gt; 11:35:04</code></td></tr><tr><td style="text-align:left;">MICROSECOND(date)</td><td style="text-align:left;">\u8FD4\u56DE\u65E5\u671F\u53C2\u6570\u6240\u5BF9\u5E94\u7684\u5FAE\u79D2\u6570</td><td style="text-align:left;"><code>SELECT MICROSECOND(&quot;2017-06-20 09:34:00.000023&quot;); -&gt; 23</code></td></tr><tr><td style="text-align:left;">MINUTE(t)</td><td style="text-align:left;">\u8FD4\u56DE t \u4E2D\u7684\u5206\u949F\u503C</td><td style="text-align:left;"><code>SELECT MINUTE(&#39;1:2:3&#39;) -&gt; 2</code></td></tr><tr><td style="text-align:left;">MONTHNAME(d)</td><td style="text-align:left;">\u8FD4\u56DE\u65E5\u671F\u5F53\u4E2D\u7684\u6708\u4EFD\u540D\u79F0\uFF0C\u5982 November</td><td style="text-align:left;"><code>SELECT MONTHNAME(&#39;2011-11-11 11:11:11&#39;) -&gt; November</code></td></tr><tr><td style="text-align:left;">MONTH(d)</td><td style="text-align:left;">\u8FD4\u56DE\u65E5\u671Fd\u4E2D\u7684\u6708\u4EFD\u503C\uFF0C1 \u5230 12</td><td style="text-align:left;"><code>SELECT MONTH(&#39;2011-11-11 11:11:11&#39;) -&gt;11</code></td></tr><tr><td style="text-align:left;">NOW()</td><td style="text-align:left;">\u8FD4\u56DE\u5F53\u524D\u65E5\u671F\u548C\u65F6\u95F4</td><td style="text-align:left;"><code>SELECT NOW() -&gt; 2018-09-19 20:57:43</code></td></tr><tr><td style="text-align:left;">PERIOD_ADD(period, number)</td><td style="text-align:left;">\u4E3A \u5E74-\u6708 \u7EC4\u5408\u65E5\u671F\u6DFB\u52A0\u4E00\u4E2A\u65F6\u6BB5</td><td style="text-align:left;"><code>SELECT PERIOD_ADD(201703, 5); -&gt; 201708</code></td></tr><tr><td style="text-align:left;">PERIOD_DIFF(period1, period2)</td><td style="text-align:left;">\u8FD4\u56DE\u4E24\u4E2A\u65F6\u6BB5\u4E4B\u95F4\u7684\u6708\u4EFD\u5DEE\u503C</td><td style="text-align:left;"><code>SELECT PERIOD_DIFF(201710, 201703); -&gt; 7</code></td></tr><tr><td style="text-align:left;">QUARTER(d)</td><td style="text-align:left;">\u8FD4\u56DE\u65E5\u671Fd\u662F\u7B2C\u51E0\u5B63\u8282\uFF0C\u8FD4\u56DE 1 \u5230 4</td><td style="text-align:left;"><code>SELECT QUARTER(&#39;2011-11-11 11:11:11&#39;) -&gt; 4</code></td></tr><tr><td style="text-align:left;">SECOND(t)</td><td style="text-align:left;">\u8FD4\u56DE t \u4E2D\u7684\u79D2\u949F\u503C</td><td style="text-align:left;"><code>SELECT SECOND(&#39;1:2:3&#39;) -&gt; 3</code></td></tr><tr><td style="text-align:left;">SEC_TO_TIME(s)</td><td style="text-align:left;">\u5C06\u4EE5\u79D2\u4E3A\u5355\u4F4D\u7684\u65F6\u95F4 s \u8F6C\u6362\u4E3A\u65F6\u5206\u79D2\u7684\u683C\u5F0F</td><td style="text-align:left;"><code>SELECT SEC_TO_TIME(4320) -&gt; 01:12:00</code></td></tr><tr><td style="text-align:left;">STR_TO_DATE(string, format_mask)</td><td style="text-align:left;">\u5C06\u5B57\u7B26\u4E32\u8F6C\u53D8\u4E3A\u65E5\u671F</td><td style="text-align:left;"><code>SELECT STR_TO_DATE(&quot;August 10 2017&quot;, &quot;%M %d %Y&quot;); -&gt; 2017-08-10</code></td></tr><tr><td style="text-align:left;">SUBDATE(d,n)</td><td style="text-align:left;">\u65E5\u671F d \u51CF\u53BB n \u5929\u540E\u7684\u65E5\u671F</td><td style="text-align:left;"><code>SELECT SUBDATE(&#39;2011-11-11 11:11:11&#39;, 1) -&gt;2011-11-10 11:11:11 (\u9ED8\u8BA4\u662F\u5929)</code></td></tr><tr><td style="text-align:left;">SUBTIME(t,n)</td><td style="text-align:left;">\u65F6\u95F4 t \u51CF\u53BB n \u79D2\u7684\u65F6\u95F4</td><td style="text-align:left;"><code>SELECT SUBTIME(&#39;2011-11-11 11:11:11&#39;, 5) -&gt;2011-11-11 11:11:06 (\u79D2)</code></td></tr><tr><td style="text-align:left;">SYSDATE()</td><td style="text-align:left;">\u8FD4\u56DE\u5F53\u524D\u65E5\u671F\u548C\u65F6\u95F4</td><td style="text-align:left;"><code>SELECT SYSDATE() -&gt; 2018-09-19 20:57:43</code></td></tr><tr><td style="text-align:left;">TIME(expression)</td><td style="text-align:left;">\u63D0\u53D6\u4F20\u5165\u8868\u8FBE\u5F0F\u7684\u65F6\u95F4\u90E8\u5206</td><td style="text-align:left;"><code>SELECT TIME(&quot;19:30:10&quot;); -&gt; 19:30:10</code></td></tr><tr><td style="text-align:left;">TIME_FORMAT(t,f)</td><td style="text-align:left;">\u6309\u8868\u8FBE\u5F0F f \u7684\u8981\u6C42\u663E\u793A\u65F6\u95F4 t</td><td style="text-align:left;"><code>SELECT TIME_FORMAT(&#39;11:11:11&#39;,&#39;%r&#39;) 11:11:11 AM</code></td></tr><tr><td style="text-align:left;">TIME_TO_SEC(t)</td><td style="text-align:left;">\u5C06\u65F6\u95F4 t \u8F6C\u6362\u4E3A\u79D2</td><td style="text-align:left;"><code>SELECT TIME_TO_SEC(&#39;1:12:00&#39;) -&gt; 4320</code></td></tr><tr><td style="text-align:left;">TIMEDIFF(time1, time2)</td><td style="text-align:left;">\u8BA1\u7B97\u65F6\u95F4\u5DEE\u503C</td><td style="text-align:left;"><code>SELECT TIMEDIFF(&quot;13:10:11&quot;, &quot;13:10:10&quot;); -&gt; 00:00:01</code></td></tr><tr><td style="text-align:left;">TIMESTAMP(expression, interval)</td><td style="text-align:left;">\u5355\u4E2A\u53C2\u6570\u65F6\uFF0C\u51FD\u6570\u8FD4\u56DE\u65E5\u671F\u6216\u65E5\u671F\u65F6\u95F4\u8868\u8FBE\u5F0F\uFF1B\u67092\u4E2A\u53C2\u6570\u65F6\uFF0C\u5C06\u53C2\u6570\u52A0\u548C</td><td style="text-align:left;"><code>SELECT TIMESTAMP(&quot;2017-07-23&quot;, &quot;13:10:11&quot;); -&gt; 2017-07-23 13:10:11</code></td></tr><tr><td style="text-align:left;">TO_DAYS(d)</td><td style="text-align:left;">\u8BA1\u7B97\u65E5\u671F d \u8DDD\u79BB 0000 \u5E74 1 \u6708 1 \u65E5\u7684\u5929\u6570</td><td style="text-align:left;"><code>SELECT TO_DAYS(&#39;0001-01-01 01:01:01&#39;) -&gt; 366</code></td></tr><tr><td style="text-align:left;">WEEK(d)</td><td style="text-align:left;">\u8BA1\u7B97\u65E5\u671F d \u662F\u672C\u5E74\u7684\u7B2C\u51E0\u4E2A\u661F\u671F\uFF0C\u8303\u56F4\u662F 0 \u5230 53</td><td style="text-align:left;"><code>SELECT WEEK(&#39;2011-11-11 11:11:11&#39;) -&gt; 45</code></td></tr><tr><td style="text-align:left;">WEEKDAY(d)</td><td style="text-align:left;">\u65E5\u671F d \u662F\u661F\u671F\u51E0\uFF0C0 \u8868\u793A\u661F\u671F\u4E00\uFF0C1 \u8868\u793A\u661F\u671F\u4E8C</td><td style="text-align:left;"><code>SELECT WEEKDAY(&quot;2017-06-15&quot;); -&gt; 3</code></td></tr><tr><td style="text-align:left;">WEEKOFYEAR(d)</td><td style="text-align:left;">\u8BA1\u7B97\u65E5\u671F d \u662F\u672C\u5E74\u7684\u7B2C\u51E0\u4E2A\u661F\u671F\uFF0C\u8303\u56F4\u662F 0 \u5230 53</td><td style="text-align:left;"><code>SELECT WEEKOFYEAR(&#39;2011-11-11 11:11:11&#39;) -&gt; 45</code></td></tr><tr><td style="text-align:left;">YEAR(d)</td><td style="text-align:left;">\u8FD4\u56DE\u5E74\u4EFD</td><td style="text-align:left;"><code>SELECT YEAR(&quot;2017-06-15&quot;); -&gt; 2017</code></td></tr><tr><td style="text-align:left;">YEARWEEK(date, mode)</td><td style="text-align:left;">\u8FD4\u56DE\u5E74\u4EFD\u53CA\u7B2C\u51E0\u5468\uFF080\u523053\uFF09\uFF0Cmode \u4E2D 0 \u8868\u793A\u5468\u5929\uFF0C1\u8868\u793A\u5468\u4E00\uFF0C\u4EE5\u6B64\u7C7B\u63A8</td><td style="text-align:left;"><code>SELECT YEARWEEK(&quot;2017-06-15&quot;); -&gt; 201724</code></td></tr></tbody></table><hr><h2 id="mysql-\u52A0\u5BC6\u51FD\u6570" tabindex="-1">MySQL \u52A0\u5BC6\u51FD\u6570 <a class="header-anchor" href="#mysql-\u52A0\u5BC6\u51FD\u6570" aria-hidden="true">#</a></h2><table><thead><tr><th>\u51FD\u6570\u540D</th><th>\u63CF\u8FF0</th><th>\u5B9E\u4F8B</th></tr></thead><tbody><tr><td>PASSWORD(str)</td><td>\u8BE5\u51FD\u6570\u53EF\u4EE5\u5BF9\u5B57\u7B26\u4E32str\u8FDB\u884C\u52A0\u5BC6\uFF0C\u4E00\u822C\u60C5\u51B5\u4E0B\uFF0CPASSWORD(str)\u7528\u4E8E\u7ED9\u7528\u6237\u7684\u5BC6\u7801\u52A0\u5BC6\u3002</td><td>SELECT PASSWORD(&#39;123&#39;)</td></tr><tr><td>MD5(str)</td><td>MD5(str)\u51FD\u6570\u53EF\u4EE5\u5BF9\u5B57\u7B26\u4E32str\u8FDB\u884C\u6563\u5217\uFF0C\u53EF\u4EE5\u7528\u4E8E\u4E00\u4E9B\u666E\u901A\u7684\u4E0D\u9700\u8981\u89E3\u5BC6\u7684\u6570\u636E\u52A0\u5BC6\u3002</td><td>SELECT md5(&#39;123&#39;)</td></tr><tr><td>ENCODE(str,pswd_str)\u4E0EDECODE(crypt_str,pswd_str)</td><td>ENCODE\u51FD\u6570\u53EF\u4EE5\u4F7F\u7528\u52A0\u5BC6\u5BC6\u7801pswd_str\u6765\u52A0\u5BC6\u5B57\u7B26\u4E32str\uFF0C\u52A0\u5BC6\u7ED3\u679C\u662F\u4E8C\u8FDB\u5236\u6570\uFF0C\u9700\u8981\u4F7F\u7528BLOB\u7C7B\u578B\u7684\u5B57\u6BB5\u4FDD\u5B58\u3002\u8BE5\u51FD\u6570\u4E0EDECODE\u662F\u4E00\u5BF9\uFF0C\u9700\u8981\u540C\u6837\u7684\u5BC6\u7801\u624D\u80FD\u591F\u89E3\u5BC6\u3002</td><td>SELECT ENCODE(&#39;123&#39;,&#39;xxoo&#39;)<br> -&gt;;vx<br>SELECT DECODE(&#39;;vx&#39;,&#39;xxoo&#39;)<br> -&gt;123</td></tr></tbody></table><hr><h2 id="mysql-\u9AD8\u7EA7\u51FD\u6570" tabindex="-1">MySQL \u9AD8\u7EA7\u51FD\u6570 <a class="header-anchor" href="#mysql-\u9AD8\u7EA7\u51FD\u6570" aria-hidden="true">#</a></h2><table><thead><tr><th style="text-align:left;">\u51FD\u6570\u540D</th><th style="text-align:left;">\u63CF\u8FF0</th><th style="text-align:left;">\u5B9E\u4F8B</th></tr></thead><tbody><tr><td style="text-align:left;">BIN(x)</td><td style="text-align:left;">\u8FD4\u56DE x \u7684\u4E8C\u8FDB\u5236\u7F16\u7801</td><td style="text-align:left;">15 \u7684 2 \u8FDB\u5236\u7F16\u7801:<code>SELECT BIN(15); -- 1111</code></td></tr><tr><td style="text-align:left;">BINARY(s)</td><td style="text-align:left;">\u5C06\u5B57\u7B26\u4E32 s \u8F6C\u6362\u4E3A\u4E8C\u8FDB\u5236\u5B57\u7B26\u4E32</td><td style="text-align:left;"><code>SELECT BINARY &quot;RUNOOB&quot;; -&gt; RUNOOB</code></td></tr><tr><td style="text-align:left;"><code>CASE expression WHEN condition1 THEN result1 WHEN condition2 THEN result2 ... WHEN conditionN THEN resultN ELSE result END</code></td><td style="text-align:left;">CASE \u8868\u793A\u51FD\u6570\u5F00\u59CB\uFF0CEND \u8868\u793A\u51FD\u6570\u7ED3\u675F\u3002\u5982\u679C condition1 \u6210\u7ACB\uFF0C\u5219\u8FD4\u56DE result1, \u5982\u679C condition2 \u6210\u7ACB\uFF0C\u5219\u8FD4\u56DE result2\uFF0C\u5F53\u5168\u90E8\u4E0D\u6210\u7ACB\u5219\u8FD4\u56DE result\uFF0C\u800C\u5F53\u6709\u4E00\u4E2A\u6210\u7ACB\u4E4B\u540E\uFF0C\u540E\u9762\u7684\u5C31\u4E0D\u6267\u884C\u4E86\u3002</td><td style="text-align:left;"><code>SELECT CASE \u3000WHEN 1 &gt; 0 \u3000THEN &#39;1 &gt; 0&#39; \u3000WHEN 2 &gt; 0 \u3000THEN &#39;2 &gt; 0&#39; \u3000ELSE &#39;3 &gt; 0&#39; \u3000END -&gt;1 &gt; 0</code></td></tr><tr><td style="text-align:left;">CAST(x AS type)</td><td style="text-align:left;">\u8F6C\u6362\u6570\u636E\u7C7B\u578B</td><td style="text-align:left;">\u5B57\u7B26\u4E32\u65E5\u671F\u8F6C\u6362\u4E3A\u65E5\u671F\uFF1A<code>SELECT CAST(&quot;2017-08-29&quot; AS DATE); -&gt; 2017-08-29</code></td></tr><tr><td style="text-align:left;">COALESCE(expr1, expr2, ...., expr_n)</td><td style="text-align:left;">\u8FD4\u56DE\u53C2\u6570\u4E2D\u7684\u7B2C\u4E00\u4E2A\u975E\u7A7A\u8868\u8FBE\u5F0F\uFF08\u4ECE\u5DE6\u5411\u53F3\uFF09</td><td style="text-align:left;"><code>SELECT COALESCE(NULL, NULL, NULL, &#39;runoob.com&#39;, NULL, &#39;google.com&#39;); -&gt; runoob.com</code></td></tr><tr><td style="text-align:left;">CONNECTION_ID()</td><td style="text-align:left;">\u8FD4\u56DE\u552F\u4E00\u7684\u8FDE\u63A5 ID</td><td style="text-align:left;"><code>SELECT CONNECTION_ID(); -&gt; 4292835</code></td></tr><tr><td style="text-align:left;">CONV(x,f1,f2)</td><td style="text-align:left;">\u8FD4\u56DE f1 \u8FDB\u5236\u6570\u53D8\u6210 f2 \u8FDB\u5236\u6570</td><td style="text-align:left;"><code>SELECT CONV(15, 10, 2); -&gt; 1111</code></td></tr><tr><td style="text-align:left;">CONVERT(s USING cs)</td><td style="text-align:left;">\u51FD\u6570\u5C06\u5B57\u7B26\u4E32 s \u7684\u5B57\u7B26\u96C6\u53D8\u6210 cs</td><td style="text-align:left;"><code>SELECT CHARSET(&#39;ABC&#39;) -&gt;utf-8 SELECT CHARSET(CONVERT(&#39;ABC&#39; USING gbk)) -&gt;gbk</code></td></tr><tr><td style="text-align:left;">CURRENT_USER()</td><td style="text-align:left;">\u8FD4\u56DE\u5F53\u524D\u7528\u6237</td><td style="text-align:left;"><code>SELECT CURRENT_USER(); -&gt; guest@%</code></td></tr><tr><td style="text-align:left;">DATABASE()</td><td style="text-align:left;">\u8FD4\u56DE\u5F53\u524D\u6570\u636E\u5E93\u540D</td><td style="text-align:left;"><code>SELECT DATABASE(); -&gt; runoob</code></td></tr><tr><td style="text-align:left;">IF(expr,v1,v2)</td><td style="text-align:left;">\u5982\u679C\u8868\u8FBE\u5F0F expr \u6210\u7ACB\uFF0C\u8FD4\u56DE\u7ED3\u679C v1\uFF1B\u5426\u5219\uFF0C\u8FD4\u56DE\u7ED3\u679C v2\u3002</td><td style="text-align:left;"><code>SELECT IF(1 &gt; 0,&#39;\u6B63\u786E&#39;,&#39;\u9519\u8BEF&#39;) -&gt;\u6B63\u786E</code></td></tr><tr><td style="text-align:left;"><a href="https://www.runoob.com/mysql/mysql-func-ifnull.html" target="_blank" rel="noopener noreferrer">IFNULL(v1,v2)</a></td><td style="text-align:left;">\u5982\u679C v1 \u7684\u503C\u4E0D\u4E3A NULL\uFF0C\u5219\u8FD4\u56DE v1\uFF0C\u5426\u5219\u8FD4\u56DE v2\u3002</td><td style="text-align:left;"><code>SELECT IFNULL(null,&#39;Hello Word&#39;) -&gt;Hello Word</code></td></tr><tr><td style="text-align:left;">ISNULL(expression)</td><td style="text-align:left;">\u5224\u65AD\u8868\u8FBE\u5F0F\u662F\u5426\u4E3A NULL</td><td style="text-align:left;"><code>SELECT ISNULL(NULL); -&gt;1</code></td></tr><tr><td style="text-align:left;">LAST_INSERT_ID()</td><td style="text-align:left;">\u8FD4\u56DE\u6700\u8FD1\u751F\u6210\u7684 AUTO_INCREMENT \u503C</td><td style="text-align:left;"><code>SELECT LAST_INSERT_ID(); -&gt;6</code></td></tr><tr><td style="text-align:left;">NULLIF(expr1, expr2)</td><td style="text-align:left;">\u6BD4\u8F83\u4E24\u4E2A\u5B57\u7B26\u4E32\uFF0C\u5982\u679C\u5B57\u7B26\u4E32 expr1 \u4E0E expr2 \u76F8\u7B49 \u8FD4\u56DE NULL\uFF0C\u5426\u5219\u8FD4\u56DE expr1</td><td style="text-align:left;"><code>SELECT NULLIF(25, 25); -&gt;</code></td></tr><tr><td style="text-align:left;">SESSION_USER()</td><td style="text-align:left;">\u8FD4\u56DE\u5F53\u524D\u7528\u6237</td><td style="text-align:left;"><code>SELECT SESSION_USER(); -&gt; guest@%</code></td></tr><tr><td style="text-align:left;">SYSTEM_USER()</td><td style="text-align:left;">\u8FD4\u56DE\u5F53\u524D\u7528\u6237</td><td style="text-align:left;"><code>SELECT SYSTEM_USER(); -&gt; guest@%</code></td></tr><tr><td style="text-align:left;">USER()</td><td style="text-align:left;">\u8FD4\u56DE\u5F53\u524D\u7528\u6237</td><td style="text-align:left;"><code>SELECT USER(); -&gt; guest@%</code></td></tr><tr><td style="text-align:left;">VERSION()</td><td style="text-align:left;">\u8FD4\u56DE\u6570\u636E\u5E93\u7684\u7248\u672C\u53F7</td><td style="text-align:left;"><code>SELECT VERSION() -&gt; 5.6.34</code></td></tr></tbody></table>',21),o=[n];function a(E,r,g,i,x,y){return e(),l("div",null,o)}var T=t(s,[["render",a]]);export{c as __pageData,T as default};
