import{_ as e,o as p,c as o,a as r}from"./app.1e6146c3.js";const C=JSON.parse('{"title":"Ceph\u67B6\u6784","description":"","frontmatter":{},"headers":[{"level":3,"title":"Ceph\u67B6\u6784","slug":"ceph\u67B6\u6784"},{"level":3,"title":"Ceph\u6838\u5FC3\u7EC4\u4EF6","slug":"ceph\u6838\u5FC3\u7EC4\u4EF6"},{"level":3,"title":"Ceph\u6570\u636E\u5B58\u50A8\u8FC7\u7A0B","slug":"ceph\u6570\u636E\u5B58\u50A8\u8FC7\u7A0B"},{"level":3,"title":"CRUSH \u7B97\u6CD5","slug":"crush-\u7B97\u6CD5"},{"level":3,"title":"\u53C2\u8003","slug":"\u53C2\u8003"}],"relativePath":"LinuxBasics/FileSystem/Ceph/Ceph Basic.md","lastUpdated":1657272051000}'),a={name:"LinuxBasics/FileSystem/Ceph/Ceph Basic.md"},t=r('<p>Ceph\u662F\u57FA\u4E8E<code>RADOS</code>\u65E0\u9650\u6269\u5C55\u7684\u5206\u5E03\u5F0F\u5B58\u50A8\u96C6\u7FA4\uFF0C\u63D0\u4F9B\u540C\u65F6\u5305\u62EC\u5BF9\u8C61\u3001\u5757\u8BBE\u5907\u3001\u548C\u6587\u4EF6\u7684\u5B58\u50A8\u670D\u52A1\uFF0C\u62E5\u6709\u9AD8\u53EF\u9760\u3001\u6613\u4E8E\u7BA1\u7406\u3001\u5F00\u6E90\u7B49\u7279\u70B9\u3002Ceph\u6709\u7740\u6781\u6613\u6269\u5C55\u7684\u7279\u6027\u2014\u2014\u4E0A\u5343\u6570\u91CF\u7684\u5BA2\u6237\u7AEF\u53EF\u540C\u65F6\u8BBF\u95EEPB~EB\u6570\u91CF\u7EA7\u7684\u6570\u636E\u3002</p><h3 id="ceph\u67B6\u6784" tabindex="-1">Ceph\u67B6\u6784 <a class="header-anchor" href="#ceph\u67B6\u6784" aria-hidden="true">#</a></h3><p>Ceph\u7684\u6574\u4F53\u67B6\u6784\u7531\u4E09\u4E2A\u5C42\u6B21\u7EC4\u6210\uFF1A<strong>\u6700\u5E95\u5C42\u4E5F\u662F\u6700\u6838\u5FC3\u7684\u90E8\u5206\u662FRADOS\u5BF9\u8C61\u5B58\u50A8\u7CFB\u7EDF\u3002\u7B2C\u4E8C\u5C42\u662Flibrados\u5E93\u5C42\uFF1B\u6700\u4E0A\u5C42\u5BF9\u5E94\u7740Ceph\u4E0D\u540C\u5F62\u5F0F\u7684\u5B58\u50A8\u63A5\u53E3\u5B9E\u73B0\u3002</strong></p><p><img src="https://cdn.jsdelivr.net/gh/AGou-ops/images/2020/ceph-rados.png" alt="img"></p><h4 id="rados" tabindex="-1">RADOS <a class="header-anchor" href="#rados" aria-hidden="true">#</a></h4><p>\u4ECE\u67B6\u6784\u56FE\u4E2D\u53EF\u4EE5\u770B\u5230\u6700\u5E95\u5C42\u7684\u662FRADOS\uFF08reliable\uFF0Cautonomous\uFF0Cdistributed object store\uFF09\uFF0CRADOS\u81EA\u8EAB\u662F\u4E00\u4E2A\u5B8C\u6574\u7684\u5206\u5E03\u5F0F\u5BF9\u8C61\u5B58\u50A8\u7CFB\u7EDF\uFF0C\u5B83\u5177\u6709\u53EF\u9760\u3001\u667A\u80FD\u3001\u5206\u5E03\u5F0F\u7B49\u7279\u6027\uFF0CCeph\u7684\u9AD8\u53EF\u9760\u3001\u9AD8\u53EF\u62D3\u5C55\u3001\u9AD8\u6027\u80FD\u3001\u9AD8\u81EA\u52A8\u5316\u90FD\u662F\u7531\u8FD9\u4E00\u5C42\u6765\u63D0\u4F9B\u7684\uFF0C\u7528\u6237\u6570\u636E\u7684\u5B58\u50A8\u6700\u7EC8\u4E5F\u90FD\u662F\u901A\u8FC7\u8FD9\u4E00\u5C42\u6765\u8FDB\u884C\u5B58\u50A8\u7684\uFF0CRADOS\u53EF\u4EE5\u8BF4\u5C31\u662FCeph\u7684\u6838\u5FC3\u3002</p><p><strong>RADOS\u7CFB\u7EDF\u4E3B\u8981\u7531\u4E24\u90E8\u5206\u7EC4\u6210\uFF0C\u5206\u522B\u662FOSD\u548CMonitor\u3002</strong></p><h4 id="librados" tabindex="-1">LIBRADOS <a class="header-anchor" href="#librados" aria-hidden="true">#</a></h4><p>\u57FA\u4E8ERADOS\u5C42\u7684\u4E0A\u4E00\u5C42\u662FLIBRADOS\uFF0CLIBRADOS\u662F\u4E00\u4E2A\u5E93\uFF0C\u5B83\u5141\u8BB8\u5E94\u7528\u7A0B\u5E8F\u901A\u8FC7\u8BBF\u95EE\u8BE5\u5E93\u6765\u4E0ERADOS\u7CFB\u7EDF\u8FDB\u884C\u4EA4\u4E92\uFF0C\u652F\u6301\u591A\u79CD\u7F16\u7A0B\u8BED\u8A00\uFF0C\u6BD4\u5982C\u3001C++\u3001Python\u7B49\u3002</p><h4 id="radosgw-rbd-ceph-fs" tabindex="-1">RADOSGW RBD CEPH FS <a class="header-anchor" href="#radosgw-rbd-ceph-fs" aria-hidden="true">#</a></h4><p>\u6700\u4E0A\u5C42\u9762\u5411\u5E94\u7528\u63D0\u4F9B3\u79CD\u4E0D\u540C\u7684\u5B58\u50A8\u63A5\u53E3\uFF1A</p><p>l \u5757\u5B58\u50A8\u63A5\u53E3\uFF0C\u901A\u8FC7<strong>librbd\u5E93</strong>\u63D0\u4F9B\u4E86\u5757\u5B58\u50A8\u8BBF\u95EE\u63A5\u53E3\u3002RBD\u901A\u8FC7Linux\u5185\u6838\u5BA2\u6237\u7AEF\u548CQEMU/KVM\u9A71\u52A8\u6765\u63D0\u4F9B\u4E00\u4E2A\u5206\u5E03\u5F0F\u7684\u5757\u8BBE\u5907\u3002\u5B83\u53EF\u4EE5\u4E3A\u865A\u62DF\u673A\u63D0\u4F9B\u865A\u62DF\u78C1\u76D8\uFF0C\u6216\u8005\u901A\u8FC7\u5185\u6838\u6620\u5C04\u4E3A\u7269\u7406\u4E3B\u673A\u63D0\u4F9B\u78C1\u76D8\u7A7A\u95F4\u3002</p><p>l \u5BF9\u8C61\u5B58\u50A8\u63A5\u53E3\uFF0C<strong>RADOSGW</strong> \u76EE\u524D\u63D0\u4F9B\u4E86\u4E24\u79CD\u7C7B\u578B\u7684API\uFF0C\u4E00\u79CD\u662F\u548CAWS\u7684S3\u63A5\u53E3\u517C\u5BB9\u7684API\uFF0C\u53E6\u4E00\u79CD\u662F\u548COpenStack\u7684Swift\u5BF9\u8C61\u63A5\u53E3\u517C\u5BB9\u7684API\u3002</p><p>l \u6587\u4EF6\u7CFB\u7EDF\u63A5\u53E3\uFF0C\u76EE\u524D\u63D0\u4F9B\u4E24\u79CD\u63A5\u53E3\uFF0C\u4E00\u79CD\u662F\u6807\u51C6\u7684posix\u63A5\u53E3\uFF0C\u53E6\u4E00\u79CD\u901A\u8FC7<strong>libcephfs\u5E93</strong>\u63D0\u4F9B\u6587\u4EF6\u7CFB\u7EDF\u8BBF\u95EE\u63A5\u53E3\u3002\u6587\u4EF6\u7CFB\u7EDF\u7684\u5143\u6570\u636E\u670D\u52A1\u5668MDS\u7528\u4E8E\u63D0\u4F9B\u5143\u6570\u636E\u8BBF\u95EE\u3002\u6570\u636E\u76F4\u63A5\u901A\u8FC7librados\u5E93\u8BBF\u95EE\u3002</p><h3 id="ceph\u6838\u5FC3\u7EC4\u4EF6" tabindex="-1">Ceph\u6838\u5FC3\u7EC4\u4EF6 <a class="header-anchor" href="#ceph\u6838\u5FC3\u7EC4\u4EF6" aria-hidden="true">#</a></h3><p><strong>Ceph\u7684\u6838\u5FC3\u7EC4\u4EF6\u5305\u62ECCeph OSD\u3001Ceph Monitor\u548CCeph MDS</strong></p><h4 id="ceph-osd" tabindex="-1">Ceph OSD <a class="header-anchor" href="#ceph-osd" aria-hidden="true">#</a></h4><p>OSD\u7684\u82F1\u6587\u5168\u79F0\u662F<strong>Object Storage Device</strong>\uFF0C\u5B83\u7684\u4E3B\u8981\u529F\u80FD\u662F\u5B58\u50A8\u6570\u636E\u3001\u590D\u5236\u6570\u636E\u3001\u5E73\u8861\u6570\u636E\u3001\u6062\u590D\u6570\u636E\u7B49\uFF0C\u4E0E\u5176\u5B83OSD\u95F4\u8FDB\u884C\u5FC3\u8DF3\u68C0\u67E5\u7B49\uFF0C\u5E76\u5C06\u4E00\u4E9B\u53D8\u5316\u60C5\u51B5\u4E0A\u62A5\u7ED9Ceph Monitor\u3002<code>\u4E00\u822C\u60C5\u51B5\u4E0B\u4E00\u5757\u786C\u76D8\u5BF9\u5E94\u4E00\u4E2AOSD</code>\u3002</p><h5 id="filestore" tabindex="-1">filestore <a class="header-anchor" href="#filestore" aria-hidden="true">#</a></h5><p>\u4F34\u968FOSD\u7684\u8FD8\u6709\u4E00\u4E2A\u6982\u5FF5\u53EB\u505AJournal\u76D8\uFF0C\u4E00\u822C\u5199\u6570\u636E\u5230Ceph\u96C6\u7FA4\u65F6\uFF0C\u90FD\u662F\u5148\u5C06\u6570\u636E\u5199\u5165\u5230Journal\u76D8\u4E2D\uFF0C\u7136\u540E\u6BCF\u9694\u4E00\u6BB5\u65F6\u95F4\u6BD4\u59825\u79D2\u518D\u5C06Journal\u76D8\u4E2D\u7684\u6570\u636E\u5237\u65B0\u5230\u6587\u4EF6\u7CFB\u7EDF\u4E2D\u3002\u4E00\u822C\u4E3A\u4E86\u4F7F\u8BFB\u5199\u65F6\u5EF6\u66F4\u5C0F\uFF0CJournal\u76D8\u90FD\u662F\u91C7\u7528SSD\uFF0C\u4E00\u822C\u5206\u914D10G\u4EE5\u4E0A\uFF0C\u5F53\u7136\u5206\u914D\u591A\u70B9\u90A3\u662F\u66F4\u597D\uFF0CCeph\u4E2D\u5F15\u5165Journal\u76D8\u7684\u6982\u5FF5\u662F\u56E0\u4E3AJournal\u5141\u8BB8Ceph OSD\u80FD\u5F88\u5FEB\u505A\u5C0F\u7684\u5199\u64CD\u4F5C\uFF1B\u4E00\u4E2A\u968F\u673A\u5199\u5165\u9996\u5148\u5199\u5165\u5728\u4E0A\u4E00\u4E2A\u8FDE\u7EED\u7C7B\u578B\u7684journal\uFF0C\u7136\u540E\u5237\u65B0\u5230\u6587\u4EF6\u7CFB\u7EDF\uFF0C\u8FD9\u7ED9\u4E86\u6587\u4EF6\u7CFB\u7EDF\u8DB3\u591F\u7684\u65F6\u95F4\u6765\u5408\u5E76\u5199\u5165\u78C1\u76D8\uFF0C\u4E00\u822C\u60C5\u51B5\u4E0B\u4F7F\u7528SSD\u4F5C\u4E3AOSD\u7684journal\u53EF\u4EE5\u6709\u6548\u7F13\u51B2\u7A81\u53D1\u8D1F\u8F7D\u3002(<strong>bluestore\u4E2D\u53BB\u6389\u4E86</strong>)</p><h5 id="bluestore" tabindex="-1">bluestore <a class="header-anchor" href="#bluestore" aria-hidden="true">#</a></h5><p>bluestore\u7684\u8BDE\u751F\u662F\u4E3A\u4E86\u89E3\u51B3filestore\u81EA\u8EAB\u7EF4\u62A4\u4E00\u5957journal\u5E76\u540C\u65F6\u8FD8\u9700\u8981\u57FA\u4E8E\u7CFB\u7EDF\u6587\u4EF6\u7CFB\u7EDF\u7684\u5199\u653E\u5927\u95EE\u9898\uFF0C\u5E76\u4E14filestore\u672C\u8EAB\u6CA1\u6709\u5BF9SSD\u8FDB\u884C\u4F18\u5316\uFF0C\u56E0\u6B64bluestore\u76F8\u6BD4\u4E8Efilestore\u4E3B\u8981\u505A\u4E86\u4E24\u65B9\u9762\u7684\u6838\u5FC3\u5DE5\u4F5C\uFF1A</p><ul><li>\u53BB\u6389journal\uFF0C\u76F4\u63A5\u7BA1\u7406\u88F8\u8BBE\u5907</li><li>\u9488\u5BF9SSD\u8FDB\u884C\u5355\u72EC\u4F18\u5316</li></ul><h4 id="ceph-monitor" tabindex="-1">Ceph Monitor <a class="header-anchor" href="#ceph-monitor" aria-hidden="true">#</a></h4><p>Ceph Monitor\uFF1A\u7531\u8BE5\u82F1\u6587\u540D\u5B57\u6211\u4EEC\u53EF\u4EE5\u77E5\u9053\u5B83\u662F\u4E00\u4E2A\u76D1\u89C6\u5668\uFF0C\u8D1F\u8D23\u76D1\u89C6Ceph\u96C6\u7FA4\uFF0C\u7EF4\u62A4Ceph\u96C6\u7FA4\u7684\u5065\u5EB7\u72B6\u6001\uFF0C\u540C\u65F6\u7EF4\u62A4\u7740Ceph\u96C6\u7FA4\u4E2D\u7684\u5404\u79CDMap\u56FE\uFF0C\u6BD4\u5982OSD Map\u3001Monitor Map\u3001PG Map\u548CCRUSH Map\uFF0C\u8FD9\u4E9BMap\u7EDF\u79F0\u4E3ACluster Map\uFF0CCluster Map\u662F<code>RADOS</code>\u7684\u5173\u952E\u6570\u636E\u7ED3\u6784\uFF0C\u7BA1\u7406\u96C6\u7FA4\u4E2D\u7684\u6240\u6709\u6210\u5458\u3001\u5173\u7CFB\u3001\u5C5E\u6027\u7B49\u4FE1\u606F\u4EE5\u53CA\u6570\u636E\u7684\u5206\u53D1\uFF0C\u6BD4\u5982\u5F53\u7528\u6237\u9700\u8981\u5B58\u50A8\u6570\u636E\u5230Ceph\u96C6\u7FA4\u65F6\uFF0COSD\u9700\u8981\u5148\u901A\u8FC7Monitor\u83B7\u53D6\u6700\u65B0\u7684Map\u56FE\uFF0C\u7136\u540E\u6839\u636EMap\u56FE\u548Cobject id\u7B49\u8BA1\u7B97\u51FA\u6570\u636E\u6700\u7EC8\u5B58\u50A8\u7684\u4F4D\u7F6E\u3002</p><p>\u67E5\u770B\u5404\u79CDMap\u7684\u4FE1\u606F\u53EF\u4EE5\u901A\u8FC7\u5982\u4E0B\u547D\u4EE4\uFF1Aceph osd(mon\u3001pg) dump</p><h5 id="map" tabindex="-1">map <a class="header-anchor" href="#map" aria-hidden="true">#</a></h5><ul><li><strong>Monitor map</strong>\uFF1A\u5305\u62EC\u6709\u5173monitor \u8282\u70B9\u7AEF\u5230\u7AEF\u7684\u4FE1\u606F\uFF0C\u5176\u4E2D\u5305\u62EC Ceph \u96C6\u7FA4ID\uFF0C\u76D1\u63A7\u4E3B\u673A\u540D\u548CIP\u4EE5\u53CA\u7AEF\u53E3\u3002\u5E76\u4E14\u5B58\u50A8\u5F53\u524D\u7248\u672C\u4FE1\u606F\u4EE5\u53CA\u6700\u65B0\u66F4\u6539\u4FE1\u606F\uFF0C\u901A\u8FC7 \u201Cceph mon dump\u201D \u67E5\u770B monitor map\u3002</li><li><strong>OSD map</strong>\uFF1A\u5305\u62EC\u4E00\u4E9B\u5E38\u7528\u7684\u4FE1\u606F\uFF0C\u5982\u96C6\u7FA4ID\u3001\u521B\u5EFAOSD map\u7684 \u7248\u672C\u4FE1\u606F\u548C\u6700\u540E\u4FEE\u6539\u4FE1\u606F\uFF0C\u4EE5\u53CApool\u76F8\u5173\u4FE1\u606F\uFF0C\u4E3B\u8981\u5305\u62ECpool \u540D\u5B57\u3001pool\u7684ID\u3001\u7C7B\u578B\uFF0C\u526F\u672C\u6570\u76EE\u4EE5\u53CAPGP\u7B49\uFF0C\u8FD8\u5305\u62EC\u6570\u91CF\u3001\u72B6\u6001\u3001\u6743\u91CD\u3001\u6700\u65B0\u7684\u6E05\u6D01\u95F4\u9694\u548COSD\u4E3B\u673A\u4FE1\u606F\u3002\u901A\u8FC7\u547D\u4EE4 \u201Cceph osd dump\u201D \u67E5\u770B\u3002</li><li><strong>PG map</strong>\uFF1A\u5305\u62EC\u5F53\u524DPG\u7248\u672C\u3001\u65F6\u95F4\u6233\u3001\u6700\u65B0\u7684OSD Map\u7684\u7248\u672C\u4FE1\u606F\u3001\u7A7A\u95F4\u4F7F\u7528\u6BD4\u4F8B\uFF0C\u4EE5\u53CA\u63A5\u8FD1\u5360\u6EE1\u6BD4\u4F8B\u4FE1\u606F\uFF0C\u540C\u4E8B\uFF0C\u4E5F\u5305\u62EC\u6BCF\u4E2APG ID\u3001\u5BF9\u8C61\u6570\u76EE\u3001\u72B6\u6001\u3001OSD \u7684\u72B6\u6001\u4EE5\u53CA\u6DF1\u5EA6\u6E05\u7406\u7684\u8BE6\u7EC6\u4FE1\u606F\u3002\u901A\u8FC7\u547D\u4EE4 \u201Cceph pg dump\u201D \u53EF\u4EE5\u67E5\u770B\u76F8\u5173\u72B6\u6001\u3002</li><li><strong>CRUSH map</strong>\uFF1A CRUSH map \u5305\u62EC\u96C6\u7FA4\u5B58\u50A8\u8BBE\u5907\u4FE1\u606F\uFF0C\u6545\u969C\u57DF\u5C42\u6B21\u7ED3\u6784\u548C\u5B58\u50A8\u6570\u636E\u65F6\u5B9A\u4E49\u5931\u8D25\u57DF\u89C4\u5219\u4FE1\u606F\u3002\u901A\u8FC7 \u547D\u4EE4 \u201Cceph osd crush map\u201D \u67E5\u770B\u3002</li><li><strong>MDS map</strong>\uFF1AMDS Map \u5305\u62EC\u5B58\u50A8\u5F53\u524D MDS map \u7684\u7248\u672C\u4FE1\u606F\u3001\u521B\u5EFA\u5F53\u524D\u7684Map\u7684\u4FE1\u606F\u3001\u4FEE\u6539\u65F6\u95F4\u3001\u6570\u636E\u548C\u5143\u6570\u636EPOOL ID\u3001\u96C6\u7FA4MDS\u6570\u76EE\u548CMDS\u72B6\u6001\uFF0C\u53EF\u901A\u8FC7\u201Dceph mds dump\u201D\u67E5\u770B\u3002</li></ul><h4 id="ceph-mds" tabindex="-1">Ceph MDS <a class="header-anchor" href="#ceph-mds" aria-hidden="true">#</a></h4><p>Ceph MDS\uFF1A\u5168\u79F0\u662FCeph MetaData Server\uFF0C\u4E3B\u8981\u4FDD\u5B58\u7684\u6587\u4EF6\u7CFB\u7EDF\u670D\u52A1\u7684\u5143\u6570\u636E\uFF0C\u4F46\u5BF9\u8C61\u5B58\u50A8\u548C\u5757\u5B58\u50A8\u8BBE\u5907\u662F\u4E0D\u9700\u8981\u4F7F\u7528\u8BE5\u670D\u52A1\u7684\u3002</p><h3 id="ceph\u6570\u636E\u5B58\u50A8\u8FC7\u7A0B" tabindex="-1">Ceph\u6570\u636E\u5B58\u50A8\u8FC7\u7A0B <a class="header-anchor" href="#ceph\u6570\u636E\u5B58\u50A8\u8FC7\u7A0B" aria-hidden="true">#</a></h3><p>Ceph\u5B58\u50A8\u96C6\u7FA4\u4ECE\u5BA2\u6237\u7AEF\u63A5\u6536\u6587\u4EF6\uFF0C\u6BCF\u4E2A\u6587\u4EF6\u90FD\u4F1A\u88AB\u5BA2\u6237\u7AEF\u5207\u5206\u6210\u4E00\u4E2A\u6216\u591A\u4E2A\u5BF9\u8C61\uFF0C\u7136\u540E\u5C06\u8FD9\u4E9B\u5BF9\u8C61\u8FDB\u884C\u5206\u7EC4\uFF0C\u518D\u6839\u636E\u4E00\u5B9A\u7684\u7B56\u7565\u5B58\u50A8\u5230\u96C6\u7FA4\u7684OSD\u8282\u70B9\u4E2D\uFF0C\u5176\u5B58\u50A8\u8FC7\u7A0B\u5982\u56FE\u6240\u793A\uFF1A</p><p><img src="https://cdn.jsdelivr.net/gh/AGou-ops/images/2020/907596-20190731125728096-1940075886.png" alt="image-20190623224741004"></p><p>\u56FE\u4E2D\uFF0C\u5BF9\u8C61\u7684\u5206\u53D1\u9700\u8981\u7ECF\u8FC7\u4E24\u4E2A\u9636\u6BB5\u7684\u8BA1\u7B97\uFF0C\u624D\u80FD\u5F97\u5230\u5B58\u50A8\u8BE5\u5BF9\u8C61\u7684OSD\uFF0C\u7136\u540E\u5C06\u5BF9\u8C61\u5B58\u50A8\u5230OSD\u4E2D\u5BF9\u5E94\u7684\u4F4D\u7F6E\u3002</p><p>(1) **\u5BF9\u8C61\u5230PG\u7684\u6620\u5C04\u3002**PG(PlacementGroup)\u662F\u5BF9\u8C61\u7684\u903B\u8F91\u96C6\u5408\u3002PG\u662F\u7CFB\u7EDF\u5411OSD\u8282\u70B9\u5206\u53D1\u6570\u636E\u7684\u57FA\u672C\u5355\u4F4D\uFF0C\u76F8\u540CPG\u91CC\u7684\u5BF9\u8C61\u5C06\u88AB\u5206\u53D1\u5230\u76F8\u540C\u7684OSD\u8282\u70B9\u4E2D(\u4E00\u4E2A\u4E3BOSD\u8282\u70B9\u591A\u4E2A\u5907\u4EFDOSD\u8282\u70B9)\u3002\u5BF9\u8C61\u7684PG\u662F\u7531\u5BF9\u8C61ID\u53F7\u901A\u8FC7Hash\u7B97\u6CD5\uFF0C\u7ED3\u5408\u5176\u4ED6\u4E00\u4E9B\u4FEE\u6B63\u53C2\u6570\u5F97\u5230\u7684\u3002</p><p>(2) **PG\u5230\u76F8\u5E94\u7684OSD\u7684\u6620\u5C04\u3002**RADOS\u7CFB\u7EDF\u5229\u7528\u76F8\u5E94\u7684\u54C8\u5E0C\u7B97\u6CD5\u6839\u636E\u7CFB\u7EDF\u5F53\u524D\u7684\u72B6\u6001\u4EE5\u53CAPG\u7684ID\u53F7\uFF0C\u5C06\u5404\u4E2APG\u5206\u53D1\u5230OSD\u96C6\u7FA4\u4E2D\u3002OSD\u96C6\u7FA4\u662F\u6839\u636E\u7269\u7406\u8282\u70B9\u7684\u5BB9\u9519\u533A\u57DF(\u6BD4\u5982\u673A\u67B6\u3001\u673A\u623F\u7B49)\u6765\u8FDB\u884C\u5212\u5206\u7684\u3002</p><p>Ceph\u4E2D\u7684OSD\u8282\u70B9\u5C06\u6240\u6709\u7684\u5BF9\u8C61\u5B58\u50A8\u5728\u4E00\u4E2A\u6CA1\u6709\u5206\u5C42\u548C\u76EE\u5F55\u7684\u7EDF\u4E00\u7684\u547D\u540D\u7A7A\u95F4\u4E2D\u3002<strong>\u6BCF\u4E2A\u5BF9\u8C61\u90FD\u5305\u542B\u4E00\u4E2AID\u53F7\u3001\u82E5\u5E72\u4E8C\u8FDB\u5236\u6570\u636E\u4EE5\u53CA\u76F8\u5E94\u7684\u5143\u6570\u636E\u3002</strong></p><p>ID\u53F7\u5728\u6574\u4E2A\u5B58\u50A8\u96C6\u7FA4\u4E2D\u662F\u552F\u4E00\u7684\uFF1B\u5143\u6570\u636E\u6807\u8BC6\u4E86\u6240\u5B58\u50A8\u6570\u636E\u7684\u5C5E\u6027\u3002\u4E00\u4E2A\u5BF9\u8C61\u5728OSD\u8282\u70B9\u4E2D\u7684\u5B58\u50A8\u65B9\u5F0F\u5927\u81F4\u5982\u56FE\u6240\u793A\u3002</p><p><img src="https://cdn.jsdelivr.net/gh/AGou-ops/images/2020/image-20190623224819249.png" alt="image-20190623224819249"></p><p>\u800C\u5BF9\u5B58\u50A8\u6570\u636E\u7684\u8BED\u4E49\u89E3\u91CA\u5B8C\u5168\u4EA4\u7ED9\u76F8\u5E94\u7684\u5BA2\u6237\u7AEF\u6765\u5B8C\u6210\uFF0C\u6BD4\u5982\uFF0CCeph FS\u5BA2\u6237\u7AEF\u5C06\u6587\u4EF6\u5143\u6570\u636E(\u6BD4\u5982\u6240\u6709\u8005\u3001\u521B\u5EFA\u65E5\u671F\u3001\u4FEE\u6539\u65E5\u671F\u7B49)\u4F5C\u4E3A\u5BF9\u8C61\u5C5E\u6027\u5B58\u50A8\u5728Ceph\u4E2D\u3002</p><h3 id="crush-\u7B97\u6CD5" tabindex="-1">CRUSH \u7B97\u6CD5 <a class="header-anchor" href="#crush-\u7B97\u6CD5" aria-hidden="true">#</a></h3><p><strong>Controlled Replication Under Scalable Hashing \u53EF\u6269\u5C55\u54C8\u5E0C\u7B97\u6CD5\u7684\u53EF\u63A7\u590D\u5236</strong></p><p><strong>PG\u5230OSD\u7684\u6620\u5C04\u7684\u8FC7\u7A0B\u7B97\u6CD5\u53EB\u505Acrush \u7B97\u6CD5</strong></p><p>Ceph\u4F5C\u4E3A\u4E00\u4E2A\u9AD8\u53EF\u7528\u3001\u9AD8\u6027\u80FD\u7684\u5BF9\u8C61\u5B58\u50A8\u7CFB\u7EDF\uFF0C\u5176\u6570\u636E\u8BFB\u53D6\u53CA\u5199\u5165\u65B9\u5F0F\u662F\u4FDD\u8BC1\u5176\u9AD8\u53EF\u7528\u6027\u53CA\u9AD8\u6027\u80FD\u7684\u91CD\u8981\u624B\u6BB5\u3002\u5BF9\u4E8E\u5DF2\u77E5\u7684\u6570\u636E\u5BF9\u8C61\uFF0CCeph\u901A\u8FC7\u4F7F\u7528CRUSH(ControlledReplication Under Scalable Hashing)\u7B97\u6CD5\u8BA1\u7B97\u51FA\u5176\u5728Ceph\u96C6\u7FA4\u4E2D\u7684\u4F4D\u7F6E\uFF0C\u7136\u540E\u76F4\u63A5\u4E0E\u5BF9\u5E94\u7684OSD\u8BBE\u5907\u8FDB\u884C\u4EA4\u4E92\uFF0C\u8FDB\u884C\u6570\u636E\u8BFB\u53D6\u6216\u8005\u5199\u5165\u3002</p><h4 id="\u5199\u5165\u6570\u636E" tabindex="-1">\u5199\u5165\u6570\u636E <a class="header-anchor" href="#\u5199\u5165\u6570\u636E" aria-hidden="true">#</a></h4><p>\u4F8B\u5982\u5176\u5199\u5165\u6570\u636E\u7684\u5176\u4E3B\u8981\u8FC7\u7A0B\u5982\u56FE\u6240\u793A\u3002</p><p><img src="https://cdn.jsdelivr.net/gh/AGou-ops/images/2020/image-20190623225350763.png" alt="image-20190623225350763"></p><p>\u9996\u5148\uFF0C\u5BA2\u6237\u7AEF\u83B7\u53D6Ceph\u5B58\u50A8\u7CFB\u7EDF\u7684\u72B6\u6001\u4FE1\u606FCluster Map\uFF0C\u7136\u540E\u6839\u636E\u72B6\u6001\u4FE1\u606F\u4EE5\u53CA\u5C06\u8981\u5199\u5165\u7684Pool\u7684CRUSH\u76F8\u5173\u4FE1\u606F\uFF0C\u83B7\u53D6\u5230\u6570\u636E\u5C06\u8981\u5199\u5165\u7684OSD\uFF0C\u6700\u540EOSD\u5C06\u6570\u636E\u5199\u5165\u5230\u5176\u4E2D\u76F8\u5E94\u7684\u5B58\u50A8\u4F4D\u7F6E\u3002\u5176\u4E2D\u76F8\u5173\u6982\u5FF5\u7684\u89E3\u91CA\u5982\u4E0B\uFF1A</p><p>(1) \u96C6\u7FA4\u5730\u56FE(Cluster Map)\uFF1ACeph\u4F9D\u8D56\u4E8E\u5BA2\u6237\u7AEF\u4EE5\u53CAOSD\u8FDB\u7A0B\u4E2D\u4FDD\u5B58\u6709\u6574\u4E2A\u96C6\u7FA4\u76F8\u5173\u7684\u62D3\u6251\u4FE1\u606F\uFF0C\u6765\u5B9E\u73B0\u96C6\u7FA4\u7684\u7BA1\u7406\u548C\u6570\u636E\u7684\u8BFB\u5199\u3002\u6574\u4E2A\u96C6\u7FA4\u76F8\u5173\u7684\u62D3\u6251\u4FE1\u606F\u5C31\u79F0\u4E4B\u4E3A\u201CCluster Map\u201D\u3002Cluster Map\u4E3B\u8981\u4FDD\u5B58Monitor\u96C6\u7FA4\u3001OSD\u96C6\u7FA4\u3001MDS\u96C6\u7FA4\u7B49\u76F8\u5173\u7684\u62D3\u6251\u7ED3\u6784\u4FE1\u606F\u4EE5\u53CA\u72B6\u6001\u4FE1\u606F\u3002</p><p>(2) \u5B58\u50A8\u6C60(pool)\uFF1A\u662F\u5BF9Ceph\u96C6\u7FA4\u8FDB\u884C\u7684\u903B\u8F91\u5212\u5206\uFF0C\u4E3B\u8981\u8BBE\u7F6E\u5176\u4E2D\u5B58\u50A8\u5BF9\u8C61\u7684\u6743\u9650\u3001\u5907\u4EFD\u6570\u76EE\u3001PG\u6570\u4EE5\u53CACRUSH\u89C4\u5219\u7B49\u5C5E\u6027\u3002</p><p>\u5728\u4F20\u7EDF\u7684\u5B58\u50A8\u7CFB\u7EDF\u4E2D\uFF0C\u8981\u67E5\u627E\u6570\u636E\u901A\u5E38\u662F\u4F9D\u8D56\u4E8E\u67E5\u627E\u7CFB\u7EDF\u7684\u7684\u6587\u4EF6\u7D22\u5F15\u8868\u627E\u5230\u5BF9\u5E94\u7684\u6570\u636E\u5728\u78C1\u76D8\u4E2D\u7684\u4F4D\u7F6E\u3002\u800C\u5728Ceph\u5BF9\u8C61\u5B58\u50A8\u7CFB\u7EDF\u4E2D\uFF0C\u5BA2\u6237\u7AEF\u4E0EOSD\u8282\u70B9\u90FD\u4F7F\u7528CRUSH\u7B97\u6CD5\u6765\u9AD8\u6548\u7684\u8BA1\u7B97\u6240\u5B58\u50A8\u6570\u636E\u7684\u76F8\u5173\u4FE1\u606F\u3002\u76F8\u5BF9\u4E8E\u4F20\u7EDF\u7684\u65B9\u5F0F\uFF0CCRUSH\u63D0\u4F9B\u4E86\u4E00\u79CD\u66F4\u597D\u7684\u6570\u636E\u7BA1\u7406\u673A\u5236\uFF0C\u5B83\u80FD\u591F\u5C06\u6570\u636E\u7BA1\u7406\u7684\u5927\u90E8\u5206\u5DE5\u4F5C\u90FD\u5206\u914D\u7ED9\u5BA2\u6237\u7AEF\u548COSD\u8282\u70B9\uFF0C\u8FD9\u6837\u4E3A\u96C6\u7FA4\u7684\u6269\u5927\u548C\u5B58\u50A8\u5BB9\u91CF\u7684\u52A8\u6001\u6269\u5C55\u5E26\u6765\u4E86\u5F88\u5927\u7684\u65B9\u4FBF\u3002CRUSH\u662F\u4E00\u79CD\u4F2A\u968F\u673A\u6570\u636E\u5206\u5E03\u7B97\u6CD5\uFF0C\u5B83\u80FD\u591F\u5728\u5177\u6709\u5C42\u7EA7\u7ED3\u6784\u7684\u5B58\u50A8\u96C6\u7FA4\u4E2D\u6709\u6548\u7684\u5206\u53D1\u5BF9\u8C61\u526F\u672C\u3002</p><p>CRUSH\u7B97\u6CD5\u662F\u6839\u636E\u96C6\u7FA4\u4E2D\u5B58\u50A8\u8BBE\u5907\u7684\u6743\u91CD\u6765\u8FDB\u884C\u6570\u636E\u5206\u53D1\u7684\uFF0C\u6570\u636E\u5728\u5404\u4E2AOSD\u8BBE\u5907\u4E0A\u8FD1\u4F3C\u5747\u5300\u6982\u7387\u5206\u5E03\u3002CRUSH\u4E2D\uFF0C\u6570\u636E\u5728\u5B58\u50A8\u8BBE\u5907\u4E0A\u7684\u5206\u5E03\u662F\u6839\u636E\u4E00\u4E2A\u5C42\u6B21\u5316\u7684\u96C6\u7FA4\u5730\u56FE(Cluster Map)\u6765\u51B3\u5B9A\u7684\u3002\u96C6\u7FA4\u5730\u56FE\u662F\u7531\u53EF\u7528\u7684\u5B58\u50A8\u8D44\u6E90\u4EE5\u53CA\u7531\u8FD9\u4E9B\u5B58\u50A8\u8D44\u6E90\u6784\u5EFA\u7684\u96C6\u7FA4\u7684\u903B\u8F91\u5355\u5143\u7EC4\u6210\u3002\u6BD4\u5982\u4E00\u4E2ACeph\u5B58\u50A8\u96C6\u7FA4\u7684\u96C6\u7FA4\u5730\u56FE\u7684\u7ED3\u6784\u53EF\u80FD\u662F\u4E00\u6392\u6392\u5927\u578B\u7684\u673A\u67DC\uFF0C\u6BCF\u4E2A\u673A\u67DC\u4E2D\u5305\u542B\u591A\u4E2A\u673A\u67B6\uFF0C\u6BCF\u4E2A\u673A\u67B6\u4E2D\u653E\u7F6E\u7740\u5B58\u50A8\u8BBE\u5907\u3002\u6570\u636E\u5206\u53D1\u7B56\u7565\u662F\u4F9D\u7167\u6570\u636E\u7684\u5B58\u653E\u89C4\u5219(placement rules)\u8FDB\u884C\u5B9A\u4E49\u7684\uFF0C\u5B58\u653E\u89C4\u5219\u662F\u6307\u6570\u636E\u5728\u5907\u4EFD\u4EE5\u53CA\u5B58\u653E\u65F6\u5E94\u8BE5\u9075\u5FAA\u7684\u76F8\u5173\u7EA6\u5B9A\uFF0C\u6BD4\u5982\u7EA6\u5B9A\u4E00\u4E2A\u5BF9\u8C61\u7684\u4E09\u4E2A\u526F\u672C\u5E94\u8BE5\u5B58\u653E\u5728\u4E09\u4E2A\u4E0D\u540C\u7684\u7269\u7406\u673A\u67B6\u4E0A\u3002</p><p>\u7ED9\u5B9A\u4E00\u4E2A\u503C\u4E3Ax\u7684\u6574\u6570\uFF0CCRUSH\u5C06\u6839\u636E\u76F8\u5E94\u7684\u7B56\u7565\u8FDB\u884C\u54C8\u5E0C\u8BA1\u7B97\u8F93\u51FA\u4E00\u4E2A\u6709\u5E8F\u7684\u5305\u542Bn\u4E2A\u5B58\u50A8\u76EE\u6807\u7684\u5E8F\u5217\uFF1A</p><p>CRUSH(x)=(osd1,osd2,osd3,osdn)</p><p>CRUSH\u5229\u7528\u5065\u58EE\u7684\u54C8\u5E0C\u51FD\u6570\uFF0C\u5176\u5F97\u5230\u7684\u7ED3\u679C\u4F9D\u8D56\u4E8E\u96C6\u7FA4\u5730\u56FECluster Map\u3001\u5B58\u653E\u89C4\u5219(placementmles)\u548C\u8F93\u5165x\u3002\u5E76\u4E14CRUSH\u662F\u4E00\u4E2A\u4F2A\u968F\u673A\u7B97\u6CD5\uFF0C\u4E24\u4E2A\u76F8\u4F3C\u7684\u8F93\u5165\u5F97\u5230\u7684\u7ED3\u679C\u662F\u6CA1\u6709\u660E\u663E\u7684\u76F8\u5173\u6027\u7684\u3002\u8FD9\u6837\u5C31\u80FD\u786E\u4FDDCeph\u4E2D\u6570\u636E\u5206\u5E03\u662F\u968F\u673A\u5747\u5300\u7684\u3002</p><p>\u5728\u5206\u5E03\u5F0F\u5B58\u50A8\u7CFB\u7EDF\u4E2D\u6BD4\u8F83\u5173\u6CE8\u7684\u4E00\u70B9\u662F\u5982\u4F55\u4F7F\u5F97\u6570\u636E\u80FD\u591F\u5206\u5E03\u5F97\u66F4\u52A0\u5747\u8861\uFF0C\u5E38\u89C1\u7684\u6570\u636E\u5206\u5E03\u7B97\u6CD5\u6709\u4E00\u81F4\u6027Hash\u548CCeph\u7684Crush\u7B97\u6CD5\u3002<strong>Crush\u662F\u4E00\u79CD\u4F2A\u968F\u673A\u7684\u63A7\u5236\u6570\u636E\u5206\u5E03\u3001\u590D\u5236\u7684\u7B97\u6CD5</strong>\uFF0CCeph\u662F\u4E3A\u5927\u89C4\u6A21\u5206\u5E03\u5F0F\u5B58\u50A8\u800C\u8BBE\u8BA1\u7684\uFF0C\u6570\u636E\u5206\u5E03\u7B97\u6CD5\u5FC5\u987B\u80FD\u591F\u6EE1\u8DB3\u5728\u5927\u89C4\u6A21\u7684\u96C6\u7FA4\u4E0B\u6570\u636E\u4F9D\u7136\u80FD\u591F\u5FEB\u901F\u7684\u51C6\u786E\u7684\u8BA1\u7B97\u5B58\u653E\u4F4D\u7F6E\uFF0C\u540C\u65F6\u80FD\u591F\u5728\u786C\u4EF6\u6545\u969C\u6216\u6269\u5C55\u786C\u4EF6\u8BBE\u5907\u65F6\u505A\u5230\u5C3D\u53EF\u80FD\u5C0F\u7684\u6570\u636E\u8FC1\u79FB\uFF0CCeph\u7684CRUSH\u7B97\u6CD5\u5C31\u662F\u7CBE\u5FC3\u4E3A\u8FD9\u4E9B\u7279\u6027\u8BBE\u8BA1\u7684\uFF0C\u53EF\u4EE5\u8BF4CRUSH\u7B97\u6CD5\u4E5F\u662FCeph\u7684\u6838\u5FC3\u4E4B\u4E00\u3002</p><p>\u5728\u8BF4\u660ECRUSH\u7B97\u6CD5\u7684\u57FA\u672C\u539F\u7406\u4E4B\u524D\uFF0C\u5148\u4ECB\u7ECD\u51E0\u4E2A\u6982\u5FF5\u548C\u5B83\u4EEC\u4E4B\u95F4\u7684\u5173\u7CFB\u3002</p><p><strong>\u5B58\u50A8\u6570\u636E\u4E0Eobject\u7684\u5173\u7CFB</strong>\uFF1A</p><p>\u5F53\u7528\u6237\u8981\u5C06\u6570\u636E\u5B58\u50A8\u5230Ceph\u96C6\u7FA4\u65F6\uFF0C\u5B58\u50A8\u6570\u636E\u90FD\u4F1A\u88AB\u5206\u5272\u6210\u591A\u4E2A<code>object</code>\uFF0C\u6BCF\u4E2Aobject\u90FD\u6709\u4E00\u4E2Aobject id\uFF0C\u6BCF\u4E2Aobject\u7684\u5927\u5C0F\u662F\u53EF\u4EE5\u8BBE\u7F6E\u7684\uFF0C\u9ED8\u8BA4\u662F4MB\uFF0C<code>object\u53EF\u4EE5\u770B\u6210\u662FCeph\u5B58\u50A8\u7684\u6700\u5C0F\u5B58\u50A8\u5355\u5143</code>\u3002</p><p><strong>object\u4E0Epg\u7684\u5173\u7CFB\uFF1A</strong></p><p>\u7531\u4E8Eobject\u7684\u6570\u91CF\u5F88\u591A\uFF0C\u6240\u4EE5Ceph\u5F15\u5165\u4E86pg\u7684\u6982\u5FF5\u7528\u4E8E\u7BA1\u7406object\uFF0C\u6BCF\u4E2Aobject\u6700\u540E\u90FD\u4F1A\u901A\u8FC7hash\u8BA1\u7B97\u6620\u5C04\u5230\u67D0\u4E2Apg\u4E2D\uFF0C<strong>\u4E00\u4E2Apg\u53EF\u4EE5\u5305\u542B\u591A\u4E2Aobject</strong>\u3002</p><p><strong>pg\u4E0Eosd\u7684\u5173\u7CFB\uFF1A</strong></p><p>pg\u4E5F\u9700\u8981\u901A\u8FC7CRUSH\u8BA1\u7B97\u6620\u5C04\u5230osd\u4E2D\u53BB\u5B58\u50A8\uFF0C\u5982\u679C\u662F\u4E8C\u526F\u672C\u7684\uFF0C\u5219\u6BCF\u4E2Apg\u90FD\u4F1A\u6620\u5C04\u5230\u4E8C\u4E2Aosd\uFF0C\u6BD4\u5982[osd.1,osd.2]\uFF0C\u90A3\u4E48osd.1\u662F\u5B58\u653E\u8BE5pg\u7684\u4E3B\u526F\u672C\uFF0Cosd.2\u662F\u5B58\u653E\u8BE5pg\u7684\u4ECE\u526F\u672C\uFF0C\u4FDD\u8BC1\u4E86\u6570\u636E\u7684\u5197\u4F59\u3002</p><p><strong>pg\u548Cpgp\u7684\u5173\u7CFB\uFF1A</strong></p><p>pg\u662F\u7528\u6765\u5B58\u653Eobject\u7684\uFF0Cpgp\u76F8\u5F53\u4E8E\u662Fpg\u5B58\u653Eosd\u7684\u4E00\u79CD\u6392\u5217\u7EC4\u5408\uFF0C\u6211\u4E3E\u4E2A\u4F8B\u5B50\uFF0C\u6BD4\u5982\u67093\u4E2Aosd\uFF0Cosd.1\u3001osd.2\u548Cosd.3\uFF0C\u526F\u672C\u6570\u662F2\uFF0C\u5982\u679Cpgp\u7684\u6570\u76EE\u4E3A1\uFF0C\u90A3\u4E48pg\u5B58\u653E\u7684osd\u7EC4\u5408\u5C31\u53EA\u6709\u4E00\u79CD\uFF0C\u53EF\u80FD\u662F[osd.1,osd.2]\uFF0C\u90A3\u4E48\u6240\u6709\u7684pg\u4E3B\u4ECE\u526F\u672C\u5206\u522B\u5B58\u653E\u5230osd.1\u548Cosd.2\uFF0C\u5982\u679Cpgp\u8BBE\u4E3A2\uFF0C\u90A3\u4E48\u5176osd\u7EC4\u5408\u53EF\u4EE5\u4E24\u79CD\uFF0C\u53EF\u80FD\u662F[osd.1,osd.2]\u548C[osd.1,osd.3]\uFF0C\u662F\u4E0D\u662F\u5F88\u50CF\u6211\u4EEC\u9AD8\u4E2D\u6570\u5B66\u5B66\u8FC7\u7684\u6392\u5217\u7EC4\u5408\uFF0Cpgp\u5C31\u662F\u4EE3\u8868\u8FD9\u4E2A\u610F\u601D\u3002\u4E00\u822C\u6765\u8BF4\u5E94\u8BE5\u5C06pg\u548Cpgp\u7684\u6570\u91CF\u8BBE\u7F6E\u4E3A\u76F8\u7B49\u3002<a href="https://www.cnblogs.com/luohaixian/p/8087591.html" target="_blank" rel="noopener noreferrer">\u4E00\u4E2A\u5B9E\u9A8C</a></p><p>\u901A\u8FC7\u5B9E\u9A8C\u603B\u7ED3\uFF1A \uFF081\uFF09PG\u662F\u6307\u5B9A\u5B58\u50A8\u6C60\u5B58\u50A8\u5BF9\u8C61\u7684\u76EE\u5F55\u6709\u591A\u5C11\u4E2A\uFF0CPGP\u662F\u5B58\u50A8\u6C60PG\u7684OSD\u5206\u5E03\u7EC4\u5408\u4E2A\u6570 \uFF082\uFF09PG\u7684\u589E\u52A0\u4F1A\u5F15\u8D77PG\u5185\u7684\u6570\u636E\u8FDB\u884C\u5206\u88C2\uFF0C\u5206\u88C2\u76F8\u540C\u7684OSD\u4E0A\u65B0\u751F\u6210\u7684PG\u5F53\u4E2D \uFF083\uFF09PGP\u7684\u589E\u52A0\u4F1A\u5F15\u8D77\u90E8\u5206PG\u7684\u5206\u5E03\u8FDB\u884C\u53D8\u5316\uFF0C\u4F46\u662F\u4E0D\u4F1A\u5F15\u8D77PG\u5185\u5BF9\u8C61\u7684\u53D8\u52A8</p><p><strong>pg\u548Cpool\u7684\u5173\u7CFB</strong>\uFF1Apool\u4E5F\u662F\u4E00\u4E2A\u903B\u8F91\u5B58\u50A8\u6982\u5FF5\uFF0C\u6211\u4EEC\u521B\u5EFA\u5B58\u50A8\u6C60pool\u7684\u65F6\u5019\uFF0C\u90FD\u9700\u8981\u6307\u5B9Apg\u548Cpgp\u7684\u6570\u91CF\uFF0C\u903B\u8F91\u4E0A\u6765\u8BF4pg\u662F\u5C5E\u4E8E\u67D0\u4E2A\u5B58\u50A8\u6C60\u7684\uFF0C\u5C31\u6709\u70B9\u50CFobject\u662F\u5C5E\u4E8E\u67D0\u4E2Apg\u7684\u3002</p><p>\u4EE5\u4E0B\u8FD9\u4E2A\u56FE\u8868\u660E\u4E86\u5B58\u50A8\u6570\u636E\uFF0Cobject\u3001pg\u3001pool\u3001osd\u3001\u5B58\u50A8\u78C1\u76D8\u7684\u5173\u7CFB</p><p><img src="https://cdn.jsdelivr.net/gh/AGou-ops/images/2020/ceph-storage.png" alt="ceph"></p><p>\u672C\u8D28\u4E0ACRUSH\u7B97\u6CD5\u662F\u6839\u636E\u5B58\u50A8\u8BBE\u5907\u7684\u6743\u91CD\u6765\u8BA1\u7B97\u6570\u636E\u5BF9\u8C61\u7684\u5206\u5E03\u7684\uFF0C\u6743\u91CD\u7684\u8BBE\u8BA1\u53EF\u4EE5\u6839\u636E\u8BE5\u78C1\u76D8\u7684\u5BB9\u91CF\u548C\u8BFB\u5199\u901F\u5EA6\u6765\u8BBE\u7F6E\uFF0C\u6BD4\u5982\u6839\u636E\u5BB9\u91CF\u5927\u5C0F\u53EF\u4EE5\u5C061T\u7684\u786C\u76D8\u8BBE\u5907\u6743\u91CD\u8BBE\u4E3A1\uFF0C2T\u7684\u5C31\u8BBE\u4E3A2\uFF0C\u5728\u8BA1\u7B97\u8FC7\u7A0B\u4E2D\uFF0CCRUSH\u662F\u6839\u636ECluster Map\u3001\u6570\u636E\u5206\u5E03\u7B56\u7565\u548C\u4E00\u4E2A\u968F\u673A\u6570\u5171\u540C\u51B3\u5B9A\u6570\u7EC4\u6700\u7EC8\u7684\u5B58\u50A8\u4F4D\u7F6E\u7684\u3002</p><p>Cluster Map\u91CC\u7684\u5185\u5BB9\u4FE1\u606F\u5305\u62EC\u5B58\u50A8\u96C6\u7FA4\u4E2D\u53EF\u7528\u7684\u5B58\u50A8\u8D44\u6E90\u53CA\u5176\u76F8\u4E92\u4E4B\u95F4\u7684\u7A7A\u95F4\u5C42\u6B21\u5173\u7CFB\uFF0C\u6BD4\u5982\u96C6\u7FA4\u4E2D\u6709\u591A\u5C11\u4E2A\u652F\u67B6\uFF0C\u6BCF\u4E2A\u652F\u67B6\u4E2D\u6709\u591A\u5C11\u4E2A\u670D\u52A1\u5668\uFF0C\u6BCF\u4E2A\u670D\u52A1\u5668\u6709\u591A\u5C11\u5757\u78C1\u76D8\u7528\u4EE5OSD\u7B49\u3002</p><p>\u6570\u636E\u5206\u5E03\u7B56\u7565\u662F\u6307\u53EF\u4EE5\u901A\u8FC7Ceph\u7BA1\u7406\u8005\u901A\u8FC7\u914D\u7F6E\u4FE1\u606F\u6307\u5B9A\u6570\u636E\u5206\u5E03\u7684\u4E00\u4E9B\u7279\u70B9\uFF0C\u6BD4\u5982\u7BA1\u7406\u8005\u914D\u7F6E\u7684\u6545\u969C\u57DF\u662FHost\uFF0C\u4E5F\u5C31\u610F\u5473\u7740\u5F53\u6709\u4E00\u53F0Host\u8D77\u4E0D\u6765\u65F6\uFF0C\u6570\u636E\u80FD\u591F\u4E0D\u4E22\u5931\uFF0CCRUSH\u53EF\u4EE5\u901A\u8FC7\u5C06\u6BCF\u4E2Apg\u7684\u4E3B\u4ECE\u526F\u672C\u5206\u522B\u5B58\u653E\u5728\u4E0D\u540CHost\u7684OSD\u4E0A\u5373\u53EF\u8FBE\u5230\uFF0C\u4E0D\u5355\u5355\u53EF\u4EE5\u6307\u5B9AHost\uFF0C\u8FD8\u53EF\u4EE5\u6307\u5B9A\u673A\u67B6\u7B49\u6545\u969C\u57DF\uFF0C\u9664\u4E86\u6545\u969C\u57DF\uFF0C\u8FD8\u6709\u9009\u62E9\u6570\u636E\u5197\u4F59\u7684\u65B9\u5F0F\uFF0C\u6BD4\u5982\u526F\u672C\u6570\u6216\u7EA0\u5220\u7801\u3002</p><h3 id="\u53C2\u8003" tabindex="-1">\u53C2\u8003 <a class="header-anchor" href="#\u53C2\u8003" aria-hidden="true">#</a></h3><p><a href="http://www.voidcn.com/article/p-njiezowl-beg.html" target="_blank" rel="noopener noreferrer">Ceph\u6574\u4F53\u67B6\u6784</a></p><p><a href="https://www.cnblogs.com/yangxiaoyi/p/7795274.html" target="_blank" rel="noopener noreferrer">ceph\u5206\u5E03\u5F0F\u5B58\u50A8</a></p><p><a href="https://www.cnblogs.com/luohaixian/p/8087591.html" target="_blank" rel="noopener noreferrer">Ceph\u57FA\u7840\u77E5\u8BC6\u548C\u57FA\u7840\u67B6\u6784\u8BA4\u8BC6</a></p><p><a href="https://blog.51cto.com/tasnrh/1761437" target="_blank" rel="noopener noreferrer">Ceph\u5206\u5E03\u5F0F\u5B58\u50A8\u7CFB\u7EDF</a></p><blockquote><p>\u6587\u7AE0\u6765\u6E90\uFF1A<a href="https://llussy.github.io/2019/08/17/ceph-architecture" target="_blank" rel="noopener noreferrer">https://llussy.github.io/2019/08/17/ceph-architecture</a></p></blockquote>',78),s=[t];function h(n,i,l,d,c,g){return p(),o("div",null,s)}var u=e(a,[["render",h]]);export{C as __pageData,u as default};
