import{_ as s,o as a,c as o,a as e}from"./app.1e6146c3.js";const m=JSON.parse('{"title":"RabbitMQ Mirror Queue","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u955C\u50CF\u961F\u5217\u7684\u57FA\u672C\u6982\u5FF5\u4E0E\u4ECB\u7ECD","slug":"\u955C\u50CF\u961F\u5217\u7684\u57FA\u672C\u6982\u5FF5\u4E0E\u4ECB\u7ECD"},{"level":2,"title":"Rabbitmq Mirror Queue \u642D\u5EFA","slug":"rabbitmq-mirror-queue-\u642D\u5EFA"},{"level":2,"title":"\u5176\u4ED6","slug":"\u5176\u4ED6"},{"level":2,"title":"\u53C2\u8003\u8D44\u6599","slug":"\u53C2\u8003\u8D44\u6599"}],"relativePath":"LinuxBasics/RabbitMQ/RabbitMQ Mirror Queue.md","lastUpdated":1657272051000}'),t={name:"LinuxBasics/RabbitMQ/RabbitMQ Mirror Queue.md"},n=e(`<h1 id="rabbitmq-mirror-queue" tabindex="-1">RabbitMQ Mirror Queue <a class="header-anchor" href="#rabbitmq-mirror-queue" aria-hidden="true">#</a></h1><h2 id="\u955C\u50CF\u961F\u5217\u7684\u57FA\u672C\u6982\u5FF5\u4E0E\u4ECB\u7ECD" tabindex="-1">\u955C\u50CF\u961F\u5217\u7684\u57FA\u672C\u6982\u5FF5\u4E0E\u4ECB\u7ECD <a class="header-anchor" href="#\u955C\u50CF\u961F\u5217\u7684\u57FA\u672C\u6982\u5FF5\u4E0E\u4ECB\u7ECD" aria-hidden="true">#</a></h2><p>\u961F\u5217\u8FDB\u7A0B\u53CA\u5176\u5185\u5BB9\u4EC5\u4EC5\u7EF4\u6301\u5728\u5355\u4E2A\u8282\u70B9\u4E4B\u4E0A\uFF0C\u6240\u4EE5\u4E00\u4E2A\u8282\u70B9\u7684\u5931\u6548\u8868\u73B0\u4E3A\u5176\u5BF9\u5E94\u7684\u961F\u5217\u4E0D\u53EF\u7528\u3002</p><p>\u5F15\u5165\u955C\u50CF\u961F\u5217\uFF08<code>Mirror Queue</code>\uFF09\u7684\u673A\u5236\uFF0C\u53EF\u4EE5\u5C06\u961F\u5217\u955C\u50CF\u5230\u96C6\u7FA4\u4E2D\u7684\u5176\u4ED6<code> Broker</code> \u8282\u70B9\u4E4B\u4E0A\uFF0C\u6BCF\u4E00\u4E2A\u955C\u50CF\u961F\u5217\u90FD\u5305\u542B\u4E00\u4E2A<code>master</code>\u8282\u70B9\u548C\u591A\u4E2A<code>slave</code>\u8282\u70B9\uFF08\u4E3B\u4ECE\u7ED3\u6784\uFF09\uFF0C<strong>\u5982\u679C\u96C6\u7FA4\u4E2D\u7684\u4E00\u4E2A\u8282\u70B9\u5931\u6548\u4E86\uFF0C\u961F\u5217\u80FD\u591F\u81EA\u52A8\u5207\u6362\u5230\u955C\u50CF\u4E2D\u7684\u53E6\u4E00\u4E2A\u8282\u70B9\u4E0A\u4EE5\u4FDD\u8BC1\u670D\u52A1\u7684\u53EF\u7528\u6027\u3002</strong></p><p>\u7ED3\u6784\u56FE\u5982\u4E0B\u6240\u793A\uFF1A</p><p><img src="https://upload-images.jianshu.io/upload_images/5114528-121cd4bca546fff0.PNG?imageMogr2/auto-orient/strip%7CimageView2/2/w/280/format/webp" alt=""></p><p><strong>\u9664\u53D1\u9001\u6D88\u606F\uFF08<code>Basic.Publish</code>\uFF09\u5916\u7684\u6240\u6709\u52A8\u4F5C\u90FD\u53EA\u4F1A\u5411 master \u53D1\u9001\uFF0C\u7136\u540E\u518D\u7531master \u5C06\u547D\u4EE4\u6267\u884C\u7684\u7ED3\u679C\u5E7F\u64AD\u7ED9\u5404\u4E2A slave\u3002</strong></p><h2 id="rabbitmq-mirror-queue-\u642D\u5EFA" tabindex="-1">Rabbitmq Mirror Queue \u642D\u5EFA <a class="header-anchor" href="#rabbitmq-mirror-queue-\u642D\u5EFA" aria-hidden="true">#</a></h2><p>\u955C\u50CF\u961F\u5217\u7684\u914D\u7F6E\u4E3B\u8981\u662F\u901A\u8FC7\u6DFB\u52A0\u76F8\u5E94\u7684 Policy \u6765\u5B8C\u6210 \uFF1A</p><div class="language-bash"><span class="copy"></span><pre class="vp-code-dark"><code><span class="line"><span style="color:#758575;"># rabbitmqctl [--node &lt;node&gt;] [--longnames] [--quiet] set_policy [--vhost &lt;vhost&gt;] [--priority &lt;priority&gt;] [--apply-to &lt;apply-to&gt;] &lt;name&gt; &lt;pattern&gt; &lt;definition&gt;</span></span>
<span class="line"></span></code></pre><pre class="vp-code-light"><code><span class="line"><span style="color:#A0ADA0;"># rabbitmqctl [--node &lt;node&gt;] [--longnames] [--quiet] set_policy [--vhost &lt;vhost&gt;] [--priority &lt;priority&gt;] [--apply-to &lt;apply-to&gt;] &lt;name&gt; &lt;pattern&gt; &lt;definition&gt;</span></span>
<span class="line"></span></code></pre></div><p><code>definition </code>\u8981\u5305\u542B3\u4E2A\u90E8\u5206<code> ha-mode</code>\u3001<code> ha-params</code>\u3001 <code>ha-sync-mode</code></p><ul><li><strong><code>ha-mode</code></strong> \u6307\u660E\u955C\u50CF\u961F\u5217\u7684\u6A21\u5F0F\uFF0C\u6709\u6548\u503C\u4E3A all/exactly/nodes\u9ED8\u8BA4\u4E3A all <strong>all</strong> \u8868\u793A\u5728\u96C6\u7FA4\u4E2D\u6240\u6709\u7684\u8282\u70B9\u4E0A\u8FDB\u884C\u955C\u50CF <strong>exactly</strong> \u8868\u793A\u5728\u6307\u5B9A\u4E2A\u6570\u7684\u8282\u70B9\u4E0A\u8FDB\u884C\u955C\u50CF\uFF0C\u8282\u70B9\u4E2A\u6570\u7531 ha-params \u6307\u5B9A; <strong>nodes</strong> \u8868\u793A\u5728\u6307\u5B9A\u8282\u70B9\u4E0A\u8FDB\u884C\u955C\u50CF\uFF0C\u8282\u70B9\u540D\u79F0\u901Aha-params \u6307\u5B9A\uFF0C\u8282\u70B9\u7684\u540D\u79F0\u901A\u5E38\u7C7B\u4F3C\u4E8E rabbit@hostname \uFF0C\u53EF\u4EE5\u901A\u8FC7<code>rabbitmqctl cluster status </code>\u547D\u4EE4\u67E5\u770B\u5230</li><li><strong><code>ha-params</code></strong> \u4E0D\u540C\u7684 hamode \u914D\u7F6E\u4E2D\u9700\u8981\u7528\u5230\u7684\u53C2\u6570\u3002</li><li><strong><code>ha-sync-mode</code></strong> \u961F\u5217\u4E2D\u6D88\u606F\u7684\u540C\u6B65\u65B9\u5F0F\uFF0C\u6709\u6548\u503C\u4E3A automatic \u3001manual</li></ul><p><strong>\u547D\u4EE4\u6837\u4F8B</strong></p><ul><li><p>\u5BF9\u961F\u5217\u540D\u79F0\u4EE5<code> queue_</code>\u5F00\u5934\u7684\u6240\u6709\u961F\u5217\u8FDB\u884C\u955C\u50CF\uFF0C\u5E76\u5728\u96C6\u7FA4\u7684\u4E24\u4E2A\u8282\u70B9\u4E0A\u5B8C \u6210\u955C\u50CF</p><div class="language-"><span class="copy"></span><pre class="vp-code-dark"><code><span class="line"><span style="color:#dbd7ca;">rabbitmqctl set_policy --priority 0 --apply-to queues mirror_queue &quot;^queue_&quot; &#39;{&quot;ha-mode &quot;:&quot;exactly&quot;,&quot;ha-params&quot;\uFF1A2,&quot;ha-sync-mode&quot;:&quot;automatic&quot; }&#39;</span></span>
<span class="line"><span style="color:#dbd7ca;"></span></span></code></pre><pre class="vp-code-light"><code><span class="line"><span style="color:#393a34;">rabbitmqctl set_policy --priority 0 --apply-to queues mirror_queue &quot;^queue_&quot; &#39;{&quot;ha-mode &quot;:&quot;exactly&quot;,&quot;ha-params&quot;\uFF1A2,&quot;ha-sync-mode&quot;:&quot;automatic&quot; }&#39;</span></span>
<span class="line"><span style="color:#393a34;"></span></span></code></pre></div></li><li><p>\u5BF9\u961F\u5217\u540D\u79F0\u4EE5 <code>queue_</code> \u5F00\u5934\u7684\u6240\u6709\u961F\u5217\u8FDB\u884C\u955C\u50CF\uFF0C\u5E76\u5728\u96C6\u7FA4\u7684\u6240\u6709\u8282\u70B9\u4E0A\u5B8C \u6210\u955C\u50CF</p><div class="language-"><span class="copy"></span><pre class="vp-code-dark"><code><span class="line"><span style="color:#dbd7ca;">rabbitmqctl set_policy --priority 0 --apply-to queues mirror_queue &quot;^queue_&quot; &#39;{&quot;ha-mode&quot;:&quot;all&quot;,&quot;ha-sync-mode&quot;:&quot;automatic&quot;</span></span>
<span class="line"><span style="color:#dbd7ca;"></span></span></code></pre><pre class="vp-code-light"><code><span class="line"><span style="color:#393a34;">rabbitmqctl set_policy --priority 0 --apply-to queues mirror_queue &quot;^queue_&quot; &#39;{&quot;ha-mode&quot;:&quot;all&quot;,&quot;ha-sync-mode&quot;:&quot;automatic&quot;</span></span>
<span class="line"><span style="color:#393a34;"></span></span></code></pre></div></li></ul><p><code>rabbitmqctl set_policy ha-all &quot;^&quot; &#39;{&quot;ha-mode&quot;:&quot;all&quot;}&#39;</code>\u53EF\u4EE5\u628A\u961F\u5217\u8BBE\u7F6E\u4E3A\u955C\u50CF\u961F\u5217</p><p><strong>\u547D\u4EE4\u6267\u884C</strong></p><div class="language-bash"><span class="copy"></span><pre class="vp-code-dark"><code><span class="line"><span style="color:#858585;">[</span><span style="color:#DBD7CA;">root@master </span><span style="color:#CB7676;">~</span><span style="color:#858585;">]</span><span style="color:#D4976C;">\\#</span><span style="color:#DBD7CA;"> rabbitmqctl set_policy --priority 0 --apply-to queues mirror_queue </span><span style="color:#C98A7D;">&quot;^queue_&quot;</span><span style="color:#DBD7CA;"> </span><span style="color:#C98A7D;">&#39;{&quot;ha-mode&quot;:&quot;all&quot;,&quot;ha-sync-mode&quot;:&quot;automatic&quot;</span></span>
<span class="line"><span style="color:#C98A7D;">}&#39;</span></span>
<span class="line"><span style="color:#DBD7CA;">Setting policy </span><span style="color:#C98A7D;">&quot;mirror_queue&quot;</span><span style="color:#DBD7CA;"> </span><span style="color:#4D9375;">for</span><span style="color:#DBD7CA;"> pattern </span><span style="color:#C98A7D;">&quot;^queue_&quot;</span><span style="color:#DBD7CA;"> to </span><span style="color:#C98A7D;">&quot;{&quot;</span><span style="color:#DBD7CA;">ha-mode</span><span style="color:#C98A7D;">&quot;:&quot;</span><span style="color:#DBD7CA;">all</span><span style="color:#C98A7D;">&quot;,&quot;</span><span style="color:#DBD7CA;">ha-sync-mode</span><span style="color:#C98A7D;">&quot;:&quot;</span><span style="color:#DBD7CA;">automatic</span><span style="color:#C98A7D;">&quot;}&quot;</span><span style="color:#DBD7CA;"> with priority </span><span style="color:#C98A7D;">&quot;0&quot;</span><span style="color:#DBD7CA;"> </span><span style="color:#4D9375;">for</span><span style="color:#DBD7CA;"> vhost </span><span style="color:#C98A7D;">&quot;/&quot;</span><span style="color:#DBD7CA;"> ...</span></span>
<span class="line"></span></code></pre><pre class="vp-code-light"><code><span class="line"><span style="color:#8E8F8B;">[</span><span style="color:#393A34;">root@master </span><span style="color:#AB5959;">~</span><span style="color:#8E8F8B;">]</span><span style="color:#A65E2B;">\\#</span><span style="color:#393A34;"> rabbitmqctl set_policy --priority 0 --apply-to queues mirror_queue </span><span style="color:#B56959;">&quot;^queue_&quot;</span><span style="color:#393A34;"> </span><span style="color:#B56959;">&#39;{&quot;ha-mode&quot;:&quot;all&quot;,&quot;ha-sync-mode&quot;:&quot;automatic&quot;</span></span>
<span class="line"><span style="color:#B56959;">}&#39;</span></span>
<span class="line"><span style="color:#393A34;">Setting policy </span><span style="color:#B56959;">&quot;mirror_queue&quot;</span><span style="color:#393A34;"> </span><span style="color:#1C6B48;">for</span><span style="color:#393A34;"> pattern </span><span style="color:#B56959;">&quot;^queue_&quot;</span><span style="color:#393A34;"> to </span><span style="color:#B56959;">&quot;{&quot;</span><span style="color:#393A34;">ha-mode</span><span style="color:#B56959;">&quot;:&quot;</span><span style="color:#393A34;">all</span><span style="color:#B56959;">&quot;,&quot;</span><span style="color:#393A34;">ha-sync-mode</span><span style="color:#B56959;">&quot;:&quot;</span><span style="color:#393A34;">automatic</span><span style="color:#B56959;">&quot;}&quot;</span><span style="color:#393A34;"> with priority </span><span style="color:#B56959;">&quot;0&quot;</span><span style="color:#393A34;"> </span><span style="color:#1C6B48;">for</span><span style="color:#393A34;"> vhost </span><span style="color:#B56959;">&quot;/&quot;</span><span style="color:#393A34;"> ...</span></span>
<span class="line"></span></code></pre></div><p>\u5728 web \u7BA1\u7406\u63A5\u53E3\u4E2D\u521B\u5EFA\u961F\u5217\uFF0C\u540D\u79F0\u524D\u7F00\u4E3A<code>queue</code>\uFF1A</p><p><img src="https://agou-images.oss-cn-qingdao.aliyuncs.com/blog-images/rabbitmq/rabbitmq_mirror_queue.png" alt=""></p><p>\u5728\u56FE\u4E2D\u770B\u5230<code>Node--&gt;rabbitmq@master</code>\u540E\u9762\u6709\u4E2A<code>+2</code>\u5B57\u6837\uFF0C\u5C31\u8868\u793A\u5DF2\u7ECF\u540C\u6B65\u5230<code>node01</code>\u548C<code>node02</code>\u8282\u70B9\uFF0C\u5C06\u9F20\u6807\u79FB\u52A8\u81F3\u6B64\u53EF\u4EE5\u770B\u5230\u63D0\u793A\u4FE1\u606F\uFF1A<code>Synchronised mirrors: rabbit@node02,rabbit@node01</code>\u3002</p><p><strong>\u6D4B\u8BD5<code> master</code> \u8282\u70B9\u5B95\u673A</strong></p><div class="language-bash"><span class="copy"></span><pre class="vp-code-dark"><code><span class="line"><span style="color:#758575;"># \u5728 master \u8282\u70B9\u4E0A\u4EBA\u4E3A\u505C\u6B62 rabbitmq \u670D\u52A1</span></span>
<span class="line"><span style="color:#DBD7CA;">systemctl stop rabbitmq-server</span></span>
<span class="line"></span></code></pre><pre class="vp-code-light"><code><span class="line"><span style="color:#A0ADA0;"># \u5728 master \u8282\u70B9\u4E0A\u4EBA\u4E3A\u505C\u6B62 rabbitmq \u670D\u52A1</span></span>
<span class="line"><span style="color:#393A34;">systemctl stop rabbitmq-server</span></span>
<span class="line"></span></code></pre></div><p>\u6B64\u65F6\uFF0C\u6253\u5F00 web \u7BA1\u7406\u63A5\u53E3\uFF0C\u53EF\u4EE5\u770B\u5230 <code>node02</code>\u5DF2\u7ECF\u6210\u4E3A\u4E3B\u8282\u70B9\uFF0C\u5E76\u5B8C\u6210\u540C\u6B65\uFF1A</p><p><img src="https://agou-images.oss-cn-qingdao.aliyuncs.com/blog-images/rabbitmq/rabbitmq_mirror_queue-2.png" alt=""></p><h2 id="\u5176\u4ED6" tabindex="-1">\u5176\u4ED6 <a class="header-anchor" href="#\u5176\u4ED6" aria-hidden="true">#</a></h2><p>\u5F53 slave \u6302\u6389\u4E4B\u540E\uFF0C\u9664\u4E86\u4E0E slave \u76F8\u8FDE\u7684\u5BA2\u6237\u7AEF\u8FDE\u63A5\u5168\u90E8\u65AD\u5F00\uFF0C\u6CA1\u6709\u5176\u4ED6\u5F71\u54CD\u3002\u5F53 master \u6302\u6389\u4E4B\u540E\uFF0C\u4F1A\u6709\u4EE5\u4E0B\u8FDE\u9501\u53CD\u5E94\uFF1A</p><p>\uFF081\uFF09\u4E0E master \u8FDE\u63A5\u7684\u5BA2\u6237\u7AEF\u8FDE\u63A5\u5168\u90E8\u65AD\u5F00\u3002</p><p>\uFF082\uFF09\u9009\u4E3E\u6700\u8001\u7684 slave \u4F5C\u4E3A\u65B0\u7684 master\uFF0C\u56E0\u4E3A\u6700\u8001\u7684 slave \u4E0E\u65E7\u7684 master \u4E4B\u95F4\u7684\u540C\u6B65\u72B6\u6001\u5E94\u8BE5\u662F\u6700\u597D\u7684\u3002<strong>\u5982\u679C\u6B64\u65F6\u6240\u6709 slave \u5904\u4E8E\u672A\u540C\u6B65\u72B6\u6001\uFF0C\u5219\u672A\u540C\u6B65\u7684\u6D88\u606F\u4F1A\u4E22\u5931\u3002</strong></p><p>\uFF083\uFF09\u65B0\u7684 master \u91CD\u65B0\u5165\u961F\u6240\u6709 unack \u7684\u6D88\u606F\uFF0C\u56E0\u4E3A\u65B0\u7684 slave \u65E0\u6CD5\u533A\u5206\u8FD9\u4E9B unack \u7684\u6D88\u606F\u662F\u5426\u5DF1\u7ECF\u5230\u8FBE\u5BA2\u6237\u7AEF\uFF0C\u6216\u8005\u662F ack \u4FE1\u606F\u4E22\u5931\u5728\u8001\u7684 master \u94FE\u8DEF\u4E0A\uFF0C\u518D\u6216\u8005\u662F\u4E22\u5931\u5728\u8001\u7684 master \u7EC4\u64AD ack \u6D88\u606F\u5230\u6240\u6709slave \u7684\u94FE\u8DEF\u4E0A\uFF0C\u6240\u4EE5\u51FA\u4E8E\u6D88\u606F\u53EF\u9760\u6027\u7684\u8003\u8651\uFF0C\u91CD\u65B0\u5165\u961F\u6240\u6709unack \u7684\u6D88\u606F\uFF0C<strong>\u4E0D\u8FC7\u6B64\u65F6\u5BA2\u6237\u7AEF\u53EF\u80FD\u4F1A\u6709\u91CD\u590D\u6D88\u606F</strong>\u3002</p><p>\uFF084\uFF09\u5982\u679C\u5BA2\u6237\u7AEF\u8FDE\u63A5\u7740 slave\uFF0C\u5E76\u4E14 Basic.Consume \u6D88\u8D39\u65F6\u6307\u5B9A\u4E86 <code>x-cancel-on-ha-failover</code> \u53C2\u6570\uFF0C\u90A3\u4E48\u65AD\u5F00\u4E4B\u65F6\u5BA2\u6237\u7AEF\u4F1A\u6536\u5230\u4E00\u4E2A Consumer Cancellation Notification \u7684\u901A\u77E5\uFF0C\u6D88\u8D39\u8005\u5BA2\u6237\u7AEF\u4E2D\u4F1A\u56DE\u8C03 Consumer \u63A5\u53E3\u7684 handleCancel \u65B9\u6CD5\u3002\u5982\u679C\u672A\u6307\u5B9A <code>x-cancel-on-ha-failover</code> \u53C2\u6570\uFF0C\u90A3\u4E48\u6D88\u8D39\u8005\u5C06\u65E0\u6CD5\u611F\u77E5 master \u5B95\u673A\u3002</p><h2 id="\u53C2\u8003\u8D44\u6599" tabindex="-1">\u53C2\u8003\u8D44\u6599 <a class="header-anchor" href="#\u53C2\u8003\u8D44\u6599" aria-hidden="true">#</a></h2><ul><li>\u7B80\u4E66@[Dear_diary] \uFF1A<a href="https://www.jianshu.com/p/fcc35573567c" target="_blank" rel="noopener noreferrer">https://www.jianshu.com/p/fcc35573567c</a></li></ul>`,32),l=[n];function p(r,c,i,u,d,q){return a(),o("div",null,l)}var h=s(t,[["render",p]]);export{m as __pageData,h as default};
