<span class="line"><span style="color:#393A34;">MariaDB </span><span style="color:#8E8F8B;">[(</span><span style="color:#393A34;">none</span><span style="color:#8E8F8B;">)]</span><span style="color:#AB5959;">&gt;</span><span style="color:#393A34;"> use zabbix</span><span style="color:#AB5959;">;</span></span>
